[8227] = {title = "A fita métrica do Nat", objective = "Leve a Fita Métrica do Nat de volta para Nat Pagle no Pântano Vadeoso.", description = "Em meio às iscas despedaçadas e ao material de pesca, há um objeto enrolado e a textura lembra seda: é uma fita métrica. Na fita há uma etiqueta na qual se lê:\n\n\"Nat Pagle, Pântano Vadeoso\".Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
