[8272] = {title = "Herói dos Lobo do Gelo", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nSanguinárioLança Farpada de GeloVarinha do Frio PenetranteMartelo Forjado a Frio\n\nVocê também receberá:\n\n"},
[8273] = {title = "A gratidão de Urraca", objective = "Olha se isso não é interessante! Você diz que achou essa tabuleta em uma ilhota deserta nos Penhascos Panorâmicos das Terras Agrestes, não é, <class>? Muito, muito interessante. \n\nAgradeço pelo seu esforço. Nossa Dama Sombria ficará muito satisfeita com essa descoberta. Aqui. Permita-me recompensá-lo em justa medida.", description = nil, progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCasquete do Reservista da Cidade BaixaTúnica do Fidalgo Antiquado\n\nVocê também receberá:\n\n"},
[8274] = {title = "Test Kill Quest", objective = "Kill 5 Murlocs and come back to the test character.", description = "Got some time to spare?  Kill me some Murlocs and I'll give you something shiny!", progress = nil, completion = nil, rewards = nil},
[8275] = {title = "A retomada de Silithus", objective = "Apresente-se à Arauto do Vento Chifre Altivo no Forte Cenariano, em Silithus.", description = "Atenção, heróis da Aliança! A ameaça dos silitídeos já foi ignorada por tempo demais. Sem nenhuma oposição de verdade, eles vêm se preparando durante todo esse tempo para uma grande expansão.\n\nForças do Círculo Cenariano estão se concentrando em Silithus para avançar contra aquelas criaturas desgraçadas, e todos os aventureiros capazes estão doravante convocados.\n\nNão espere até que seja tarde demais! Ajude na defesa de Azeroth! Apresente-se à Arauto do Vento Chifre Altivo no Forte Cenariano para receber instruções!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8276] = {title = "A retomada de Silithus", objective = "Apresente-se à Arauto do Vento Chifre Altivo no Forte Cenariano, em Silithus.", description = "Atenção, campeões da Horda! Há uma ameaça que já ignoramos por tempo demais. Tremores de terra perto de colmeias dos silitídeos por toda a Azeroth nos alertaram a respeito de grandes movimentações em preparação para uma expansão.\n\nForças do Círculo Cenariano estão se concentrando em Silithus para lançar uma ofensiva contra os silitídeos antes que seja tarde demais! Eles não profanarão nossas terras novamente!\n\nTodos os combatentes capazes devem se apresentar à Arauto do Vento Chifre Altivo no Forte Cenariano! Os silitídeos serão varridos do mapa!", progress = nil, completion = "Excelente! Um nova recruta atendendo ao chamado às armas contra os silitídeos. Bem-vindo ao Forte Cenariano, &lt;class&gt;. Fico muito satisfeita de saber que as palavras de nossos emissários estão chegando aos ouvidos de heróis do seu calibre. Oportunidade de ajudar não faltará, e creio que você logo se provará um aliado valioso.", rewards = "Você receberá:\n\n"},
[8277] = {title = "Peçonha mortal do deserto", objective = "Belhatriz Corredente, no Forte Cenariano em Silithus, quer que você traga 8 Aguilhões de Escorpídeo Petraçoite e 8 Presas de Rastejante da Areia. Fale com ela quando tiver completado a tarefa.", description = "Belhatriz Corredente ao seu dispor! Meu primo Noggel ganha a vida colhendo plantas raras para os alquimistas do Forte Cenariano. Quer dizer, ganhava a vida, sei lá. Ele voltou da última viagem com um veneno muito forte nas veias.\n\nEu consegui retardar a peçonha, mas para curá-la de vez preciso de amostras do veneno do bicho que o picou.\n\nNoggel chegou do norte, então aposto minhas roupas que foram os escorpídeos petraçoites ou os rastejantes da areia. Me traz alguns ferrões e presas que eu vou tentar criar um antídoto para ele.", progress = "Já conseguiu todas as amostras? Podemos perder o Noggel a qualquer minuto!", completion = "Cruza os dedos, amiga! Vamos torcer pro Noggel se salvar!", rewards = "Você receberá:\n\n"},
[8278] = {title = "A última esperança de Noggel", objective = "Belhatriz Corredente, no Forte Cenariano em Silithus, quer que você consiga 3 Aguilhões de Tenaz Petraçoite, 3 Aguilhões de Esfolador Petraçoite e 3 Presas de Espreitadora de Rocha.", description = "Maldição! O antídoto não funcionou. Noggel deve ter sido picado por uma uma das criaturas mais fortes ao sul.\n\nPreciso que você vá lá e me traga mais amostras, <name>. A boa notícia é que o veneno delas é mais poderoso, então não vou precisar de tantas amostras. A má é que, como tem mais criaturas venenosas se arrastando ao sul, precisarei que você colete de mais fontes.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPoção Elevada de CuraPoção Elevada de Mana\n\nVocê também receberá:\n\n"},
[8279] = {title = "O dicionário do crepúsculo", objective = "Traga os três capítulos do Dicionário do Crepúsculo para o Eremita Ortell em Silithus.", description = "Os membros do Martelo do Crepúsculo não são gentis com aqueles que abandonam seus postos. Eles me caçam até hoje como se eu fosse um animal arredio. Não preciso dizer que um inimigo deles é um amigo meu!\n\nPara decifrar essa Tabuleta, vou precisar do livro que os escribas deles usam para codificar as mensagens: O Dicionário do Crepúsculo. É uma ferramenta tão importante que eles guardam cada um dos três capítulos em locais diferentes! Os oficiais encarregados de guardar o Dicionário são conhecidos como os Tutores do Crepúsculo. Procure por eles nos acampamentos do Martelo do Crepúsculo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8280] = {title = "Defendendo as linhas de suprimento", objective = "O Arauto do Vento Chifre Altivo, no Forte Cenariano, quer que você mate 15 Dragatacas.", description = "O Forte Cenariano é um local de extrema importância estratégica. Estou convencida de que, enquanto ele estiver de pé, nossa vitória contra os silitídeos estará garantida. Claro, se conseguirmos manter nossa importação de suprimentos.\n\nAs minhocas do deserto se mostraram uma ameaça maior às nossas caravanas do que os próprios silitídeos. Essa manhã mesmo, um grande carregamento de grão-matinal foi devastado por um grupo de dragatacas. Preciso que você reduza o número delas para garantirmos a segurança dos nossos mantimentos.", progress = nil, completion = "Você tem minha sincera gratidão. Seus esforços em ajudar o Forte Cenariano demonstram coragem.", rewards = "Você receberá:\n\n"},
[8281] = {title = "Aumentando a segurança", objective = "O Arauto do Vento Chifre Altivo, no Forte Cenariano, quer que você mate 20 Dragasmagas.", description = "Sua performance tem sido exemplar até então, <name>. Entretanto, nossas linhas de abastecimento ainda não estão totalmente seguras. Devido à ausência das dragatacas, seus primos grandes que habitam o sul vieram para o norte ocupar o lugar delas.\n\nVocê terá que matar algumas dragasmagas para conseguir impedir o avanço. Não podemos arriscar a segurança das nossas linhas de abastecimento.", progress = nil, completion = "Excelente! Você tem provado ser uma valiosa aliada no preparo para a guerra contra os silitídeos.", rewards = "Você receberá:\n\n"},
[8282] = {title = "Algibeira perdida do Noggel", objective = "Vá para as montanhas na parte sul de Silithus e recupere a Algibeira do Noggel. Devolva-a no Forte Cenariano.", description = "Onde estou? Cadê minha sacola? *arff* Acho que perdi ela enquanto fugia do Agarramata, aquele escorpídeo gigante. Sem ver pra onde eu tava correndo, fui parar num ninho de esfoladores petraçoites.\n\nA minha algibeira estava cheia de um monte de reagente raro. Sem eles, eu e a Belhatriz não vamos conseguir produzir todas as poções que o Forte Cenariano precisa. Ainda estou muito fraco para levantar, mas posso recompensá-<lo/la>, se trouxer de volta meus reagentes.\n\nEu topei com o Agarramata pela montanha ao sul, atrás do acampamento do Barbabronze.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nElixir do MangustoElixir dos Sábios\n\nVocê também receberá:\n\n"},
[8283] = {title = "Procura-se: Agarramata, o Terror das Areias", objective = "Leve a Tenaz da Agarramata para Vish Kozus, Capitão da Guarda.", description = "Acredita-se que a terrível escorpídea Agarramata se abrigou na extensão da montanha ao sul devido ao aumento de atividade silitídea. Ela apresenta uma grande ameaça às patrulhas e expedições na região sul. Indivíduos que participarem da sua eliminação serão decentemente recompensados. Pergunte ao Capitão Vish Kozus sobre a recompensa. Ele se encontra no topo do Mirante do Forte Cenariano.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nMarca do ArenomoaPunhal de Cristal Preto\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[8284] = {title = "O mistério do crepúsculo", objective = "A Geóloga Matarriso, no Forte Cenariano, quer que você recolha 8 Fragmentos de Tabuleta do Crepúsculo.", description = "Até pouco tempo atrás, havia um acampamento de sectários do Martelo do Crepúsculo a noroeste daqui. Hoje, se alguém passar lá, só vai encontrar uma área infestada de elementais só com pedaços de madeira e barracas marcando onde era o acampamento.\n\nRecuperei alguns fragmentos de uma tabuleta feita de barro, mas tenho poucas peças para juntar e conseguir algo útil. Veja se você consegue achar mais fragmentos restantes da tabuleta para tentarmos entender a presença do Martelo do Crepúsculo aqui em Silithus.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8285] = {title = "O desertor", objective = "Leve a tabuleta restaurada para o Eremita Ortell em Silithus.", description = "Existem rumores de que um membro do Martelo do Crepúsculo chamado Ortell seja um desertor da seita. Dizem que ele se esconde nas montanhas ao sul da Vila do Vento Sul, onde vive como eremita. Talvez você possa pedir-lhe que ajude a decifrar a tabuleta.\n\nLeve a tabuleta restaurada e procure o Ortell.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8286] = {title = "O que nos traz o dia de amanhã", objective = "Adentre as Cavernas do Tempo, em Tanaris, e encontre Anacronos, Prole de Nozdormu.", description = "O abraço da morte já envolvia meu ser quando ele interveio. Ele soprou meu corpo destroçado uma única vez e esperou pacientemente enquanto as areias do tempo curavam meus ferimentos.\n\n\"Eternamente você permanecerá, se necessário\", ele disse. E com essas palavras fui feito Vigia.\n\nOlhe para o deserto. A segunda guerra está chegando. Você precisa ir em busca do local do descanso dos brônzeos. Adentre as Cavernas do Tempo e veja se o mestre voltou. Preciso ter certeza antes de agir.", progress = nil, completion = "É como deve ser. O grande se levantou. Minha vigília chegou ao fim.", rewards = "Você receberá:\n\n"},
[8287] = {title = "Intenção terrível", objective = "Leve a Transcrição Decodificada da Tabuleta para o Comandante Mar'alith.", description = "Pronto, aqui está... informações bastante valiosas, sem dúvida! Eu percebi que o Círculo Cenariano chegou a Silithus. Eles planejam expulsar o Martelo do Crepúsculo, não é? Isto vai ajudar. \n\nLeve esta folha de pergaminho para o líder deles, o Comandante Mar'alith. Em breve o Martelo do Crepúsculo será derrotado e estarei fora das garras deles de vez!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8288] = {title = "Só pode ascender um", objective = "Leve a Cabeça do Prolemestre Flagelador para Baristolth das Areias Cambiantes no Forte Cenariano, em Silithus.", description = "Não há tempo a perder! É preciso achar um herói! A tarefa que lhe confio exigirá um exército, mas somente uma pessoa poderá ascender dentre os potenciais campeões para assumir o fardo e a responsabilidade de quem se alia à Prole.\n\nNo covil do Senhor dos Rocha Negra há uma fera das mais cruéis. O Prolemestre Flagelador protege os Salões da Contenda, barrando o caminho para o sacrário interno de Nefarian. Mate aquela aberração torpe e traga-me a cabeça dele. Depois disso poderemos começar o próximo estágio de sua jornada.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8289] = {title = "Talismãs de Mérito", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[8290] = {title = "Aniquile os invasores", objective = "Vá à Ravina Brado Guerreiro, derrote a Horda, obtenha uma Marca de Honra da Ravina Brado Guerreiro e a entregue à Sentinela Cantálgures no Bosque Asa de Prata.", description = "Há um campo de batalhas e destruição do outro lado deste túnel, jovem <class>. A Horda continua cortando as nossas sempre-vivas para abastecer as máquinas de guerra e isso tem contribuído para o extermínio da floresta sagrada.\n\nVá à Ravina Brado Guerreiro e defenda o Castelo Asa de Prata. Contenha as forças invasoras da Horda!\n\nVocê receberá um talismã de mérito por isso. Traga-o a mim, <name>, e você será <recompensado/recompensada>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8291] = {title = "Aniquile os invasores!", objective = "Vá à Ravina Brado Guerreiro, derrote a Horda, obtenha uma Marca de Honra da Ravina Brado Guerreiro e a entregue à Sentinela Cantálgures no Bosque Asa de Prata.", description = "Há um campo de batalhas e destruição do outro lado deste túnel, jovem <class>. A Horda continua cortando as nossas sempre-vivas para abastecer as máquinas de guerra e isso tem contribuído para o extermínio da floresta sagrada.\n\nVá à Ravina Brado Guerreiro e defenda o Castelo Asa de Prata. Contenha as forças invasoras da Horda!\n\nVocê receberá um talismã de mérito por isso. Traga-o a mim, <name>, e você será <recompensado/recompensada>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8292] = {title = "Talismãs de Mérito", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[8293] = {title = "Marca de Honra", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[8294] = {title = "Domine os usurpadores da Asa de Prata", objective = "Vá à Ravina Brado Guerreiro, derrote a Aliança, obtenha uma Marca de Honra da Ravina Brado Guerreiro e a entregue ao Capitão Estilhaça-crânio no Campo-base Mor'shan.", description = "Os selvagens da floresta do Vale Gris não resistirão ao poder da Horda, <race>. Nada que os Asa de Prata digam ou façam poderá abalar a nossa soberania. Kalimdor pertence à Horda. É muita ousadia deles tentar impedir que tomemos o que é nosso de direito!\n\nAcalente o orgulho de impedir as pífias tentativas deles de retardar nosso avanço. Aniquile as Sentinelas da Asa de Prata e obtenha uma marca de honra do Brado Guerreiro. Traga a marca para mim e eu <o/a> recompensarei, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8295] = {title = "Domine os usurpadores da Asa de Prata", objective = "Vá à Ravina Brado Guerreiro, derrote a Aliança, obtenha uma Marca de Honra da Ravina Brado Guerreiro e a entregue ao Capitão Estilhaça-crânio no Campo-base Mor'shan.", description = "Os selvagens da floresta do Vale Gris não resistirão ao poder da Horda, <race>. Nada que os Asa de Prata digam ou façam poderá abalar a nossa soberania. Kalimdor pertence à Horda. É muita ousadia deles tentar impedir que tomemos o que é nosso de direito!\n\nAcalente o orgulho de impedir as pífias tentativas deles de retardar nosso avanço. Aniquile as Sentinelas da Asa de Prata e obtenha uma marca de honra do Brado Guerreiro. Traga a marca para mim e eu <o/a> recompensarei, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8296] = {title = "Marca de Honra", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[8297] = {title = "Recursos da Bacia Arathi!", objective = "Vença a batalha pela Bacia Arathi, consiga uma Caixa de Recursos Arathi e fale novamente com a Sargento Martina na Ponta do Refúgio.", description = "Todos sabem que uma guerra se vence com soldados. Também é sabido que uma batalha que se luta com equipamentos medíocres ou de barriga vazia já está perdida antes mesmo de começar!\n\nEsta é a importância da Bacia Arathi. Existem locais estratégicos com importantes recursos como metais, armas, comida e lenha.\n\nPois bem, <name>. Preciso que você vá até a Bacia Arathi e controle mais bases do que o inimigo para, assim, vencer a batalha. E traga-me uma caixa de recursos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8298] = {title = "Mais caixas de recursos", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[8299] = {title = "Quebra da rota de suprimentos de Arathor", objective = "Vença a batalha pela Bacia Arathi, consiga uma Caixa de Recursos Arathi e fale novamente com o Sicário Mortis na Ruína do Martelo.", description = "A Liga de Arathor não está poupando recursos na retomada de Arathi. Eles, junto com todas as raças da Aliança, estão se movendo para esta distante região na tentativa de expandir seus territórios a partir da Ponta do Refúgio. A melhor maneira de impedi-los é roubando os suprimentos deles.\n\nVocê pode nos ajudar, <name>. Entre na Bacia Arathi, conhecida por suas ricas minas, solos férteis e hábeis artesãos. Capture e controle o maior número de bases, vença a batalha e traga-me os recursos que conseguir.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8300] = {title = "Mais caixas de recursos", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[8301] = {title = "O caminho do íntegros", objective = "Colete 200 Fragmentos de Carapaça Silitídea e fale novamente com Baristolth.", description = "A Prole de Nozdormu é naturalmente desconfiada das raças inferiores. Conquanto você tenha me provado seu potencial para ser u<m/ma> <herói/heroína>, chegou a hora de prová-lo a meu mestre.\n\nA Colme'Ashi, a Colme'Zora e a Colme'Régia. Todas em plena atividade, cada uma ameaça à nossa existência. Mate silitídeos dessas colmeias e traga fragmentos das carapaças. Quando tiver o quanto conseguir carregar, fale novamente comigo para que eu reforce sua determinação.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nGraça de Nozdormu\n\nVocê receberá:\n\nProcurador de Nozdormu"},
[8302] = {title = "A mão do íntegro", objective = "Colete 200 Fragmentos de Carapaça Silitídea e fale novamente com Baristolth.", description = "Escolha seus assistentes sabiamente, mortal. Cada vez que você tiver cumprido uma tarefa e voltar, poderá escolher um novo assistente.\n\nDito isto, preciso de mais fragmentos de carapaça. Não perca tempo!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nGraça de Nozdormu\n\nVocê receberá:\n\nProcurador de Nozdormu"},
[8303] = {title = "Anacronos", objective = "Encontre Anacronos nas Cavernas do Tempo, em Tanaris.\n\nItem fornecido:\n", description = "Está na hora, <name>.\n\nPreciso me desculpar por guardar tantos segredos. Abundam os inimigos, mesmo embaixo do nosso nariz. Por favor, leia este tomo. Aprenda. Leve este conhecimento que lhe dou e vá em busca de meu mestre.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[8304] = {title = "Querida Natália", objective = "O Comandante Mar'alith, no Forte Cenariano, em Silithus, quer que você interrogue os ocupantes do Acampamento Barbabronze. Ele fica diretamente ao sul do Forte Cenariano.\n\nUma vez obtidas as informações solicitadas, fale novamente com o Comandante Mar'alith.", description = "A minha querida Natália desapareceu.\n\nPara dizer a verdade, ela andava estranha nas semanas anteriores ao seu desaparecimento. Eu a peguei falando sozinha quando ninguém estava por perto em mais de uma ocasião. Ela jurava que tudo estava bem e que devíamos permitir que continuasse a pesquisa.\n\nDa última vez que a vimos, ela estava indo para o acampamento do Barbabronze ao sul.\n\nMeus soldados já estão loucos com tantas obrigações, <name>. Preciso que você vá até o acampamento do Barbabronze e interpele os anões.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8305] = {title = "Relembranças longínquas", objective = "Localize a Lágrima Cristalina em Silithus, e contemple suas profundezas.", description = "Ao sul do deserto de Silithus, nas cercanias da barreira sagrada, há uma única lágrima cristalina — um símbolo das grandes perdas que se abateram sobre nós durante a Guerra das Areias Cambiantes. Encontre a lágrima e perscrute seu âmago para obter iluminação.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8306] = {title = "No ventre da loucura", objective = "O Comandante Mar'alith do Forte Cenariano em Silithus quer que você encontre Natália. A informação que conseguiu aponta para Colme'Régia, ao sul, como sendo a área em que você encontrará a Senhora Natália Mar'alith.\n\nNão se esqueça de falar com os anões no acampamento do Barbabronze antes de se aventurar na colmeia. Eles podem ter algum trabalho e conselhos para você.\n\nE, <name>, lembre-se das palavras do Comandante: \"Faça o que for necessário...\"", description = "Preciso que vá até Colme'Régia e descubra que fim levou a minha querida Natália, <name>. Essa não vai ser uma tarefa fácil para você sozinh<o/a>. Sugiro que você vá com seus aliados. Antes que entre de cabeça no ventre da loucura, não esqueça de falar novamente com aqueles anões. Eles devem ter alguma dica de como lidar com os silitídeos que habitam a colmeia.\n\nE, <name>, se ela realmente estiver louca como dizem... faça o que for necessário.\n\n<O Comandante Mar'alith lhe dá as costas.>", progress = nil, completion = "&lt;O Comandante Mar'alith agarra o próprio peito.&gt;\n\nC'Thun? O QUE significa tudo isso? O que é C'Thun? Como pode este demônio ter transformado minha doce Natália em um monstro assim?", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nCajado Bosquenero Corrompido"},
[8307] = {title = "Receita do deserto", objective = "Procure informações sobre como cozinhar Carne de Verme da Areia no acampamento do Martelo do Crepúsculo, a oeste do Forte Cenariano.", description = "Adaptar-se à vida no deserto tem sido bem difícil. Além de sentir falta dos pássaros e árvores de Auberdine, nossas opções de comida aqui são muito limitadas por causa do clima árido. Estou tentado encontrar algumas plantas pelo deserto, mas não existem muitas.\n\nVi grupos do Martelo do Crepúsculo, do acampamento a oeste daqui, caçarem vermes da areia para comerem. Mas... a carne deles é amarga demais. Não sei como fazem aquilo ficar tragável. Fique <atento/atenta> se passar pelo acampamento deles e me avise se descobrir algo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8308] = {title = "A carta perdida de Brann Barbabronze", objective = "Leve a Carta de Brann Barbabronze para o Acampamento do Barbabronze na região sul de Silithus.", description = "A quem interessar possa:\n\nSe estiver lendo isto, já estou morto. Ou estarei em breve. É difícil saber. Pouco importa, pois a maior parte da tripulação está morta.\n\nDescobri informações sobre um titã. Uma grande batalha entre ele e um deus antigo. Sim, um legítimo deus antigo. Encontrei um antigo túnel que leva a Ahn'Qiraj. Estou seguindo por ele.\n\nLeve esta carta para meu antigo acampamento. Assegure-se de que meu macaco está recebendo uma quantidade decente de comida, água e abraços.\n\n-Brann Barbabronze, Extremo Explorador\n\nP.S. Cuidado com o elfo noturno louco que acredita no antigo deus.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nPicareta Fiel do Brann"},
[8309] = {title = "À procura de glifos", objective = "Rutgar Formaglifo, no Acampamento Barbabronze, em Silithus, quer que você vá a Colme'Ashi, Colme'Zora e Colme'Régia e decalque os Cristais Entalhados de Glifos em cada colmeia.\n\nUse o Kit de Transcrição do Geólogo para tirar um decalque razoável dos glifos antigos. Fale novamente com Rutgar Formaglifo quando terminar a tarefa.", description = "Depois das nossas primeiras incursões – e da loucura subsequente da Senhora Mar'alith – as três colmeias não param de vomitar insetos! Essa intensa atividade deve estar ligada aos glifos que cobrem aqueles estranhos cristais.\n\nPrecisamos de amostras dos cristais de todas as três colmeias. Se eu tiver decalques dos glifos naqueles cristais, talvez algo nessa loucura faça sentido.\n\nPegue este kit de transcrição e vá às colmeias, <name>. Não volte até fazer as cópias de cada colmeia.", progress = "Se tu vispar uma elfa noturna caduca numa das colmeias, não deixa de dar-lhe um belo dum coice nas ancas, por nossa conta.", completion = "Não faço ideia do que se trata esses glifos. Nunca vi nada igual! Espero que a pesquisa do Frankal nos dê alguma luz.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8310] = {title = "Quebrando o código", objective = "Frankal Pontepedra, no Acampamento Barbabronze, em Silithus, quer que você obtenha um Cérebro de Silitídeo de Colme'Zora, um Cérebro de Silitídeo de Colme'Ashi e um Cérebro de Silitídeo de Colme'Régia. Fale novamente com Frankal quando tiver concluído a tarefa.", description = "Acho que consigo criar um elixir que vai nos fazer entender melhor os glifos e a linguagem dos silitídeos e dos Qirajis! Do que necessito? Que bom que perguntou! Preciso de cérebros! Mais especificamente, um cérebro de cada tipo de silitídeo que habita as colmeias. Traga os cérebros e talvez o trabalho do Rutgar não tenha sido em vão.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8311] = {title = "Gostosuras da Noturnália para Jacinto!", objective = "Fale com os estalajadeiros de Ventobravo, Altaforja e Darnassus, e também com Didiê Du Mocó, em Altaforja. Pague as prendas que eles pedirem em troca de gostosuras.\n\nLeve um Marzipã de Darnassus, uma Jujuba de Gnomeregan, um Nougat de Ventobravo e uma Balinha de Altaforja para Jacinto, que está no Orfanato de Ventobravo.", description = "Eu queria brincar de gostosuras ou travessuras, mas não posso sair de casa porque estou doente... Você não quer ir no meu lugar? Eu troco os doces que você me trouxer por alguns que eu tenho aqui. E eu acho que você vai gostar dos meus doces... Eles transformam as pessoas em coisas estranhas e engraçadas!\n\nTodos os estalajadeiros das cidades grandes dão balas durante a Noturnália. É só falar com eles e pagar as prendas que eles pedirem! E também tem um gnomo dando bala em Altaforja. O nome dele é Didiê. Quando você pegar todos os doces, traz tudo aqui pra mim!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGostosura de Abóbora da Noturnália"},
[8312] = {title = "Gostosuras de Noturnália para Assustosa!", objective = "Fale com os estalajadeiros de Orgrimmar, da Cidade Baixa e do Penhasco do Trovão, e também com Kali Remik, na Aldeia Sen'jin. Pague as prendas que eles pedirem e receba doces como recompensa.\n\nLeve um Marzipã do Penhasco do Trovão, uma Jujuba Lançanegra, um Nougat de Orgrimmar e uma Balinha da Cidade Baixa para Assustosa, no Orfanato de Orgrimmar.", description = "Eu queria brincar de gostosuras ou travessuras, mas não posso sair de casa porque estou doente... Você não quer ir no meu lugar? Eu troco os doces que você me trouxer por alguns que eu tenho aqui. E eu acho que você vai gostar dos meus doces... Eles transformam as pessoas em coisas estranhas e engraçadas!\n\nTodos os estalajadeiros das cidades grandes dão balas durante a Noturnália. É só falar com eles e pagar as prendas que eles pedirem! E também tem uma trolesa dando bala na Aldeia Sen'jin. O nome dela é Kali Remik. Quando você pegar todos os doces, traz tudo aqui pra mim!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGostosura de Abóbora da Noturnália"},
[8313] = {title = "Compartilhando o conhecimento", objective = "Leve a Página Rasgada de Receita para Calandrath na estalagem do Forte Cenariano.", description = "Embora seja fácil entender a receita de como cozinhar carne de verme da areia, ela precisa de plantas e temperos de que você nunca ouviu falar. Calandrath deve saber do que se trata por ser uma profunda conhecedora de plantas do deserto.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nBolinhos Defumados do Deserto\n\nVocê receberá:\n\n"},
[8314] = {title = "Desvendando o mistério", objective = "Rutgar Formaglifo, no Acampamento Barbabronze, quer que você entregue o Prisma de Cristal com Glifos para a Geóloga Matarriso no Forte Cenariano, que fica em Silithus.", description = "Estamos perto de uma grande descoberta, <name>! Os decalques que você trouxe nos permitiram recriar os glifos neste prisma cristalino. Eles parecem ser um meio dos qirajis e de outras entidades desconhecidas se comunicarem com os silitídeos.\n\nInfelizmente, isso foi tudo o que conseguimos até agora. Precisamos descobrir uma forma de decifrar as mensagens ocultas nos cristais.\n\nLeve o prisma de cristal com glifos para a Geóloga Matarriso no Forte Cenariano. Talvez ela consiga ajudar.", progress = "Ó céus! O que você tem aí, <name>?", completion = "A razão pela qual os anões não conseguiram decifrar os glifos é porque não há nada para ser decifrado. A camada móvel exterior do prisma tem que se alinhar perfeitamente com as inscrições do cristal interno para que a mensagem seja transmitida. Inteligente...\n\nMesmo que sejamos capazes de traduzir essas informações, primeiro precisamos descobrir o código. Só há uma pista na superfície do prisma – assumindo que os decalques foram transferidos corretamente – e não parece bom.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8315] = {title = "O chamado", objective = "Geóloga Larksbane, no Forte Cenariano em Silithus, quer que você pegue o Mecanismo de Destrancamento de Cristal do Emissário Qiraji.\n\nVocê foi instruído a levar os Glifos de Evocação até os Ossos de Grakkarond ao sul do Forte Cenariano e desenhá-los na areia. Caso o Emissário Qiraji apareça, mate-o e recupere o Mecanismo de Destrancamento de Cristal. Fale novamente com a Geóloga Matarriso caso tenha sucesso.\n\nReúna uma tropa para esta missão, <name>!", description = "Os glifos ilustram um emissário dos Qiraji se encontrando com três silitídeos na ossada de um grande dragão. Acima dos glifos tem algo que acredito ser um tipo de código. Provavelmente é a chave para a evocação do emissário.\n\nCriarei uma cópia do glifo para você usar na areia da região dos Ossos de Grakkarond. Se eu estiver certa, os lordes supremos atenderão o chamado, acreditando que mais informações estão prontas para serem transmitidas. Acabe com eles e enfrente o emissário. Ele guarda a chave para os cristais...", progress = "Precisamos aproveitar a oportunidade que nos foi dada e atacar!", completion = "Estou em posse de 20 mil anos de história nas minhas mãos, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[8316] = {title = "Armamentos de Guerra", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nCoxotes do Reservista CenarianoGrevas Incrustadas de Cristal"},
[8317] = {title = "Assistência culinária", objective = "Calandrath, no Forte Cenariano, precisa de uma porção de 10 Bolinhos Defumados do Deserto.", description = "Aquela receita vai ser muito útil. É melhor eu voltar logo pro trabalho. Quero fazer alguns bolinhos defumados do deserto ainda hoje. Você podia me ajudar a fazer alguns. Prometo-lhe uma boa recompensa.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8318] = {title = "Comunicação secreta", objective = "Traga 10 Textos Cifrados do Crepúsculo para Bor Juba Agreste no Forte Cenariano.", description = "Alguns sectários do Martelo do Crepúsculo carregam papeizinhos com estranhas anotações. Quero que me traga um monte deles, <class>.\n\nNão consigo entender nada do que está escrito, mas isso não importa! Estes papéis são o meio de comunicação entre os diferentes grupos do culto. Quanto mais textos tirarmos de circulação, menos eles vão se comunicar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8319] = {title = "Textos codificados do Crepúsculo", objective = "Continue trazendo mais textos. Logo expulsaremos o Martelo do Crepúsculo de Silithus.", description = nil, progress = "Continue trazendo mais textos. Logo expulsaremos o Martelo do Crepúsculo de Silithus.", completion = "Excelente trabalho! Falarei bem de você para todos!", rewards = nil},
[8320] = {title = "Geolordes do Crepúsculo", objective = "Mate 10 Geolordes do Crepúsculo, então fale novamente com Huum Juba Agreste no Forte Cenariano.", description = "O Martelo do Crepúsculo, um culto insano com tendências destrutivas, armou acampamentos em Silithus e está realizando rituais dia e noite. Ainda não sabemos quais são os planos deles, mas acreditamos que estejam de alguma forma ligados aos cristais vermelhos pulsantes espalhados pelo deserto.\n\nEmbora o mistério possa ser solucionado algum dia, precisamos lidar com esta ameaça imediatamente. Vá aos acampamentos deles a oeste e encontre os geolordes do Crepúsculo. São esses líderes traiçoeiros quem devemos atacar.", progress = nil, completion = "Foi um trabalho bem feito, &lt;name&gt;. A morte dos geolordes do Crepúsculo deve causar grande impacto ao culto. Vamos torcer para termos mesmo atrasado os planos deles...", rewards = "Você receberá:\n\n"},
[8321] = {title = "Viral, o Vil", objective = "Leve o Anel-sinete do Viral para Huum Juba Agreste no Forte Cenariano.", description = "Descobrimos outra base de operações do Martelo do Crepúsculo. É chamada de Passeio do Crepúsculo, uma caverna escondida na ponta nordeste de Silithus. Viral, o Vil, líder local do culto, guarda a caverna. Ele é o próximo alvo que você deve derrotar. Traga-me o anel-sinete dele como prova.\n\nBoa sorte para você, <name>. Os horrores causados pelo Martelo do Crepúsculo são assustadores.", progress = "<name>, você já derrotou Viral, o Vil? Está com o anel-sinete?", completion = "Você conseguiu! Viral está morto! Isso só pode significar o fim dos planos do Martelo do Crepúsculo em Silithus. Ainda devem existir muitos andando por aí, mas eles não vão se restabelecer tão facilmente depois do golpe que você deu.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManoplas do Vento do DesertoPingente Prismassol\n\nVocê também receberá:\n\n"},
[8322] = {title = "Ovos podres", objective = "Coloque os Ovos Podres no barril de cerveja principal dentro da Estalagem da Costa Sul.", description = "Os humanos de Costa Sul adoram cerveja, e por isso valorizam muito a cerveja da Estalagem da Costa Sul. Com o caos da Noturnália, podemos aproveitar para arruinar o último lote de bebida deles, que devia durar semanas, senão meses!\n\nPegue esses ovos podres e coloque no barrilzão principal da Estalagem da Costa Sul. Só a sua presença já deve causar confusão, então prepare-se para se defender!\n\nQuando você tiver deixado esse gostinho inesquecível na boca deles, fale novamente comigo aqui no festival!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8323] = {title = "Crentes Fiéis", objective = "O Eremita Ortell quer que você traga 10 Textos Codificados do Crepúsculo", description = "Agora que tenho o Dicionário do Crepúsculo em minha posse, tenho mais um pequeno favor a lhe pedir.\n\nMinha antiga organização tem um secreto boletim codificado que é distribuído mensalmente.\n\nSe eles apertarem o cerco contra mim ou desistirem de me perseguir, alguma nota será publicada na Crente Fiel.\n\nAlém dessa informação, que é claramente valiosa para mim, a publicação contém mais informes que podem nos ser úteis. Traga-me qualquer texto codificado que você encontrar dos membros do Martelo do Crepúsculo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8324] = {title = "Crendo fielmente", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8325] = {title = "Retomada da Ilha Andassol", objective = "Mate 6 Moreias de Mana. Elas ficam perto dos Cristais Flamejantes junto à torre e ao mirante.", description = "O quanto antes você der início ao treinamento, <name>, melhor para todos nós. Não há espaço para erros, por isso escute com atenção.\n\nOs Cristais Flamejantes, os objetos verdes flutuantes a oeste da Torre do Sol, são usados há muito tempo para alimentar os experimentos da ilha. As moreias de mana eram suas guardiãs, mas a invasão do Flagelo em Quel'Thalas as dispersou, pois perdemos o controle mágico sobre elas.\n\nNão há escolha a não ser reduzir seu número para a reconquista. Faça isso e volte aqui.", progress = nil, completion = "Você concluiu sua primeira tarefa com êxito; por isso, será parabenizado. Tal êxito faz-me crer que você se sairá melhor que os outros jovens elfos sangrentos que não são capazes de prestar atenção nas lições dos seus mestres. O êxito constante será recompensado, não apenas com conhecimento, mas com recompensas tangíveis.\n\nContudo, seu trabalho aqui não acabou. Há muito mais que aprender, meu jovem amigo...", rewards = "Você receberá:\n\n"},
[8326] = {title = "Medidas drásticas", objective = "Recolha 8 Coleiras de Linces Garrataque e de Filhotes de Garrataque. Fale novamente com a Magistra Erona na Ilha Andassol quando terminar.", description = "Seu esforço deixou claro algo que, honestamente, eu gostaria que não fosse verdade. O poder descontrolado dos Cristais Flamejantes prejudicou uma parte muito maior do equilíbrio natural da ilha do que eu pensava. Agora precisamos tomar medidas muito mais drásticas para retomar o controle.\n\nOs linces da área sucumbiram à influência dos cristais e precisam ser sacrificados. Traga-me suas coleiras, <name>. Talvez eu ainda seja capaz de criar um freio mágico que restabeleça o controle de alguns deles.", progress = "Não me é prazeroso pedir que você aniquile essas feras. Embora tenhamos vivido em relativa harmonia com as criaturas da floresta, os tempos são outros. A palavra de lei de todos os sin'dorei é a sobrevivência; nunca se esqueça disso.", completion = "A torre e adjacências já devem estar relativamente protegidas, embora temporariamente. Você fez bem em nos proporcionar um alento de proteção, mas precisamos reassumir o controle de toda a ilha para podermos sobreviver aqui em longo prazo. Isso envolve lidar com ameaças muito maiores do que moreias de mana e linces errantes.\n\nTome isto, &lt;name&gt;, tenho certeza de que lhe será útil nas tarefas por vir.", rewards = "Você receberá:\n\n"},
[8327] = {title = "Apresente-se a Lanthan Perilon", objective = "Fale com Lanthan Perilon na Ilha Andassol; ele encontra-se perto do caminho para oeste além da Torre do Sol.Apresente-se ao meu assistente, Lanthan Perilon. Ele vai lhe passar as instruções do que precisa ser feito em seguida. Enquanto nós, aqui na Torre do Sol, continuamos a refrear os Cristais Flamejantes, você se concentrará em uma questão mais premente relativa à Academia Falthrien. Lanthan vai lhe passar todas as informações quando você falar com ele.\n\nBasta pegar o caminho para o oeste e você o verá por perto.\n\nBoa sorte, <name>, seus êxitos constantes serão recompensados.", description = "Apresente-se ao meu assistente, Lanthan Perilon. Ele vai lhe passar as instruções do que precisa ser feito em seguida. Enquanto nós, aqui na Torre do Sol, continuamos a refrear os Cristais Flamejantes, você se concentrará em uma questão mais premente relativa à Academia Falthrien. Lanthan vai lhe passar todas as informações quando você falar com ele.\n\nBasta pegar o caminho para o oeste e você o verá por perto.\n\nBoa sorte, <name>, seus êxitos constantes serão recompensados.", progress = nil, completion = "A Magistra Erona me avisou de sua chegada oportuna, &lt;name&gt;. A Academia Falthrien a oeste daqui, o enorme prédio flutuante com torres ornadas, está em péssimas condições. Você vai liderar o esforço para retomá-la de um dos ignóbeis. Um &lt;race&gt; que sucumbiu aos desejos mais básicos para todo o sempre.\n\nEspero que você esteja pronto para o trabalho. Essa não vai ser apenas uma lição sobre perigo, mas também sobre o que acontece quando se renuncia à sua própria realidade.", rewards = nil},
[8328] = {title = "Treinamento de mago", objective = "Fale com a instrutora de magos Julia Golpessol na Torre do Sol, que fica na Ilha Andassol.À medida que você progredir como <class>, convém aproveitar o treinamento que o respectivo instrutor oferece. Para jovens magos, aqui na Ilha Andassol, a instrutora é Julia Golpessol. Fale com ela e descubra que treinamento está disponível para você. Ela cobra pelo serviço, por isso leve algum dinheiro.\n\nJulia, assim como todos os instrutores da Ilha Andassol, encontra-se no nível inferior da Torre do Sol.Completando essa missão você ganhará:\n\n", description = "À medida que você progredir como <class>, convém aproveitar o treinamento que o respectivo instrutor oferece. Para jovens magos, aqui na Ilha Andassol, a instrutora é Julia Golpessol. Fale com ela e descubra que treinamento está disponível para você. Ela cobra pelo serviço, por isso leve algum dinheiro.\n\nJulia, assim como todos os instrutores da Ilha Andassol, encontra-se no nível inferior da Torre do Sol.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[8329] = {title = "Treinamento de guerreiro", objective = "Fale com o instrutor de guerreiros Delios na Torre do Sol, que fica na Ilha Andassol.À medida que você progredir como <class>, convém aproveitar o treinamento que o respectivo instrutor oferece. Para jovens guerreiros, aqui na Ilha Andassol, o instrutor é Delios. Fale com ele e descubra que treinamento está disponível para você. Ele cobra pelo serviço, por isso leve algum dinheiro.\n\nDelios, assim como todos os instrutores da Ilha Andassol, encontra-se no nível inferior da Torre do Sol.Completando essa missão você ganhará:\n\n", description = "À medida que você progredir como <class>, convém aproveitar o treinamento que o respectivo instrutor oferece. Para jovens guerreiros, aqui na Ilha Andassol, o instrutor é Delios. Fale com ele e descubra que treinamento está disponível para você. Ele cobra pelo serviço, por isso leve algum dinheiro.\n\nDelios, assim como todos os instrutores da Ilha Andassol, encontra-se no nível inferior da Torre do Sol.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[8330] = {title = "olanian's Belongings", objective = "Recolha o Orbe de Vidência de Solanian, o Pergaminho de Magia do Flagelo e o Diário. Eles estão na Ilha Andassol ao lado do poço, da fonte e de um dos Cristais Flamejantes. Leve-os ao Vigia da Nascente na Torre do Sol, que fica na Ilha Andassol, quando estiver de posse de todos eles.", description = "Com todo esse caos aqui na Torre do Sol, não tive a oportunidade de recolher os pertences que deixei do lado de fora em muitos lugares na ilha. Preciso permanecer de vigília aqui na Torre do Sol, por isso peço que você os recolha no meu lugar.\n\nPreciso do meu orbe de vidência, meu pergaminho de magia do Flagelo e meu diário. Use esta algibeira para ter mais espaço, pois meus pertences ocupam um certo volume. Traga-os a mim e lhe darei algo em troca pelo esforço; pode ficar com a algibeira também!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8331] = {title = "Aurel Folháurea", objective = "Fale com Aurel Folháurea no Forte Cenariano.", description = "<name>, embora esperássemos que as operações do Martelo do Crepúsculo em Silithus estivessem desacelerando... infelizmente estávamos errados. Por meio das nossas ações contínuas contra a seita, descobrimos que um poder ainda mais sombrio, o Conselho Abissal, controla o Martelo através das pedras eólicas arcanas espalhadas pelos acampamentos deles. Sua próxima ação, <name>, será contra o Conselho Abissal.\n\nFale com Aurel Folháurea, na seção leste do Forte Cenariano. A perícia dela será necessária para esse próximo estratagema.", progress = nil, completion = "&lt;name&gt;, bom dia. Me contaram que você está para atacar o Conselho Abissal. Se for esse o caso, escute atentamente o que eu vou lhe dizer...", rewards = "Você receberá:\n\n"},
[8332] = {title = "Duques do Conselho", objective = "Leve 1 Estilhaço Brilhante Grande e 3 Escudos Abissais para Aurel Folháurea no Forte Cenariano.", description = "Achamos que é possível usar uma pedra eólica para emboscar um duque do Conselho Abissal. Para isso, porém, alguém precisa comungar por meio da pedra e convencer o conselho de que você é <um/uma> líder intermediári<o/a> do Martelo do Crepúsculo.\n\nTal estratagema requer uma réplica do medalhão de posto da seita. Consigo criar o medalhão, mas preciso de um estilhaço grande, brilhante, e de escudos dos que os templários abissais usam.\n\nTraga o material e eu farei o resto.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMedalhão de Posto do Sectário do Crepúsculo"},
[8333] = {title = "O medalhão de posto", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nMedalhão de Posto do Sectário do Crepúsculo"},
[8334] = {title = "Agressão", objective = "Mate 7 Conservadores e 7 Arvorosos Ferozes e fale novamente com Lanthan Perilon na Ilha Andassol.", description = "Antes de enviar você à Academia, quero que você execute outra tarefa urgente. Antes usados para nos ajudar na jardinagem da Ilha Andassol, os conservadores irracionais fugiram ao nosso controle. Extermine-os sem demora, mas esteja ciente de que a falta de controle os deixou agressivos. Tal agressividade é o que nos força a abater esses outrora gentis servos.\n\nNão me agrada fazer tal pedido, mas é uma questão de sobrevivência. Deixemos as lágrimas para depois.", progress = nil, completion = "Embora as mortes dos conservadores não me deem o menor prazer, elas me mostram que você está pronto para sua tarefa mais importante aqui na Ilha Andassol. Tome isto e faça bom uso; você precisará de bons itens e mente aguçada para a tarefa por vir. A retomada do controle da ilha depende disso.", rewards = "Você receberá:\n\n"},
[8335] = {title = "Felendren, o Banido", objective = "Mate 8 Aparições Arcanas e 2 Aparições Arcanas Maculadas, bem como Felendren, o Banido; eles encontram-se na Academia Falthrien. Leve a Cabeça de Felendren para Lanthan Perilon na Ilha Andassol.", description = "Um traidor do nosso povo habita o topo da Academia Falthrien, a sudoeste.\n\nFelendren é seu nome e ele foi banido da terra dos elfos sangrentos por não dar ouvidos às advertências dos nossos mestres e anciãos. Ele é o que nossa sociedade tem de pior, pois vive apenas para alimentar seu vício insaciável de magia. Ele recusou-se a aprender sobre controle e tornou-se uma sombra do seu antigo ser, um dos ignóbeis, e uma ameaça à Torre do Sol.\n\nAcabe com ele e com os espectros que ele usa como lacaios.", progress = "Preste bem atenção no que vou dizer: Felendren, o Banido, é apenas sintoma de um problema muito maior. Sua morte resolve nossos problemas imediatos, mas todos os elfos sangrentos correm o risco de ter o mesmo destino que ele, caso sucumbam ao vício.", completion = "A cabeça de Felendren... você receberá uma menção, &lt;name&gt;. Você obteve êxito onde outros, como Felendren, fracassaram por completo. Talvez você esteja pronto para ser um membro útil da sociedade de elfos sangrentos.\n\nSeu êxito aqui significa que você é capaz de sobreviver às ameaças maiores que espreitam o Canto Eterno... e, pode acreditar, há muitas a enfrentar.", rewards = "Você receberá:\n\n"},
[8336] = {title = "Fistful of Slivers", objective = "Consiga 6 Lascas Arcanas com qualquer criatura da ilha que use mana e fale novamente com o Arcanista Ithanas na Ilha Andassol.", description = "É uma lástima termos perdido o controle de muitas criaturas aqui na ilha. Este já foi um lugar tranquilo de estudo e pesquisa. Agora só o que podemos fazer é nos proteger do ataque das nossas próprias criações!\n\nVocê terá a oportunidade de receber uma dádiva mágica em troca de algum trabalho de coleta de sua parte. Traga-me um punhado de lascas arcanas, que podem ser encontradas nas criaturas da Ilha Andassol que utilizam mana, e lançarei um feitiço que <o/a> ajudará nas suas aventuras na ilha.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nItem nº20991"},
[8338] = {title = "Lasca Arcana Maculada", objective = "Leve a Lasca Arcana Maculada ao Arcanista Helion no mirante da Torre do Sol, que fica na Ilha Andassol.", description = "Você encontrou uma estranha lasca de mana cristalizado no cadáver do espectro arcano corrompido. A lasca é diferente de todas as que você já viu por ser negra e de aparência tão sinistra quanto à do espectro de quem você a tomou. O simples ato de segurá-la lhe causa um estranho pressentimento.\n\nCreio que o Arcanista Helion no mirante da Torre do Sol, que fica na Ilha Andassol, seja a pessoa certa para dar um destino útil à lasca.", progress = "Pelo seu comportamento, você parece ter um assunto um tanto quanto urgente. Posso lhe ser útil de alguma forma?", completion = "Assaz interessante e, quando digo interessante, quero dizer perturbador, acima de tudo.\n\nNossos esforços para reassumir o controle da ilha no período posterior à destruição da Nascente do Sol têm sido um desafio. Minha suspeita é de que, seja qual for a origem do mal que conspurca a ilha, é ela que está por trás de tudo isso; essa lasca pode ser de alguma ajuda para descobrirmos o que realmente está acontecendo.\n\nFoi sábio de sua parte trazê-la a mim, &lt;name&gt;. Aceite isto como recompensa. Obrigado.", rewards = "Você receberá:\n\n"},
[8341] = {title = "Lordes do Conselho", objective = "Leve 5 Estilhaços Brilhantes Grandes e 3 Escudos Abissais para Aurel Folháurea no Forte Cenariano.", description = "Os lordes do Conselho Abissal só podem ser invocados por meio das pedras eólicas maiores, e somente integrantes da cúpula do Crepúsculo podem falar com eles. Porém... Eu descobri uma forma de aprimorar o seu disfarce e enganar os lordes abissais.\n\nO disfarce pede um anel de suserania, usado apenas pela cúpula da seita. Farei um se você me trouxer estilhaços brilhantes grandes e sinetes abissais.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAnel de Suserania do Sectário do Crepúsculo"},
[8342] = {title = "Anel de Suserania do Crepúsculo", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nAnel de Suserania do Sectário do Crepúsculo"},
[8343] = {title = "A descoberta de Folháurea", objective = "Fale com Aurel Folháurea no Forte Cenariano.", description = "<name>, Aurel Folháurea acha que descobriu uma forma de evocar e derrotar um integrante da cúpula do Conselho Abissal! Será uma tarefa perigosa, mas você conquistou grande respeito aqui no Círculo Cenariano. Sabemos que, se alguém for capaz, será você.\n\nQue a sorte <o/a> acompanhe, <name>. Fale com Aurel quando estiver preparad<o/a>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8344] = {title = "Windows to the Source", objective = "Bring 4 Wraith Essences and 1 Tainted Wraith Essence to Summoner Teli'Larien at the Sunspire on Sunstrider Isle.", description = "Heed my words, young one - there is a taint about this island, and it is far more potent than the remnants of the Scourge.\n\nI believe there is a true demonic presence on the isle.\n\nWest of here are the spires of Falthrien Academy. The arcane wraiths that infest it are a window to the source of demonic power. You are to bring me the essences of tainted and untainted wraiths.\n\nIn exchange for your obedience, I'll teach you how to summon an imp companion - your first of many as a warlock.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nEvocar Diabrete"},
[8345] = {title = "he Shrine of Dath'Remar", objective = "Leia a placa no Altar de Dath'Remar e fale novamente com o Vigia da Nascente Solanian na Torre do Sol, que fica na Ilha Andassol.", description = "Dia após dia eu fico aqui, observando, aguardando. Acusaram-me de viver demais no passado, enquanto meus olhos fitam o horizonte. Mas tenho a convicção de que os visitantes desta ilha deveriam honrar aqueles que sacrificaram tudo para que eles pudessem continuar a fazê-lo.\n\nDath'Remar Andassol foi nosso primeiro rei. Ele liderou nossa partida de Kalimdor através do Redemoinho.\n\nProcure o altar dele a oeste e não volte aqui até ter lido a placa em sua homenagem.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8346] = {title = "hirst Unending", objective = "Use a habilidade Torrente Arcana, inerente à sua raça, em uma Moreia de Mana e fale novamente com o Arcanista Helion.", description = "Se você tiver que escolher apenas uma lição para se lembrar do tempo que passou na Ilha Andassol, que seja esta: controle a sede por magia. Trata-se de uma sede infinda, <name>, é preciso controlar o que você absorve e liberar via Torrente Arcana. Se falhar, você se tornará um dos ignóbeis... desesperadamente dependente e insano.\n\nEncontre uma moreia de mana e descarregue sua Torrente Arcana sobre ela. Aprenda a dominar seu poder. Quando liberar Torrente Arcana suficiente, volte aqui.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8347] = {title = "O auxílio aos vanguardeiros", objective = "Fale com a Vanguardeira Alarion perto da ponte que leva ao sul da Ilha Andassol.Magistra Erona quis que você seguisse para Luaprata com o intuito de auxiliar nossos esforços de retomada... isso é, depois que você tivesse resolvido o problema na Academia Falthrien. Você não nos desapontou, <name>.\n\nNo caminho para a Praça Asa do Falcão, a sul daqui, fale com a Vanguardeira Alarion. Ela não fica longe da ponte.\n\nOs vanguardeiros cuidam do transporte de mercadoria daqui para o continente e, como você se mostrou tão <despachado/despachada>, sua ajuda lhes seria útil.", description = "Magistra Erona quis que você seguisse para Luaprata com o intuito de auxiliar nossos esforços de retomada... isso é, depois que você tivesse resolvido o problema na Academia Falthrien. Você não nos desapontou, <name>.\n\nNo caminho para a Praça Asa do Falcão, a sul daqui, fale com a Vanguardeira Alarion. Ela não fica longe da ponte.\n\nOs vanguardeiros cuidam do transporte de mercadoria daqui para o continente e, como você se mostrou tão <despachado/despachada>, sua ajuda lhes seria útil.", progress = nil, completion = "Saudações, &lt;class&gt;! Fui informada de que um jovem e talentoso &lt;race&gt; que esteve a serviço da Magistra Erona talvez viesse nos ajudar. Sua ajuda é mais do que bem-vinda, principalmente vinda de alguém que se mostrou tão competente.\n\nEspero que haja interesse de sua parte em aceitar uma ou duas incumbências nossas.", rewards = "Você receberá:\n\n"},
[8348] = {title = "O sinete dos duques", objective = "Leve o Sinete Abissal para Bor Juba Agreste no Forte Cenariano.", description = "Huum me disse que você descobriu como invocar os duques abissais! É um feito e tanto.\n\nE sabe o que mais seria um baita feito? Arrebentar de verdade com um deles. Isso deve dar aos subordinados deles algo em que pensar, se é que você me entende. Traga o sinete dele como prova.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSaca com Espólios"},
[8349] = {title = "Bor Juba Agreste", objective = "Fale com Bor Juba Agreste no Forte Cenariano.", description = "Agora que você tem um medalhão e pode convocar um Duque Abissal, fale com Bor Juba Agreste. Ele mandará você em uma missão contra o Conselho Abissal.\n\nBoa sorte, <name>. E, se precisar de mais medalhões, fale comigo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8350] = {title = "Concluindo a entrega", objective = "Entregue o Pacote da Erona à Estalajadeira Delaniel na estalagem na Praça Asa do Falcão, que fica na Floresta do Canto Eterno.", description = "A morte de outro vanguardeiro é uma verdadeira tragédia, mas é um risco que todos assumimos ao aceitar a função. A Magistra Erona na Torre do Sol precisa que este pacote chegue à estalagem na Praça Asa do Falcão o quanto antes.\n\nEstamos com falta de mão-de-obra e não há mais ninguém disponível para nos ajudar.\n\nNão vou mentir para você, o caminho para a Praça Asa do Falcão pode ser extremamente perigoso longe da segurança da Alameda Alvorecescente. Você se acha capaz, <name>?", progress = "Bem-vindo à minha estalagem, <class>. Devo deduzir que você tem algo para mim?", completion = "Obrigada. Sou realmente grata pela presteza da entrega.\n\nSinta-se em casa enquanto estiver aqui. Caso ainda não o tenha feito, você pode obter uma pedra de regresso e vincular-se a esta estalagem. Com a pedra de regresso, você pode voltar para uma estalagem a cada meia hora. Se perder a pedra de regresso, basta voltar a uma estalagem e o estalajadeiro lhe dará outra de bom grado.", rewards = "Você receberá:\n\n"},
[8351] = {title = "Bor quer lhe falar", objective = "Fale com Bor Juba Agreste no Forte Cenariano.", description = "Agora que você consegue se disfarçar como integrante da cúpula do Conselho do Crepúsculo, tenho certeza de que Bor Juba Agreste vai querer falar com você. Vá ter com ele e se prepare para uma missão das mais difíceis.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8352] = {title = "Cetro do conselho", objective = "Leve um Cetro Abissal para Bor Juba Agreste no Forte Cenariano.", description = "Huum e eu estávamos falando de você. Ele me disse que você está quase pront<o/a> para tentar enfrentar um lorde abissal!\n\nPreciso lhe dizer que é uma iniciativa deveras perigosa, e é melhor você chamar reforços. E eu não estou falando de dois ou três camaradas, mas de uma força de elite pronta para a batalha. Se seus aliados forem bons como você, vocês podem até vencer!\n\nSe vencerem, e eu quero provas, como sempre, eu os recompensarei imensamente.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBaú com Espólios"},
[8353] = {title = "Imito uma galinha por uma balinha", objective = "Use a expressão /galinha diante do Estalajadeiro Aguardente e, em troca, ele lhe dará uma Balinha de Altaforja!", description = "Ah, você está brincando de gostosuras ou travessuras no lugar de um amiguinho doente que não pôde sair pra brincar, é? Eu já ia perguntar se você não acha que está <velho/velha> demais pra sair na rua atrás de doces...\n\nBom, para ganhar as minhas Balinhas de Altaforja, você vai ter que... cacarejar como uma galinha! As galinhas, <meu caro/minha cara> adoram balinhas... Tá, tá bom, elas não ADORAM de verdade... Mas você só vai ganhar a balinha se cacarejar para mim!\n\nE então, <class>, você dá conta do recado? Está na hora de ganhar balinha!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBalinha de Altaforja"},
[8354] = {title = "Imito uma galinha por uma balinha", objective = "Use a expressão /galinha diante do Estalajadeiro Ronan e, em troca, ele lhe dará uma Balinha da Cidade Baixa!", description = "Ah, você está brincando de gostosuras ou travessuras no lugar de um amiguinho doente que não pôde sair pra brincar, é? Eu já ia perguntar se você não acha que está <velho/velha> demais pra sair na rua atrás de doces...\n\nBom, para ganhar as minhas Balinhas de Altaforja, você vai ter que... cacarejar como uma galinha! As galinhas, <meu caro/minha cara> adoram balinhas... Tá, tá bom, elas não ADORAM de verdade... Mas você só vai ganhar a balinha se cacarejar para mim!\n\nE então, <class>, você dá conta do recado? Está na hora de ganhar balinha!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBalinha da Cidade Baixa"},
[8355] = {title = "Café com pão, café com pão", objective = "Use a expressão /trem ao falar com Didiê Du Mocó para ganhar uma Jujuba de Gnomeregan!", description = "Gostosuras ou travessuras! Oba!\n\nBem, eu sei que você está sendo legal e coisa e tal, pegando balinhas para uma criança doente, mas não dá para livrar a sua cara assim tão fácil! Eu tenho aqui umas deliciosas Jujubas de Gnomeregan para aquela criancinha doente... se você imitar o barulho de um trem! Rá rá, isso aí, vai ter que pagar uma prenda! Se quiser ganhar a jujuba vai ter que fazer o barulho de trem!\n\nTic-tac piuí, <class>!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nJujuba de Gnomeregan"},
[8356] = {title = "Mamãe, sou forte!", objective = "Use a expressão /muque na frente da estalajadeira Cristine para ganhar um Nougat de Ventobravo!", description = "Mas que... fantasia... interessante essa sua. Eu sei que você está sendo legal, ajudando uma criança doente a brincar de gostosuras ou travessuras e tal, mas, mesmo assim, você vai ter que pagar uma prenda para mim!\n\nBem, vejamos... Você está aí, $Gtodo fortinho:toda fortinha; nessa... fantasia... Então quero ver você me mostrar o muque! É isso aí, mostra o muque, <senhor fortão/dona fortona>!\n\nSó assim para você ganhar um delicioso Nougat de Ventobravo. É um docinho de Noturnália fenomenal.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nNougat de Ventobravo"},
[8357] = {title = "Tudo por um marzipã", objective = "Dance para a Estalajadeira Saliennte e, em troca, ela lhe dará Marzipã de Darnassus!", description = "Olha, não sei não, esse papo de criancinha doente. Sei é que você não vai levar gostosura nenhuma sem fazer a travessura! Não somos nós que vamos nos meter a mudar uma tradição milenar, não é mesmo?\n\nPois então, pra levar o marzipã vai ter que DANÇAR! Ah, é isso mesmo, vamos balançando esse esqueleto! Dance para mim e eu lhe darei uns marzipãs deliciosos!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMarzipã de Darnassus"},
[8358] = {title = "Café com pão, café com pão", objective = "Use a expressão /trem diante de Kali Remik para ganhar uma Jujuba Lançanegra!", description = "Gostosuras ou travessuras? Isso aí, cara!\n\nBem, eu sei que você está sendo legal e coisa e tal, pegando bala pruma criança doente, mas não dá para livrar a sua cara assim tão fácil, não! Eu tenho aqui na minha mão umas deliciosas Jujubas Lançanegra praquela criança doente... se você imitar o barulho de um trem! Rá rá, isso mesmo! Vai ter que pagar uma prenda! Se quiser ganhar a jujuba vai ter que fazer barulho de trem!\n\nTchuco-tchuco, <class>!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nJujuba Lançanegra"},
[8359] = {title = "Mamãe, sou forte!", objective = "Use a expressão /muque na frente da Estalajadeira Gryshka para ganhar um Nougat de Orgrimmar!", description = "Que... fantasia... interessante essa sua. Sei que você está sendo legal, ajudando uma criança doente a brincar de gostosuras ou travessuras e coisa e tal, mas, mesmo assim, vai ter que pagar uma prenda para mim!\n\nBem, vejamos... Você está aí, $Gtodo fortinho:toda fortinha; nessa... fantasia... Então quero ver você me mostrar o muque! É isso aí, mostra o muque, <senhor fortão/dona fortona>!\n\nSó assim você vai ganhar um delicioso Nougat de Orgrimmar. É um docinho de Noturnália fenomenal.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nNougat de Orgrimmar"},
[8360] = {title = "Tudo por um marzipã", objective = "Use a expressão /dançar na frente da Estalajadeira Pala e ela lhe dará um Marzipã do Penhasco do Trovão em troca!", description = "Olha, não sei não, esse papo de criancinha doente. Sei é que você não vai levar gostosura nenhuma sem fazer a travessura! Não somos nós que vamos nos meter a mudar uma tradição milenar, não é mesmo?\n\nPois então, pra levar o marzipã, você vai ter que DANÇAR! Ah, é isso mesmo, vamos balançando esse esqueleto! Dance para mim e eu lhe darei uns marzipãs deliciosos!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMarzipã do Penhasco do Trovão"},
[8361] = {title = "Contatos abissais", objective = "Bor Juba Agreste no Forte Cenariano, em Silithus, quer que você acabe com um Templário Abissal e leve um Escudo Abissal para ele como prova.", description = "Vimos os sectários do Martelo do Crepúsculo realizando rituais para evocar poderosos nobres elementais. Não sabemos se esses são os seres que os sectários adoram ou se agem como intermediários para forças ainda mais poderosas. Uma coisa é certa: eles estão recebendo ordens desses elementais.\n\nQuero que você encontre uma maneira de contactar um templário abissal e lhe dê uma boa surra! Nada desestabiliza mais as tropas do que derrubar a cadeia de comando. Então traga de volta uma prova.", progress = "Você terminou o trabalho?", completion = "Há! Você conseguiu, &lt;name&gt;! O Martelo do Crepúsculo em breve será esmagado!", rewards = "Você receberá:\n\nSaco com Espólios"},
[8362] = {title = "Escudos abissais", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSaco com Espólios"},
[8363] = {title = "Sinetes abissais", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSaca com Espólios"},
[8364] = {title = "Cetros abissais", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nBaú com Espólios"},
[8365] = {title = "Chapéus de pirata, io-hô!", objective = "Sissi Ashando quer que você colete 20 Chapéus de Pirata dos Mares do Sul e leve-os para ela no Porto de Bondebico, em Tanaris.", description = "Você é a ajuda que o Cartel enviou? Acho que vai servir. Ouça com atenção, <class>. Como tu sabe, sou quem fornece os trajes elegantes para a elite da sociedade azerothiana. Reis e rainhas, chefes guerreiros e, bem, seja lá como a Horda chama suas rainhas. Todos que usam o que eu escolho estão na moda. E eles pagam uma fortuna por isso, o que é a melhor parte.\n\nEntão o lance é o seguinte. Vá até a Enseada do Armador Perdido e obtenha vários chapéus de pirata por qualquer método. Tu acha que dá conta disso?", progress = "Tu tá com todos os 20 chapéus? Não acredito, mostra aí. É bom que estejam perfeitos!", completion = "Ah, você é um amor, ainda que tenha demorado mais tempo do que de costume. Esta remessa deve me render um bom dindim, assim que eu limpar e personalizar esses chapéus. E, como estou sempre disposta a recompensar um trabalho bem feito, toma aqui para você. Sugiro que use parte disso em um banho, queridinho.", rewards = "Você receberá:\n\n"},
[8366] = {title = "Recadinho dos Mares do Sul", objective = "O Chefe de Segurança Silvadágua do Porto de Bondebico, em Tanaris, quer que você mate 10 Piratas dos Mares do Sul, 10 Flibusteiros dos Mares do Sul, 10 Estivadores dos Mares do Sul e 10 Espadachins dos Mares do Sul.", description = "Argh! Eles esperam que eu faça o impossível!\n\nO Cartel Bondebico quer que eu ensine uma lição aos piratas dos Mares do Sul. O problema é que não posso estar em dois lugares ao mesmo tempo. Preciso ficar aqui pra manter a paz.\n\nUltimamente, os piratas têm desconsiderado o dinheiro da proteção que o cartel regularmente investe. Então quero que tu vá até lá no meu lugar e faça eles se arrependerem.\n\nTu vai encontrá-los na Enseada do Armador Perdido, que não fica muito longe daqui, em direção ao sul. Obrigado, <name>, te devo essa!", progress = nil, completion = "&lt;name&gt;! Você é demais!!!\n\nTenho certeza que o Cartel vai largar um pouco do meu pé agora que você deu um jeito nessa escória dos Mares do Sul. Vamos ver, o que posso te dar como recompensa? Que tal alguns cobres e você escolhe um desses pelo seu trabalho?", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBalde Cefálico do Mar do SulBotas do Mojo do Mar do Sul\n\nVocê também receberá:\n\n"},
[8367] = {title = "Por grande honra", objective = "Leve 3 Marcas de Honra do Vale Alterac, 3 Marcas de Honra da Bacia Arathi e 3 Marcas de Honra da Ravina Brado Guerreiro a um Armipotente da Horda fora dos campo de batalha.", description = "Servir a Horda em uma das grandes batalhas contra a Aliança é uma grande honra! Entretanto, os poucos que serviram em TODAS as linhas de frente é que são os verdadeiros heróis. Não é ganhando briguinhas que acabaremos com nosso inimigo, mas sim combatendo-os em uma guerra coordenada.\n\nEsmague a Aliança na Bacia Arathi, no Vale Alterac e na Ravina Brado Guerreiro! Pela honra! Pela Horda!!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +2388\n\nVocê receberá:\n\n"},
[8368] = {title = "Batalha da Ravina Brado Guerreiro", objective = "Leve 3 Marcas de Honra do Brado Guerreiro a um Armipotente da Horda do lado de fora do campo de batalha.", description = "A batalha na Ravina Brado Guerreiro contra as Sentinelas da Asa de Prata é de grande importância. Usando como desculpa a defesa de uma floresta que não os pertence, a Aliança tenta impedir a Horda de utilizar uma das nossas maiores fontes de madeira.\n\nNão permita que isso aconteça, <name>! Volte e me traga provas de que você foi útil à Horda de alguma forma!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +50\n\nVocê receberá:\n\n"},
[8369] = {title = "Invasores do Vale Alterac", objective = "Leve 3 Marcas de Honra do Vale Alterac a um Armipotente da Horda do lado de fora do campo de batalha.", description = "Ouça, <name>. Não sei o que você andou ouvindo por aí sobre a batalha que travamos no Vale Alterac, mas a verdade ainda é uma só: a Expedição Lançatroz invadiu o território dos Lobo do Gelo. O dia em que permitirmos que a Aliança tome um território da Horda sem receber resposta brutal será o mesmo dia em que eu largarei meu machado e passarei o resto da minha vida sentindo vergonha de mim mesmo. Vamos! Nos ajude a expulsar os invasores!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +398\n\nVocê receberá:\n\n"},
[8370] = {title = "Conquistando a Bacia Arathi", objective = "Leve 3 Marcas de Honra da Bacia Arathi a um Armipotente da Horda do lado de fora do campo de batalha.", description = "Nosso conflito com a Aliança se tornou uma verdadeira guerra. Agora, ambos os lados deixaram de usar falsos pretextos para se confrontarem.\n\nO motivo da batalha na Bacia Arathi é pelos recursos que ela nos oferece, simplesmente. Das duas, uma: ou a Aliança usa tais recursos contra nós, ou nós os usamos para manter a nossa máquina de guerra funcionando. Ajude os Profanadores a manter o fluxo de recursos de que precisamos, <name>!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +82\n\nVocê receberá:\n\n"},
[8371] = {title = "Esforços combinados", objective = "Leve 3 Marcas de Honra do Vale Alterac, 3 Marcas de Honra da Bacia Arathi e 3 Marcas de Honra da Ravina Brado Guerreiro a um General de Brigada da Aliança fora dos campos de batalha.", description = "Bah, não existem mais disputas isoladas contra a Horda, <guri/guria>.\n\nVencer em uma frente significa privar a Horda de se fortalecer em uma outra batalha. Da mesma forma, perder uma batalha pode dar vantagem ao nosso oponente em algum outro canto do mundo.\n\nA Aliança precisa de mais combatentes com esse tipo de visão global... veteranos calejados pela guerra, tchê! <name>, fale comigo novamente depois de ter servido contra a Horda em todos os nossos campos de batalha.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +2388\n\nVocê receberá:\n\n"},
[8372] = {title = "Luta pela Ravina Brado Guerreiro", objective = "Leve 3 Marcas de Honra da Ravina Brado Guerreiro a um General de Brigada da Aliança do lado de fora do campo de batalha.", description = "As Sentinelas da Asa de Prata estão em guerra com os Pioneiros do Brado Guerreiro por causa da destruição que os orcs estão causando à floresta. Mas existem outros motivos para defendê-la que não o simples amor pela natureza.\n\nEla forma uma barreira estratégica que protege o Vale Gris de um ataque em larga escala. Sem a floresta, levaria apenas alguns dias para que Astranaar se tornasse uma extensão dos Sertões.\n\nFaça sua parte na luta contra os Pioneiros do Brado Guerreiro, <name>! Pela Aliança!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +50\n\nVocê receberá:\n\n"},
[8373] = {title = "O poder do pinho", objective = "Use o Limpador de Bomba Fedida para remover qualquer Bomba Fedida dos Renegados atirada na Costa Sul. Fale novamente com o Sargento Hartman, na Costa Sul, quando você tiver terminado.", description = "O que mais odeio na Noturnália são as bombas fedidas que os Renegados usam na Costa Sul. Quem diria que comemorar a libertação envolveria algo tão sujo e fedido?\n\nDurante a Noturnália, use este limpador para remover qualquer bomba fedida que a Horda possa atirar aqui na vila. O perfume de pinheiro combate os odores desagradáveis.\n\nFaça sua parte para manter a Costa Sul limpa, e lhe darei algumas gostosuras de Noturnália que tenho certeza de que você vai gostar. Combinado?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGostosura de Abóbora da Noturnália"},
[8374] = {title = "Reivindicando a Bacia Arathi", objective = "Leve 3 Marcas de Honra da Bacia Arathi a um General de Brigada da Aliança do lado de fora do campo de batalha.", description = "Para vencer uma guerra, não basta ser mais inteligente ou mais forte que o inimigo. Muitas vezes, a vitória é obtida simplesmente ao produzir mais que ele.\n\nO que temos na Bacia Arathi é um grande estoque de suprimentos à espera de ser tomado por um dos lados.\n\nSe a Aliança sair vitoriosa, nossas tropas terão mais espadas e lanças. Se a Horda vencer, essas espadas e lanças serão feitas e estarão apontadas contra o nosso exército! Agora é a hora de ajudar a Aliança na Bacia Arathi, <name>.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +82\n\nVocê receberá:\n\n"},
[8375] = {title = "Lembrem-se do Vale Alterac!", objective = "Leve 3 Marcas de Honra do Vale Alterac a um General de Brigada da Aliança do lado de fora do campo de batalha.", description = "Não se engane, <name>. A Horda tem razão ao afirmar que o Vale Alterac é território dos Lobo do Gelo.\n\nEntenda, a Expedição Lançatroz chegou pacificamente à procura de minérios e relíquias, e os Lobo do Gelo reagiram na mais brutal agressão que a Aliança já sofreu.\n\nJamais nos esqueçamos dos bravos anões que morreram naquele covarde ataque surpresa. Vá, <class>! Faça a sua parte na batalha pelo Vale Alterac. Pela Aliança!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +398\n\nVocê receberá:\n\n"},
