[8405] = {title = "Luta pela Ravina Brado Guerreiro", objective = "Não se esqueça que a batalha pela Ravina Brado Guerreiro é muito mais do que defender algumas árvores. Lutamos para manter a barreira natural entre o Vale Gris e os Sertões. Tem alguma notícia do campo de batalha, <name>?", description = nil, progress = "Não se esqueça que a batalha pela Ravina Brado Guerreiro é muito mais do que defender algumas árvores. Lutamos para manter a barreira natural entre o Vale Gris e os Sertões. Tem alguma notícia do campo de batalha, <name>?", completion = "Bom trabalho, &lt;name&gt;. Graças a você, nossas defesas naturais no Vale Gris ficaram intactas por mais um dia.", rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +138"},
[8406] = {title = "Luta pela Ravina Brado Guerreiro", objective = "Não se esqueça que a batalha pela Ravina Brado Guerreiro é muito mais do que defender algumas árvores. Lutamos para manter a barreira natural entre o Vale Gris e os Sertões. Tem alguma notícia do campo de batalha, <name>?", description = nil, progress = "Não se esqueça que a batalha pela Ravina Brado Guerreiro é muito mais do que defender algumas árvores. Lutamos para manter a barreira natural entre o Vale Gris e os Sertões. Tem alguma notícia do campo de batalha, <name>?", completion = "Bom trabalho, &lt;name&gt;. Graças a você, nossas defesas naturais no Vale Gris ficaram intactas por mais um dia.", rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +228"},
