[8409] = {title = "Barris arruinados", objective = "Fale novamente com a Invoca-trevas Yanka no Festival do Homem de Palha, nas Clareiras de Tirisfal.", description = "Os ovos podres já foram colocados dentro do barril e os futuros lotes de cerveja da Estalagem da Costa Sul serão arruinados!\n\nAgora você deve se apresentar à Invoca-trevas Yanka no Festival do Homem de Palha, nas Clareiras de Tirisfal, e relatar seu sucesso.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGostosura de Abóbora da Noturnália"},
[8410] = {title = "Mestre dos elementos", objective = "Colete uma amostra de ar, fogo, terra e água para Bath'rah, o Vigia dos Ventos.", description = "Bath'rah, o Vigia dos Ventos mandou buscar um xamã com a sua sabedoria. Ele pede que você traga uma amostra de cada um dos elementos para provar que está pront<o/a>.\n\nPara encontrar Bath'rah, vá ao rio a leste da Serraria Tarren. Siga o rio em direção ao norte até encontrar as ruínas onde Bath'rah mora.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8411] = {title = "Dominando os elementos", objective = "Leve os elementos terra, ar, fogo e água para Bath'rah, o Vigia dos Ventos.", description = "Bem vind<o/a>, <cara/garota>. Tu me traz os elementos e depois a gente se fala.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8412] = {title = "Totem de espírito", objective = "Leve as partes para Bath'rah, o Vigia dos Ventos, fazer o totem de espírito.", description = "Os espíritos elementais deste lugar não gostam mais de mim.\n\nTu sabe sobre os totens mais do que ninguém, e eu preciso de um totem de espírito pra me proteger. Já temos os elementos, agora precisamos de outra coisa mais natureza. Mais primitiva.\n\nNas Terras Pestilentas Ocidentais, você pode matar ursos pra tirar as garras e aranhas pra pegar os olhos. As aves carniceiras comem tudo, então podem ter um ou outro.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8413] = {title = "O Vodu", objective = "Leve as Penas Vodu para Bath'rah, o Vigia dos Ventos.", description = "O totem de espírito não é suficiente. Vamo precisar de um pouco de magia vodu pra fazer ele funcionar direito.\n\nOs trolls no templo submerso têm penas cheias de magia vodu. Vai lá matar Talho, Mirra, Zolo, Hukkah, Zul'lor e Lorro. Aí vamo ter magia suficiente prum verdadeiro totem de espírito.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPunhos de AzuritaEspírito de Água EnamoradoCajado Selvagem\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[8414] = {title = "Dissipando o mal", objective = "Colete 20 Pedras do Flagelo do Lacaio e leve-as ao Sumo Sacerdote Thel'danis nas Terras Pestilentas Ocidentais.", description = "Saudações, <class>. Cruzados como você são muito bem-vindos nessas regiões desoladas.\n\nSua força sagrada é necessária para ajudar a combater os mortos-vivos sem fim por aqui. Precisamos que você colete um grande número de pedras do flagelo e leve-as para o Sumo Sacerdote Thel'danis. O sumo sacerdote pode ser encontrado guardando a Tumba de Uther na Colina do Pesar, não muito longe daqui.\n\nApenas alguém com uma Ordem da Aurora Argêntea pode esperar encontrar pedras do flagelo, então fale com a Oficial Cândida, se é que ainda não falou.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8415] = {title = "Morro do Ventogelante", objective = "Fale com o Comandante Ashlam Punhobom no Morro do Ventogelante.", description = "O Comandante Ashlam Punhobom está convocando um paladino da tua pureza e valor, <name>. Tu pode encontrá-lo no Morro do Ventogelante, que fica na parte sul das Terras Pestilentas Ocidentais. Procure-o e o ajude na causa dele pela Luz!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8416] = {title = "Pedras do Flagelo inertes", objective = "Leve a Pedra do Flagelo Inerte ao Comandante Ashlam Punhobom no Morro do Ventogelante.", description = "O mal foi purgado das pedras, <name>.\n\nConheço os planos do Comandante Ashlam, e ele vai precisar de uma única pedra inerte. Leve-a até ele para que possamos banhar o Flagelo em fogo da retidão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8417] = {title = "Um espírito atormentado", objective = "Fale com o Herói Caído da Horda na entrada da Barreira do Inferno.", description = "Na Barreira do Inferno, há um espírito atormentado que já foi um grande herói da Horda. A honra de um guerreiro não morre com o corpo, e seria bom você falar com ele.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8418] = {title = "Forjando o megalito", objective = "Leve as Penas Vodu para Ashlam Punhobom.", description = "Agora temos que combinar as pedras do flagelo inertes e criar uma arma para esmagar as fileiras dos mortos-vivos. Não é uma tarefa trivial, <name>. Somente os trolls do templo submerso possuem o tipo de magia rara capaz de virar o mal de alguma coisa contra ela mesma.\n\nNo templo, os trolls são conhecidos como Talho, Mirran, Zolo, Hukkah, Zul'lor e Lorro. Não sinta remorso pelas almas corrompidas deles... elas não estão livres do mal.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nLâmina Forjada em LuzOrbe SantificadoSinete Cavalheiresco\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nMegalito Sagrado"},
[8419] = {title = "Pedido de diabrete", objective = "Leve um pedaço de Tecido Vil para Diabretim, em Selva Maleva.", description = "<name>, um diabrete em Selva Maleva chamado Diabretim solicitou sua presença. É meio estranho, porque ele pediu que você levasse um pedaço de tecido vil como sinal de boa fé. Considerando o poder que você já tem como brux<o/a>, imagino que saiba que os sátiros de Selva Maleva carregam este tecido vil.\n\nVocê pode encontrar Diabretim ao norte do Salto da Peçonha, que é lindo nessa época do ano, pelo que ouvi dizer.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8420] = {title = "Quente e pinicante", objective = "Leve um pedaço de Tecido Vil para Diabretim, em Selva Maleva.", description = "Talvez isso pareça estranho vindo de um diabrete, mas eu gostaria de ter um bichinho de estimação. Gostaria mesmo. Não é justo que Niby me tenha e eu não tenha nada!\n\nNa verdade, nem precisa ser um bichinho vivo. Eu precisaria de alguma coisa pra fazer ele, como um pedaço de tecido vil. Os sátiros aqui em Selva Maleva às vezes têm tecido vil, então você só teria que matar alguns deles até encontrar. Não parece tão ruim, não é?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8421] = {title = "O enchimento errado", objective = "Leve 10 pedaços de Madeira Podre e 4 Essências de Peçonha para Diabretim em Selva Maleva.", description = "Agora que tenho o tecido vil, posso começar a fazer *meu* bichinho de estimação.\n\nMas ele vai precisar de olhos. Talvez você possa pegar um pedaço de madeira podre do povo de Ferrárbol pra mim, para eu poder entalhar lindos olhos. Eles ficam ao norte, na Floresta Ferrárbol.\n\nTambém vou precisar de essência de peçonha para dar ao meu bichinho aquele cheiro de demônio novo. As gosmas maculadas têm a essência, e ficam aqui bem perto de Niby e de mim.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8422] = {title = "Penas de troll", objective = "Leve um total de 6 Penas Vodu dos trolls do templo submerso.", description = "Meu bichinho de estimação está quase pronto. Agora só preciso de algo para preenchê-lo.\n\nApenas as penas vodu dos trolls no templo submerso vão servir. Eles atendem pelos nomes de Talho, Mirran, Zolo, Hukkah, Zul'lor e Lorro. Mate eles. Mate todos eles.\n\nNão é uma tarefa trivial, <name>, então vou fazer valer a pena lhe dando algo do Niby... ele provavelmente não precisa disso mesmo.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCeifalmasFragmento do AbismoVestes da Sujeição\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[8423] = {title = "Parentesco guerreiro", objective = "Mate 7 Javiltuscos na Barreira do Inferno e fale novamente com o Herói Caído da Horda.", description = "<O herói te examina.>\n\nVocê é <um/uma> <class>, sem dúvida. Eu lhe pediria para realizar uma tarefa honrosa, mas preciso testar sua força primeiro.\n\nDizem que Rexxar testava se as lâminas dele estavam afiadas, matando um javiltusco com um único e ligeiro golpe. Agora vou medir sua força, vendo se consegue matá-los.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8424] = {title = "Guerra aos devotos da sombra", objective = "Mate os Devotos da Sombra na Barreira do Inferno e fale novamente com o Herói Caído da Horda.", description = "A presença contínua dos devotos da sombras na Barreira do Inferno me traz muita dor. Os rituais sombrios e feitiços deles me causam uma agonia diferente de tudo que senti quando era vivo.\n\nAtravés de você, posso mandar minha própria mensagem de dor. Vá em frente e dissemine minha ira.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8425] = {title = "Penas Vodu", objective = "Leve as Penas Vodu dos trolls do Templo Submerso para o Herói Caído da Horda.", description = "Sua honra trouxe um pouco de paz para mim. É justo que eu lhe ofereça algo em troca.\n\nMinhas armas e armadura eram esplêndidas enquanto eu vivia, mas são inúteis para mim agora. Teriam melhor uso se ficassem com você.\n\nVá ao templo submerso e destrua os guardiões trolls Talho, Mirran, Zolo, Hukkah, Zul'lor e Lorro. Eles carregam penas mágicas que podem reformar meu corpo físico por tempo suficiente para lhe conceder uma recompensa.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nViseira da FúriaFrasco de DiamanteOmbros de Navalhaço\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[8426] = {title = "Batalha da Ravina Brado Guerreiro", objective = "Leve 3 Marcas de Honra da Ravina Brado Guerreiro a um Armipotente da Horda do lado de fora do campo de batalha.", description = "A batalha na Ravina Brado Guerreiro contra as Sentinelas da Asa de Prata é de grande importância. Usando como desculpa a defesa de uma floresta que não os pertence, a Aliança tenta impedir a Horda de utilizar uma das nossas maiores fontes de madeira.\n\nNão permita que isso aconteça, <name>! Volte e me traga provas de que você foi útil à Horda de alguma forma!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +82\n\nVocê receberá:\n\n"},
