[8443] = {title = "Conquistando a Bacia Arathi", objective = "A guerra contra a escória da Aliança requer recursos, <name>! Não podemos permitir que nossos estoques acabem no meio de uma batalha. Vá e faça tudo para que a Bacia Arathi continue sob nosso poder!", description = nil, progress = "A guerra contra a escória da Aliança requer recursos, <name>! Não podemos permitir que nossos estoques acabem no meio de uma batalha. Vá e faça tudo para que a Bacia Arathi continue sob nosso poder!", completion = "Excelente trabalho, &lt;name&gt;! Com a Bacia Arathi sob nosso controle, não precisaremos nos preocupar com nossos suprimentos.", rewards = "O seguinte feitiço será lançado em você:\n\nPontos de Honra +398"},
[8446] = {title = "Mergulhado em pesadelos", objective = "Encontre alguém capaz de decifrar o significado do Objeto Mergulhado em Pesadelos.\n\nTalvez algum druida poderoso possa lhe ajudar.", description = "Você sente corrupção vazar deste objeto.\n\nDeve existir alguma ligação entre este objeto e todos os dragões saindo do Sonho. Você precisa encontrar alguém que consiga decifrar o significado deste item. Talvez os druidas da Clareira da Lua ou do Forte Cenariano possam ajudá-<lo/la>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8447] = {title = "Despertar das lendas", objective = "Aguarde enquanto o Guardião Remulos, na Clareira da Lua, purifica o Objeto Mergulhado em Pesadelos.", description = "As águas do Lago Eluna'ara devem ser capazes de diluir os pesadelos que encharcam este objeto. Aguarde um instante enquanto eu tento limpá-lo, <name>.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAnel-sinete de Malfurion"},
[8460] = {title = "Aliando-se aos Presamatos", objective = "Grazle quer que você prove seu valor matando 6 Guerreiros de Lenha Morta, 6 Desbravadores de Lenha Morta e 6 Jardineiros de Lenha Morta. Quando você terminar, fale novamente com ele na parte sul da Selva Maleva, perto do Santuário Esmeralda.", description = "Os Presamatos são a única tribo de pelursos que escapou da corrupção. No entanto, muitas outras raças agora matam os pelursos às cegas, sem se preocupar em saber se são inimigos ou amigos. Por isso os Presamatos confiam em muito pouca gente.\n\nInteressad<o/a> em provar o seu valor? Rechace a tribo corrompida dos Lenha Morta, da Selva Maleva, e podemos um dia considerá-l<o/a> u<m/ma> aliad<o/a>. A oeste daqui você encontrará os primeiros Lenha Morta: guerreiros, desbravadores e jardineiros.", progress = nil, completion = "Ainda sofremos muito pelos eventos do passado. Obrigado pelo que você fez para ajudar a proteger os Presamatos. Como sinal de nossa amizade crescente, por favor, aceite esta humilde oferta.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nColete do Guardador da TerraCinto do Vigia do Covil\n\nVocê também receberá:\n\n"},
[8461] = {title = "Lenha Morta do Norte", objective = "Nafien quer que você mate 6 Vigias do Covil de Lenha Morta, 6 Vingadores de Lenha Morta e 6 Xamãs de Lenha Morta. Fale novamente com ele na parte norte da Selva Maleva, perto da entrada do Domínio dos Presamatos.", description = "Há uma aldeia de pelursos Lenha Morta a sudoeste daqui. A corrupção permeia aquele lugar, e quero acabar com isso antes que eles se tornem um problema.\n\nA tribo Lenha Morta é maligna, mas são os efeitos da mácula vil que poluem as suas mentes. Embora essa hostilidade não seja uma qualidade inata, eles não podem ser ajudados. Entristece-me termos que lutar contra nossos irmãos.\n\nPor favor, <name>, peço que você faça o que precisa ser feito. Só me poupe dos detalhes. Essas imagens perturbadoras me afetam demais.", progress = nil, completion = "Sinto que você conquistou a vitória. Vitória... uma palavra vazia, considerando-se o que teve de ser feito. De qualquer forma, você provou ser digno de nossa confiança.\n\nPode ser que ainda demore algum tempo para os Presamatos receberem você sem hostilidade em nossos domínios. Contudo, a sua perseverança há de dissolver qualquer suspeita que meus irmãos possam ter em relação a você. Nós nos entenderemos, desde que você jamais nos traia ou empunhe armas contra nós.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPerneiras do UrsinoElmo do Desbravador\n\nVocê também receberá:\n\n"},
[8462] = {title = "Fale com Nafien", objective = "Viaje para o norte ao longo da estrada principal em Selva Maleva e fale com o pelurso chamado Nafien. Ele monta guarda diante da entrada do Domínio dos Presamatos.", description = "Talvez você seja capaz de lidar com uma ameaça maior do que nós, Presamatos, enfrentamos. Você certamente se mostrou capaz...\n\nOutro integrante do meu povo monta guarda diante da entrada do Domínio dos Presamatos, ao longo dessa estrada bem ao norte. O nome dele é Nafien. Embora possa parecer uma criatura feroz e desagradável, não o tema. Ele só procura ajuda contra as forças malignas que ameaçam nosso lar.", progress = nil, completion = "Ah... O vento trouxe rumores sobre aqueles que não pertencem à nossa tribo, mas pretendem se aliar aos pelursos Presamatos. Eu lhe dou as boas-vindas, mas devo avisar que minha tribo não reage bem a estranhos.\n\nVivemos tempos difíceis, e só podemos confiar naqueles que escolhem combater a corrupção da terra ao nosso lado... e nos provaram do que são capazes. Aqueles que não se encaixarem nisso serão recebidos com franca hostilidade ao entrarem no Domínio.", rewards = "Você receberá:\n\n"},
[8463] = {title = "Cristais de mana instáveis", objective = "Leve 6 Cristais de Mana Instáveis para Aledon Marcassol na Praça Asa do Falcão.", description = "Um grupo de Ignóbeis saqueou recentemente um suprimento de cristais de mana instáveis de um de nossos santuários arcanos. Se não forem manuseados corretamente, os cristais podem explodir com força suficiente para abrir um buraco nos muros da cidade.\n\nVeja bem, <name>, pouco me importa que eles se explodam ao mexer com fontes arcanas instáveis, mas um incidente como esse pode denegrir a imagem dos meus homens.\n\nTraga-me todos os cristais roubados que você encontrar. Talvez ainda haja alguns nas caixas.", progress = "Recuperou a mercadoria roubada, <name>?", completion = "Excelente trabalho, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[8464] = {title = "Atividade Invernosa", objective = "Salfa quer que você mate 8 Xamãs Invernosos, 8 Vigias do Covil dos Invernosos e 8 Ursinos Invernosos. Salfa está perto da entrada do Domínio dos Presamatos, em Hibérnia.", description = "Os Presamatos preferem não se abrir muito, <name>. Apesar de permitir a passagem daqueles em quem confiamos, evitamos ao máximo o contato com outras tribos de pelursos.\n\nUltimamente a tribo dos Invernosos se tornou cada vez mais hostil a nós. Parecem viver em um estado constante de fúria, completamente imersos no próprio medo e no ódio a tudo que não compreendem.\n\nPreciso pedir que você reduza o contingente deles, para diminuir a ameaça contra nós. Procure-os na Aldeia dos Invernosos, bem a leste daqui.", progress = nil, completion = "Eu lhe agradeço por tudo que você fez, por mais triste que tenha sido. Aceite isto como um símbolo da nossa gratidão.\n\nFicaríamos felizes se você pudesse continuar a nos ajudar, &lt;name&gt;. Podemos contar com raros aliados e, sem a sua ajuda, não teríamos dado conta sozinhos das ameaças contra a tribo.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas do Guardador da TerraLuvas do DesbravadorColete do Vigia do CovilAbraço do Ursino\n\nVocê também receberá:\n\n"},
[8465] = {title = "Fale com Salfa", objective = "Viaje pelo Domínio dos Presamatos até chegar a Hibérnia. Fale com Salfa, que está de guarda em frente à entrada do Domínio dos Presamatos.", description = "A ameaça da corrupção não vem apenas dos pelursos Lenha Morta ao sul, mas também do leste e dos pelursos Invernosos de Hibérnia. Se você deseja continuar nos ajudando, converse com Salfa, que está do outro lado do Domínio, próximo à entrada de Hibérnia. Para chegar lá, siga pelo caminho à esquerda.\n\nMas um aviso: se meu povo lá dentro considerar você hostil ou perigoso, eles atacarão. Viaje o mais rápido que puder, evitando conflitos. Matar um Presamatos, até mesmo em legítima defesa, só tornará as coisas mais difíceis para você.", progress = nil, completion = "Saudações, &lt;class&gt;, ficarei de guarda aqui como porta-voz dos Presamatos em Hibérnia e como defensor da tribo.\n\nNossos irmãos de Hibérnia, esta terra gélida, caíram vítimas da mesma onda corrupta que atingiu os pelursos da Selva Maleva. Se você veio aqui oferecer ajuda aos pelursos Presamatos, estamos prontos para aceitar sua oferta.", rewards = "Você receberá:\n\n"},
[8466] = {title = "Penas para Grazle", objective = "Apesar de você ter provado seu valor perante mim, ainda é preciso muito esforço para conquistar os meus desconfiados irmãos. Para tanto, vou continuar a ajudá-lo.\n\nAlguns dos pelursos Lenha Morta usam um ornamento na cabeça que vale como prova de que foram mortos. Traga-me uma pena de qualquer adorno de cabeça. Para cada cinco que você trouxer, sua recompensa será o reconhecimento dos Presamatos.", description = nil, progress = "Apesar de você ter provado seu valor perante mim, ainda é preciso muito esforço para conquistar os meus desconfiados irmãos. Para tanto, vou continuar a ajudá-lo.\n\nAlguns dos pelursos Lenha Morta usam um ornamento na cabeça que vale como prova de que foram mortos. Traga-me uma pena de qualquer adorno de cabeça. Para cada cinco que você trouxer, sua recompensa será o reconhecimento dos Presamatos.", completion = "Muito bem, &lt;name&gt;! Vou me certificar de que meus irmãos saibam de seus atos.\n\nNão se esqueça de trazer para mim todas as penas que encontrar. Vou continuar aceitando-as enquanto for necessário.", rewards = nil},
[8467] = {title = "Penas para Nafien", objective = "Apesar de você ter provado seu valor perante mim, ainda é preciso muito esforço para conquistar os meus desconfiados irmãos. Para tanto, vou continuar a ajudá-lo.\n\nAlguns dos pelursos Lenha Morta usam um ornamento na cabeça que vale como prova de que foram mortos. Traga-me uma pena de qualquer adorno de cabeça. Para cada cinco que você trouxer, sua recompensa será o reconhecimento dos Presamatos.", description = nil, progress = "Apesar de você ter provado seu valor perante mim, ainda é preciso muito esforço para conquistar os meus desconfiados irmãos. Para tanto, vou continuar a ajudá-lo.\n\nAlguns dos pelursos Lenha Morta usam um ornamento na cabeça que vale como prova de que foram mortos. Traga-me uma pena de qualquer adorno de cabeça. Para cada cinco que você trouxer, sua recompensa será o reconhecimento dos Presamatos.", completion = "Muito bem, &lt;name&gt;! Vou me certificar de que meus irmãos saibam de seus atos.\n\nNão se esqueça de trazer para mim todas as penas que encontrar. Vou continuar aceitando-as enquanto for necessário.", rewards = nil},
[8468] = {title = "PROCURA-SE: Thaelis, o Famélico", objective = "Entregue a Cabeça do Thaelis ao Sargento Kan'ren na Praça Asa do Falcão, que fica na Floresta do Canto Eterno.", description = "A cidade de Luaprata vem por meio deste oferecer uma recompensa pela cabeça de Thaelis, o Famélico, notório líder dos Ignóbeis. Ele é procurado por crimes recorrentes contra o povo de Luaprata, inclusive o assassinato de dois guardas da cidade.\n\nApresente-se ao sargento Kan'ren para recolher a recompensa.", progress = "Você tem algo para me mostrar?", completion = "Aquele patife do Thaelis finalmente teve o que merecia. Bom trabalho, &lt;name&gt;. Os Ignóbeis nos darão sossego por um tempo.", rewards = "Você receberá:\n\n"},
[8469] = {title = "Contas para Salfa", objective = "<name>, você conquistou a minha confiança, mas não a da minha tribo. No entanto, há algo que pode fazer para mudar isso.\n\nOs Invernosos são conhecidos por carregar contas espirituais que os protegem de entidades impuras. Elas não funcionaram como deveriam, e agora os próprios Invernosos são agentes da corrupção. Traga-me as contas espirituais dos pelursos Invernosos que tombarem em batalha. A cada cinco contas que você me trouxer, seu reconhecimento dentre os Presamatos aumentará.", description = nil, progress = "<name>, você conquistou a minha confiança, mas não a da minha tribo. No entanto, há algo que pode fazer para mudar isso.\n\nOs Invernosos são conhecidos por carregar contas espirituais que os protegem de entidades impuras. Elas não funcionaram como deveriam, e agora os próprios Invernosos são agentes da corrupção. Traga-me as contas espirituais dos pelursos Invernosos que tombarem em batalha. A cada cinco contas que você me trouxer, seu reconhecimento dentre os Presamatos aumentará.", completion = "Novamente você nos demonstra sua honra! Tomarei providências para que suas ações sejam reconhecidas pelo meu povo.\n\nNão deixe de trazer todas as contas espirituais de Invernosos que encontrar. A cada cinco contas que você me trouxer, falarei ainda mais de suas façanhas ao meu povo.", rewards = nil},
[8470] = {title = "Totem ritual de Lenha Morta", objective = "Leve o Totem Ritual de Lenha Morta para dentro do Domínio dos Presamatos e veja se um dos pelursos dali tem alguma utilidade para o artefato. Os Presamatos não lhe dirigirão a palavra a não ser que você tenha alcançado, no mínimo, a reputação de <tolerado/tolerada> entre eles.", description = "Você encontrou um dos totens rituais dos pelursos Lenha Morta. É um artefato de aparência comum, mas uma evidente sensação de desespero emana dele. Se houvesse um objeto que servisse como exemplo perfeito da corrupção que assola os pelursos Lenha Morta, claramente, haveria de ser este.\n\nUm artefato como este pode ter alguma utilidade para os pelursos do Domínio dos Presamatos... Quer dizer, se você tiver conquistado a confiança deles a ponto de lhe dirigirem a palavra.", progress = "Pois não, <class>. Como posso lhe ajudar? Você caminha entre nós pacificamente, apesar de haver, certamente, algo de errado. Sinto algo... algo perturbador...", completion = "Este totem... Eu o reconheço como proveniente de Lenha Morta, mas ele está impregnado pelo odor de decomposição. Qualquer que tenha sido a perversidade que jogou nossos irmãos contra nós, certamente está presente neste objeto.\n\nVocê foi sábia em nos trazer isto, &lt;name&gt;. Talvez, por meio de estudos e rituais, consigamos descobrir o que está poluindo as mentes de nossos irmãos. Obrigado, amiga. Por gentileza, aceite estes presentes em troca do seu achado.", rewards = "Você receberá:\n\nPoção Elevada de CuraPoção Elevada de Mana"},
[8471] = {title = "Totem Ritual Invernoso", objective = "Leve o Totem Ritual Invernoso para dentro do Domínio dos Presamatos e veja se um dos pelursos dali tem alguma utilidade para o artefato. Os Presamatos não lhe dirigirão a palavra a não ser que você tenha alcançado, no mínimo, a reputação de <tolerado/tolerada> entre eles.", description = "Você encontrou um dos totens rituais dos pelursos Invernosos. É um artefato de aparência comum, mas parece emanar uma evidente sensação de medo e desespero. Se muitos dos pelursos Invernosos de fato carregam essas coisas, isso certamente ajudaria a explicar condição atual deles.\n\nUm artefato como este pode ter alguma utilidade para os pelursos do Domínio dos Presamatos... Quer dizer, se você tiver conquistado a confiança deles a ponto de lhe dirigirem a palavra.", progress = "<class>, você se aproxima de nós de maneira pacífica, mas sinto que está aqui para tratar de assuntos sérios e graves... tanto para pelursos quanto para seu povo, <race>. O que você veio nos falar?", completion = "Essa coisa... tem a forma de um Totem Ritual Invernoso, mas foi desvirtuado por forças que não posso compreender. Seja qual for a mácula que tenha colocado os Invernosos contra nós, ela certamente está presente neste objeto.\n\nA sabedoria divina trouxe tanto você quanto isso até nós, &lt;name&gt;. Estudaremos esse objeto minuciosamente, e talvez algum dia os Invernosos não mais se voltarão contra nós.\n\nObrigado, amiga. Por favor, aceite essas oferendas pela sua benevolência.", rewards = "Você receberá:\n\nPoção Elevada de CuraPoção Elevada de Mana"},
[8472] = {title = "Defeito grave", objective = "Consiga 6 Núcleos Arcanos das Patrulhas Arcanas que perambulam pelas ruínas de Luaprata e entregue-os ao Magíster Jaronis na Praça Asa do Falcão, que fica na Floresta do Canto Eterno.", description = "As patrulhas arcanas, criadas pelos nossos antepassados para proteger a cidade, apresentaram um perigoso defeito. Elas não nos reconhecem mais como amos e estão nos atacando!\n\nMesmo tendo sido criadas há muitos anos, os núcleos arcanos que as alimentam ainda nos são muitos úteis e valiosos. Quero que você pegue os núcleos e os traga aqui para que eu possa reaproveitá-los. As patrulhas estão na parte arruinada de Luaprata, a noroeste.", progress = "Já conseguiu reunir os núcleos arcanos, <name>?", completion = "Muito bem, &lt;name&gt;. Nossos santuários arcanos não estão funcionando com capacidade total, e estes núcleos de energia serão mais do que suficientes para complementar nossas necessidades.", rewards = "Você receberá:\n\n"},
[8473] = {title = "Trabalho sujo", objective = "Mate 10 Jardineiros Murchos na Mata Queimada. Em seguida apresente-se a Larianna Fluminéola na torre a noroeste da Mata Queimada, que fica na Floresta do Canto Eterno.", description = "Estamos vivendo tempos difíceis, <name>. Foi uma decisão penosa queimar a mata que cerca a Terra Fantasma para impedir a expansão do Flagelo.\n\nAgora os arvorosos, nossos amigos há anos, estão tentando replantar a floresta na Mata Queimada.\n\nSei que é uma tarefa muito desagradável, <class>, mas não conseguimos convencer nossos antigos aliados a interromper a empreitada. Preciso que você use o único meio que nos resta: a força.", progress = nil, completion = "Então, está feito. Malditos tempos que nos levam a medidas desesperadas... e maldito seja nosso inimigo, o Flagelo!\n\nAprenda isto, &lt;name&gt;. É preciso proteger nossas terras, custe o que custar. Nós persistiremos!", rewards = "Você receberá:\n\n"},
[8474] = {title = "Pingente do Velho Cascabranca", objective = "Encontre a pessoa que talvez tenha confeccionado o Pingente do Velho Cascabranca. Ela pode ter informações sobre o item.", description = "O pingente tem gravadas as iniciais L.F. Talvez essas letras levem ao artesão que fez o pingente.", progress = "Você tem algo para me mostrar?", completion = "Este... este pingente. Eu o dei ao Velho Cascabranca quando o povo dele nos ajudou a reconstruir a vila.\n\nIsso quer dizer que ele está...\n\n&lt;A elfa sangrenta limpa a garganta e se recompõe.&gt;\n\nSou grata por você ter me trazido isso, &lt;name&gt;. Quero lhe fazer um pedido.", rewards = "Você receberá:\n\n"},
[8475] = {title = "A Trilha da Morte", objective = "Mate 8 Pilhadores Pragossos e apresente-se à Patrulheira Jaela na Trilha da Morte, que fica na Floresta do Canto Eterno.", description = "A faixa negra de solo que atravessa a Floresta do Canto Eterno é um eterno lembrete do dia em que Arthas liderou o Flagelo na invasão à nossa adorável cidade.\n\nEssa mancha não apenas agride aos olhos, mas atrai o Flagelo da Terra Fantasma até Luaprata.\n\nVá, <name>, e ajude meus homens a matar os insanos mortos-vivos que vagam pela Trilha da Morte.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8476] = {title = "Defendendo o terreno", objective = "A Tenente Correalba, do Retiro dos Andarilhos, que fica na Floresta do Canto Eterno, quer que você mate 5 Berserkers Amani e 5 Arremessadores de Machado Amani.", description = "Nossos reinos não são vastos como os dos humanos e dos orcs. Tudo que nos restou em Azeroth está diante de seus olhos, <name>.\n\nAté conseguirmos chegar a Terralém, precisamos defender o pouco de terra que nos resta a qualquer custo.\n\nNossos vizinhos bárbaros a sudeste, os trolls Amani, certamente têm uma opinião diferente em relação à posse destas terras. Seus ataques-surpresa diários são algo que não podemos mais tolerar. Vá aos acampamentos Amani, Zeb'Watha e Tor'Watha, e mostre a eles.", progress = nil, completion = "Bom trabalho, &lt;name&gt;. Com mais pessoas como você, nós logo recuperaremos a antiga glória das nossas terras.", rewards = "Você receberá:\n\n"},
[8477] = {title = "O martelo do lanceiro", objective = "Procure o Armeiro de Lanças Otembe em Tor'Watha, a sudeste do Retiro dos Andarilhos, e pegue o Martelo de Otembe. Em seguida, entregue o Martelo para Arathel Forjassol no Retiro dos Andarilhos, que fica na Floresta do Canto Eterno.", description = "Os Amani e os sin'dorei têm uma longa história de mútua aversão e franca hostilidade, mas isso não me impede de apreciar o trabalho de um colega artesão, <name>.\n\nEu me deparei com armamentos Amani e devo admitir que estou impressionada com a qualidade do trabalho deles. Dizem que o armeiro de lanças Otembe tem um martelo mágico tão leve e equilibrado que permite trabalhar com grande destreza e eficiência.\n\nSe você me trouxer essa ferramenta, eu a usarei para fabricar uma arma para você.", progress = "Você trouxe o martelo de Otembe, <name>?", completion = "Os rumores são verdadeiros, &lt;name&gt;. Este martelo é, ao mesmo tempo, leve e resistente. Com ele serei capaz de concluir vários dos meus projetos inacabados e, claro, você será o primeiro a escolher um deles.", rewards = "Você receberá:\n\n"},
[8478] = {title = "Choose Your Weapon", objective = "Você receberá:\n\n", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8479] = {title = "Zul'Marosh", objective = "Ven'jashi, o prisioneiro troll em Tor'Watha, quer que você entregue a Cabeça do Chefe Zul'Marosh para ele. O Chefe Zul'Marosh está em Zeb'Watha, do outro lado do Lago Elrendar.", description = "Chega mais, <irmãozinho/irmãzinha>, não fica <cabreiro/cabreira>. Ven'jashi não vai te machucar.\n\nNós, Lançanegra, somos inimigos mortais dos Amani, como tu. O chefe deles, um tal de Zul'Marosh, é um troll cavernoso. Ele me deu um veneno brabo e me deixou nesta jaula pra morrer.\n\nEu não arrego, já vi a cara da morte de pertinho e saí na boa com um sorriso no rosto. O que tá pegando é que eu não posso partir dessa pra melhor ainda, cara, só depois que o Zul'Marosh virar presunto.\n\nEle tá do outro lado do lago, em Zeb'Watha. Vou segurar a onda do veneno até tu me trazer a cabeçorra espanta-neném dele. Aí posso cantar pra subir.", progress = "Tu tá com a cabeça do Zul'Marosh aí?", completion = "Valeu, cara. Zul'Marosh mereceu. Ele tacou fogo na vila do Ven'jashi. Eu matei um monte de Amani antes de eles me botarem na jaula. Eu maloquei uma parada na areia. É uma coisinha pra você.\n\nAh... ah, o veneno... se espalhou, cara. Hora de partir...", rewards = "Você receberá:\n\n"},
[8480] = {title = "Armamentos perdidos", objective = "Entregue 8 Armamentos Sin'dorei para Velendris Matinalba na Floresta do Canto Eterno.", description = "Os Ignóbeis estão cada dia mais audaciosos, <name>. Eles estão tão coordenados que ousaram lançar um ataque ao Ancoradouro Velaclara.\n\nComo se isso já não fosse suficientemente embaraçoso, a maioria das nossas armas ficou para trás quando eles atacaram. Traga os armamentos de volta para que possamos mostrar àqueles rufiões quem manda aqui.", progress = "Já recolheu as armas perdidas?", completion = "Excelente trabalho, &lt;name&gt;. Vamos mostrar aos Ignóbeis do que somos capazes!", rewards = "Você receberá:\n\n"},
[8481] = {title = "A raiz de todo o mal", objective = "Bote a Tocha de Evocação Demoníaca na entrada da caverna do Chefe Supremo Invernoso, na aldeia dos pelursos Invernosos. Em seguida, derrote o demônio, recolha a Essência de Shandivious e entregue-a para Gorn Caolho, no Domínio dos Presamatos.", description = "<name>, graças a você, elaboramos um meio de evocar a raiz de todo o mal que aflige a tribo dos Invernosos – o demônio corruptor que os domina!\n\nLeve esta tocha para a entrada da caverna do Chefe Supremo Invernoso e deixe-a lá. Isso fará com que o demônio se manifeste para investigar o que está acontecendo. Nessa hora, você e seus aliados devem atacar! Apenas a essência do demônio derrotado poderá começar o longo processo de cura da tribo dos Invernosos... e de salvação do Domínio dos Presamatos.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nDefensor dos Presamatos"},
[8482] = {title = "Documentos incriminadores", objective = "Leve os Documentos Incriminadores para Aledon Marcassol na Praça Asa do Falcão, que fica na Floresta do Canto Eterno.", description = "Os documentos contêm mapas minuciosos de diversos prédios estratégicos na Floresta do Canto Eterno. Também há uma planta bastante detalhada do santuário arcano. Parece haver várias anotações em um idioma que você não consegue reconhecer.\n\nComo os documentos estavam nas mãos de um espião Darnassiano, talvez seja uma boa ideia mostrá-los para o capitão da guarda, Aledon Marcassol.", progress = "Você trouxe algo para mim? Deixe-me dar uma olhada.", completion = "Tem certeza que encontrou isso com um elfo noturno? Isso está claramente escrito em enânico...\n\nPelo que entendi, quem escreveu isto não causou o defeito, mas foi enviado como espião para observar os resultados. Eles chamam nosso empreendimento de temerário e perigoso... Mas quem?\n\nÉ claro! Aquele enviado de Altaforja. Fomos tolos em confiar em alguém da Aliança!", rewards = "Você receberá:\n\n"},
[8483] = {title = "O espião enânico", objective = "Entregue a Cabeça do Minerador Bigorneiro para Aledon Marcassol na Praça Asa do Falcão, que fica na Floresta do Canto Eterno.", description = "Ouça, <name>. Esta é uma situação bastante delicada. Mesmo identificando o espião, não podemos matá-lo a céu aberto. Também não podemos tentar prendê-lo, pois o risco de ele escapar é grande demais.\n\nQuero que você encontre o minerador Bigorneiro no Sacrário do Norte e mate-o. Seja <discreto/discreta>, não queremos que saibam que deixamos um espião entrar na nossa cidade.\n\nQuero a cabeça dele como prova. Altaforja vai aprender a não mexer com os sin'dorei.", progress = "Você cuidou do intruso?", completion = "Então está feito. Excelente trabalho.\n\nO general dos patrulheiros foi avisado e, acredite, os anões pagarão caro pela traição.\n\nVocê prestou um grande serviço para o nosso povo hoje, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[8484] = {title = "Promovendo a paz", objective = "Leve a Oferenda de Paz dos Presamatos ao Rei Magni Barbabronze em Altaforja.", description = "Seus feitos vão além das histórias de <um simples herói/uma simples heroína>, <name>. Se seu povo é assim também, então teríamos interesse em fazer as pazes com vocês.\n\nPor favor, leve esta oferenda ao rei anão que mora em uma montanha de ferro. Creio que vocês chamam a cidade de Altaforja. No passado, ele tentou entrar em contato conosco em nome da Aliança. Agora é apenas justo reconhecer você como a chave nos esforços da Aliança para promover a paz.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nReputação com a Aliança\n\nVocê receberá:\n\n"},
[8485] = {title = "Promovendo a paz", objective = "Leve a Oferenda de Paz dos Presamatos para Thrall em Orgrimmar.", description = "Seus feitos vão além das histórias de <um simples herói/uma simples heroína>, <name>. Se seu povo é assim também, então teríamos interesse em fazer as pazes com vocês.\n\nPor favor, leve esta oferenda ao Chefe Guerreiro órquico que mora nas terras de Durotar. Na cidade conhecida como Orgrimmar, ao que parece. No passado, ele tentou entrar em contato conosco em nome da Horda. Agora é apenas justo reconhecer você como a chave nos esforços da Horda para promover a paz.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nReputação com a Horda\n\nVocê receberá:\n\n"},
[8486] = {title = "Instabilidade arcana", objective = "A Protetora de Meridianos Velania quer que você mate 5 Espectros de Mana e 5 Espreitadores de Mana.", description = "Até que enfim aquele lerdo do Caidanis mandou alguém aqui.\n\nEu avisei sobre o perigo de aumentar a carga do Sacrário do Oeste! Agora um de nossos conversores de energia está destruído e espectros arcanos estão pululando do sacrário!\n\nPreciso que você cuide dessas criaturas para que possamos mandar alguém para os reparos. Apresse-se!", progress = nil, completion = "Obrigada, &lt;name&gt;. Agora que os espectros estão sob controle, podemos mandar alguém lá para avaliar os danos. Tomara que não seja nada permanente.", rewards = "Você receberá:\n\n"},
[8487] = {title = "Solo conspurcado", objective = "A Aprendiz Mirveda, do Sacrário do Leste, que fica na Floresta do Canto Eterno, quer que você colete 8 Amostras de Solo Infecto.", description = "Este era um sacrário arcano totalmente funcional antes de Arthas e seu exército de Flagelos resolverem arrasá-lo a caminho de Luaprata.\n\nAgora nos tem tanta serventia quanto o resto da Trilha da Morte. O solo está contaminado, mortos-vivos estão sendo atraídos por ele e a energia nas linhas de meridiano foi interrompida por completo.\n\nO que quero saber, <name>, é se a conspurcação pode ser revertida. Quero que você recolha amostras do solo dessa área para que eu possa estudá-las. Talvez, com o tempo, consigamos restabelecer a ordem.", progress = "Já conseguiu recolher as amostras que pedi, <name>?", completion = "Excelente. Vou lançar um encantamento de potencialização nas amostras para que eu possa analisar com mais clareza o solo conspurcado.", rewards = "Você receberá:\n\n"},
[8488] = {title = "Resultados inesperados", objective = "Fale com a Aprendiz Mirveda e depois proteja-a da emboscada do Flagelo.", description = "Algo está errado, <name>. Eu sinto a presença do Flagelo. Prepare-se para enfrentá-lo!", progress = nil, completion = "Meus esforços foram em vão, &lt;name&gt;. A natureza do solo foi alterada... A conspurcação é irreversível. Obrigada pela ajuda. Preciso pedir um último favor a você.", rewards = "Você receberá:\n\n"},
[8489] = {title = "An Intact Converter", objective = "Bring the Intact Arcane Converter to Ley-Keeper Velania.This object appears to be one of the arcane converters Ley-Keeper Velania mentioned. It hasn't been damaged by the fire, perhaps she could find a use for it.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", description = "This object appears to be one of the arcane converters Ley-Keeper Velania mentioned. It hasn't been damaged by the fire, perhaps she could find a use for it.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8490] = {title = "Fortalecendo as defesas!", objective = "Insira o Cristal Infuso na Pedra Rúnica do Leste e proteja-a do Flagelo por 1 minuto. Entregue o Cristal Infuso para o Guarda-runas Deryan na Floresta do Canto Eterno para receber a recompensa.", description = "Pedras rúnicas ancestrais já alimentaram uma barreira protetora em toda a fronteira de Canto Eterno. Muitas foram destruídas durante o ataque de Arthas, enquanto outras foram furtadas.\n\nDas três que restam, somente esta manteve o poder, mas a pedra rúnica a leste talvez ainda possa ser aproveitada.\n\nLeve este cristal e injete a energia dele na Pedra Rúnica do Leste. O processo só deve levar um minuto, mas cuidado: o Flagelo nas redondezas será atraído por ela e tentará destruí-la. Não permita!", progress = "Já conseguiu energizar a pedra rúnica, <name>?", completion = "Excelente. As defesas devem manter o Flagelo afastado por um pouco mais de tempo. Obrigado pela ajuda, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[8491] = {title = "Caça às peles", objective = "Velan Roburclaro, da Vila de Brisabela, que fica na Floresta do Canto Eterno, quer que você leve 6 Pelagens de Garrataque para ele.", description = "Os garrataques desta região são conhecidos pela pelagem de alta qualidade. Até a escória noctiélfica vem a estas terras só para roubá-las.\n\nAs escaramuças com os Amani aumentaram a demanda por equipamento e os patrulheiros não têm mais muito tempo de caçar para conseguir peles. Se estiver <disposto/disposta> a ajudar a coletar peles de garrataque, ficarei feliz em <recompensá-lo/recompensá-la> com uma armadura feita a mão. Tem garrataques nas florestas a norte daqui.", progress = "Você voltou. Trouxe as pelagens?", completion = "São perfeitas, &lt;name&gt;. Aceite esta armadura como recompensa. Com as pelagens que você me trouxe, posso fabricar mais.", rewards = "Você receberá:\n\n"},
[8492] = {title = "A Aliança precisa de barras de cobre!", objective = "Leve 20 Barras de Cobre ao Sargento Pedracenho na Ala Militar de Altaforja.", description = "Buenas, <class>, você parece estar em condições de contribuir com o esforço de guerra! É seu dia de sorte. Precisamos de toda ajuda que conseguirmos para reunir o equipamento necessário à guerra em Ahn'Qiraj.\n\nEstou juntando barras de cobre em pilhas de vinte. É por uma boa causa. Pode ser? Você acha que pode ajudar a Aliança?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8493] = {title = "A Aliança precisa de mais barras de cobre!", objective = "Leve 20 Barras de Cobre ao Sargento Pedracenho na base aérea de Dun Morogh.", description = "Já de volta, <class>? Magnífico! Ainda precisamos de mais pilhas de barras de cobre, se você puder, para o esforço de guerra em Ahn'Qiraj. Podemos contar com você?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8494] = {title = "A Aliança precisa de barras de ferro!", objective = "Leve 20 Barras de Ferro ao Cabo Carens na Ala Militar de Altaforja.", description = "Bons dias, <Sr./Sra.> Calça Ridícula, você é mesmo um colírio para os olhos. Caso não tenha ficado sabendo em uma de suas \"aventuras\", a Aliança acelerou os preparativos de guerra em Ahn'Qiraj e carece de barras de ferro... um monte delas. Em pilhas de vinte. Podemos contar com você, certo, <class>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8495] = {title = "A Aliança precisa de mais barras de ferro!", objective = "Leve 20 Barras de Ferro ao Cabo Carens na base aérea de Dun Morogh.", description = "Já de volta? Pare de me fazer rever minha opinião negativa a seu respeito <class>. Você está me fazendo ficar sentimental. Bem, como você já deve ter notado, ainda não temos barras de ferro suficientes para todas as armas, armaduras, tanques a vapor e tudo mais que precisa ser construído para a caça às bruxas. Você se acha capaz de me trazer outra pilha de vinte barras de ferro entre uma viagem e outra a Zul'Gurub, ou seja lá qual for o lugar que vocês jovens frequentam hoje em dia?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8496] = {title = "Bandagens para o campo", objective = "Leve 30 Bandagens Grossas de Runatrama, 30 Bandagens Grossas de Seda e 30 Bandagens Grossas de Magitrama para o Arauto do Vento Chifre Altivo, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística X para concluir esta missão.", description = "Nosso suprimento de bandagens para o campo de batalha está arriscadamente escasso depois das últimas baixas que sofremos. Consiga um lote de suprimentos de primeiros socorros e entregue-os ao Arauto do Vento Chifre Altivo no Forte Cenariano.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística Continuada"},
[8497] = {title = "Kits de sobrevivência no deserto", objective = "Leve 4 Globos d'Água, 4 Antipeçonhas Poderosos e 4 Bolinhos Defumados do Deserto para Calandrath na estalagem do Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística I para completar esta tarefa.", description = "Com a chegada dos novos recrutas ao Forte Cenariano, vem o problema de como mantê-los vivos no deserto. A desidratação e as diversas criaturas venenosas representam uma séria ameaça a voluntários inexperientes. Reúna os suprimentos necessários para um kit de sobrevivência no deserto e leve-os para Calandrath no Forte Cenariano.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística"},
[8498] = {title = "Ordens de Batalha do Martelo do Crepúsculo", objective = "Obtenha as Ordens de Batalha do Martelo do Crepúsculo e leve-as ao Comandante Mar'alith no Forte Cenariano, que fica em Silithus. Você também precisa entregar as Instruções Táticas X para concluir a missão.", description = "Nossos batedores relataram que novas ordens de batalha foram emitidas pelos líderes do Martelo do Crepúsculo. As ordens estão sendo distribuídas pelos Profetas do Crepúsculo sob forte escolta. Intercepte as ordens e leve-as ao Comandante Mar'alith.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Tática CenarianaMissão Tática"},
[8499] = {title = "A Aliança precisa de barras de tório!", objective = "Leve 20 Barras de Tório à Dama Duas-tranças na Ala Militar de Altaforja.", description = "É muita gentileza sua se preocupar com as necessidades da Aliança. Além de todos os outros esforços de coleta em andamento, precisamos de um grande volume de barras de tório. Foi essa a tarefa que me atribuíram. O tório será usado para a criação de diversos aparatos de guerra, O principal é a construção de um batalhão de tanques a vapor que será transportado para Silithus por via aérea quando estiver concluído. Você nos ajuda nessa empreitada, <class>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8500] = {title = "A Aliança precisa de mais barras de tório!", objective = "Leve 20 Barras de Tório à Dama Duas-tranças na base aérea de Dun Morogh.", description = "É tão bom <vê-lo/vê-la> de novo <name>. Espero que você esteja bem. É verdade que ainda precisamos de barras de tório. Se tiver barras para nos ceder, estou juntando para o esforço de guerra em Ahn'Qiraj.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8501] = {title = "Alvo: aguilhantes da Colme'Ashi", objective = "Mate 30 Aguilhantes da Colme'Ashi e fale novamente com o Comandante Mar'alith no Forte Cenariano, que fica em Silithus. Você também precisa entregar as Instruções de Combate XII para concluir a missão.", description = "Com o aumento do esforço de guerra, a presença dos silitídeos na área oferece uma ameaça crescente às nossas operações. Você foi <selecionado/selecionada> para participar de um ataque à Colme'Ashi e os seus alvos principais serão os aguilhantes aéreos da Colme'Ashi.\n\nFale novamente com o comandante Mar'alith quando tiver concluído a sua tarefa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia de Combate Cenariana"},
[8502] = {title = "Alvo: trabalhadores da Colme'Ashi", objective = "Mate 30 Trabalhadores da Colme'Ashi e fale novamente com o Comandante Mar'alith no Forte Cenariano, que fica em Silithus. Você também precisa entregar as Instruções de Combate III para concluir a missão.", description = "Com o aumento do esforço de guerra, a presença dos silitídeos na área oferece uma ameaça crescente às nossas operações. Você foi <selecionado/selecionada> para participar de um ataque à Colme'Ashi e os seus alvos principais serão os trabalhadores da Colme'Ashi.\n\nFale novamente com o comandante Mar'alith quando tiver concluído a sua tarefa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia de Combate Cenariana"},
[8503] = {title = "A Aliança precisa de estrangulalgas!", objective = "Leve 20 Estrangulalgas à Recruta Medeixo na Ala Militar de Altaforja.", description = "Bom, <class>, já lhe falei de todas as fabulosas utilidades da estrangulalga? Falei? Ah bem... seja como for, precisamos de um monte delas, e quando digo um monte, digo UM MONTÃO! Você acha que pode nos ajudar? Sei que isso significa abrir mão de todas as coisas extraordinárias que você pode fazer com ela, mas é por uma boa causa!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8504] = {title = "A Aliança precisa de mais estrangulalga!", objective = "Leve 20 Estrangulalgas à Recruta Medeixo na base aérea de Dun Morogh.", description = "Olá de novo, amiguinh<o/a>... qual foi? Ah sim, <name>! Veio ver se ainda preciso de mais estrangulalga, né? Bem, é seu dia de sorte, pois a resposta a essa pergunta tão importante é um sonoro SIM! Você acha que consegue juntar vinte?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8505] = {title = "A Aliança precisa de lótus roxo!", objective = "Leve 20 Lótus Roxos ao Mestre Noturcanto na Ala Militar de Altaforja.", description = "<class>, o lótus roxo é uma flor com uma plenitude de usos. Estou aqui a pedido da Aliança como autoridade em tais usos para preservar as ervas colhidas e garantir que o esforço de guerra receba lótus roxos suficientes para suprir qualquer propósito.\n\nFaça-me o obséquio de sair pelo mundo e me trazer algumas dessas flores. Você acredita ser capaz de tal feito?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8506] = {title = "A Aliança precisa de mais lótus roxo!", objective = "Leve 20 Lótus Roxos ao Mestre Noturcanto na base aérea de Dun Morogh.", description = "Folgo em <revê-lo/revê-la>, <class>. Nossa coleção aumentou, mas, por incrível que pareça, carecemos de mais lótus roxo. Se ainda estiver ao seu alcance colher mais dessa erva, rogo que o faça e volte a ter comigo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8507] = {title = "Serviço em campanha", objective = "Apresente-se para o trabalho no posto da Brigada de Altaforja perto de Colme'Zora. Prepare seus Papéis em Branco de Serviço em Campanha e obtenha Papéis Assinados de Serviço em Campanha com o Capitão Bigornero. Então fale novamente com o Arauto do Vento Kaldon no Forte Cenariano em Silithus.\n\nNota: Curar ou lançar feitiços benéficos em um membro da Brigada de Altaforja marcará você para o JxJ.", description = "Saudações, <name> Antes de lhe atribuir tarefas mais intrincadas na guerra, é preciso que você faça sua parte no serviço em campanha. \n\nA Brigada de Altaforja do Capitão Bigornero está estacionada perto de Colme'Zora.\n\nApresente-se a ele ou a Jenila Fortemalho, tenente dele, e fale novamente comigo. Deverei ter uma tarefa mais complexa para você então.\n\nAh, por favor, faça-nos um favor e fique longe da Legião de Orgrimmar. Fomos obrigados a manter as duas forças separadas uma da outra devido à animosidade mútua.", progress = "Você encontrará a força do Capitão Bigornero posicionada perto de Colme'Zora. Fale com a tenente dele, Jenila Fortemalho, se não conseguir encontrá-lo.", completion = "Muito bem, &lt;name&gt;. Preparei uma série de tarefas que devem aproveitar bem suas habilidades.\n\nCom seus esforços contínuos e os de pessoas como você, nosso inimigo logo será derrotado de uma vez por todas!\n\nSe você quiser instruções para uma nova missão, fale novamente com o Capitão Bigornero.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMissão de CombateMissão de LogísticaMissão Tática"},
[8508] = {title = "Papéis de serviço em campanha", objective = "Presumo que você queira que os papéis sejam assinados, piá?", description = nil, progress = "Presumo que você queira que os papéis sejam assinados, piá?", completion = "Sim, suponho que aquela pequena escaramuça passará como serviço em campanha hoje em dia. Leve isso de volta ao Forte Cenariano. Tenho certeza de que eles terão um bom trabalho para você.\n\nVá!", rewards = "Você receberá:\n\nPapéis Assinados de Serviço em Campanha"},
[8509] = {title = "A Aliança precisa de lágrimas-de-arthas!", objective = "Leve 20 Lágrimas-de-arthas à Sargento-mor Germana na Ala Militar de Altaforja.", description = "Vou direto ao ponto e sem complicar, <class>. A Aliança precisa de uma grande quantidade de Lágrimas-de-arthas para suprir o esforço de guerra em Ahn'Qiraj. Não pergunte o motivo. Apenas sigo ordens, mas quero que você reúna a maior quantidade que conseguir da erva e me traga. Está <disposto/disposta>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8510] = {title = "A Aliança precisa de mais Lágrimas-de-arthas!", objective = "Leve 20 Lágrimas-de-arthas à Sargento-mor Germana na base aérea de Dun Morogh.", description = "Ok, de fato eu ainda preciso de mais Lágrimas-de-arthas. Creio que seja possível encontrá-las na mesma área em que você as recolheu da última vez. Quanto antes as conseguirmos, mais rápido podemos dar início à ofensiva contra as forças em Ahn'Qiraj.\n\nVocê vai nos ajudar novamente <class>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8511] = {title = "A Aliança precisa de couro leve!", objective = "Leve 10 peças de Couro Leve para Corina Esfolapedra na Ala Militar de Altaforja.", description = "Nós Esfolapedra temos uma longa tradição como esfoladores, <class>. Por isso a Aliança pediu à minha família que ajudasse a coletar couro leve para o esforço de guerra em Ahn'Qiraj. Eu estou aqui para garantir que o couro seja curtido corretamente para qualquer uso que eles venham a fazer dele. Mas precisamos da sua ajuda.\n\nComo eu tenho de estar aqui ocupada com isso, preciso que você saia, reúna o couro e o traga a mim. Que tal, criança, quer tentar?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8512] = {title = "A Aliança precisa de mais couro leve!", objective = "Leve 10 peças de Couro Leve para Corina Esfolapedra na base aérea de Dun Morogh.", description = "Bom <vê-lo/vê-la> novamente <name>. Pelo visto, precisamos de mais couro leve para a criação de diversos equipamentos de guerra. Se você pudesse me trazer uma pilha de dez, seria perfeito.", progress = "Bom vê-la novamente <name>. Pelo visto, precisamos de mais couro leve para a criação de diversos equipamentos de guerra. Se você pudesse me trazer uma pilha de dez, seria perfeito.", completion = "Obrigada pela generosa doação, &lt;name&gt;. Vou colocar na pilha agora mesmo, e logo tudo isso será transformado em uma bela armadura, em alguns remendos de couro, ou terá algum outro uso em nossos preparativos. Ou seja, estaremos sempre precisando de mais couro. Volte aqui o quanto antes.", rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8513] = {title = "A Aliança precisa de couro médio!", objective = "Leve 10 peças de Couro Médio ao Cabo Porter na Ala Militar de Altaforja.", description = "Sim <senhor/senhora>, eu fui encarregado de reunir e supervisionar todo o couro médio obtido em prol do esforço de guerra. Se dispuser de meios de obter couro médio, seja de que origem for, traga-o a mim. Sua ajuda será bem-vinda se você puder doar um pouco do seu tempo à causa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8514] = {title = "A Aliança precisa de mais couro médio!", objective = "Leve 10 peças de Couro Médio ao Cabo Porter na base aérea de Dun Morogh.", description = "Sim <senhor/senhora>, vejo que juntamos $2063w peças de couro médio até o momento. Sei que é pedir muito, mas se ainda estiver <disposto/disposta>, sua ajuda seria bem-vinda no intuito de amortizar o que falta da minha quota.", progress = "Sim senhora, vejo que conseguimos juntar muitas peças de couro médio, mas precisamos de mais. Garanto que faremos bom uso dele. Sei que é pedir muito, mas se ainda estiver disposta, sua ajuda seria bem-vinda na tentativa de amortizar o que falta da minha quota.", completion = "Céus, quanto couro médio você me trouxe, madame! Não tenha dúvida de que darei um destino adequado a tudo isso. Com sua ajuda, logo atingiremos a meta prevista de couro médio!\n\nObrigado, &lt;name&gt;!", rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8515] = {title = "A Aliança precisa de couro grosso!", objective = "Leve 10 peças de Couro Grosso à Marta Fusofino na Ala Militar de Altaforja.", description = "Puxa vida, vai ser demais! <class>, o esforço de guerra em Ahn'Qiraj está a todo vapor e me encarregaram de acompanhar a chegada de todo o couro grosso. Faremos mil coisas com ele, mas primeiro precisamos conseguir uma tonelada. Literalmente! É aí que você entra.\n\nE aí? Você acha que consegue sair e me trazer uma bela e gorda pilha de couro grosso? Hein, hein?!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8516] = {title = "A Aliança precisa de mais couro grosso!", objective = "Leve 10 peças de Couro Grosso à Marta Fusofino na base aérea de Dun Morogh.", description = "É, a pilha ainda não está do tamanho que queremos. <name>, ainda não atingimos a meta de $2071w peças de couro grosso. Pensa só em tudo que a gente pode fazer com esse couro! Todo tipo de armaduras e armas. Estofamento para o interior dos tanques a vapor e miras telescópicas pros rifles! Óculos de engenheiro e aposto que, se a gente acabar juntando mais do que precisa, podemos dar o resto à Horda pros zepelins dela!\n\nVai me trazer mais couro grosso?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8517] = {title = "A Aliança precisa de bandagens de linho!", objective = "Leve 20 Bandagens de Linho à Sentinela Argencélio na Ala Militar de Altaforja.", description = "A guerra em Ahn'Qiraj será longa e sangrenta. É muito sábia a atitude da Aliança em convocar sua ajuda, e a do seu povo, para construir o equipamento necessário antes de mergulhar de cabeça no conflito.\n\nMe pediram para liderar a coleta de bandagens de linho para a guerra, e conto com sua ajuda para reunir a quantidade necessária. Sei que a tarefa é difícil, mas pelo menos as bandagens de linho são as mais fáceis de confeccionar. Podemos contar com você, <class>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8518] = {title = "A Aliança precisa de mais bandagens de linho!", objective = "Leve 20 Bandagens de Linho à Sentinela Argencélio na base aérea de Dun Morogh.", description = "Não canso de me surpreender com o altruísmo demonstrado em situações que levariam tantos ao egoísmo. <class>, ainda preciso de algumas bandagens de linho para cumprir minha tarefa aqui. Posso contar com sua ajuda mais uma vez?", progress = "Não canso de me surpreender com o altruísmo demonstrado em situações que levariam tantos ao egoísmo. <class>, ainda preciso de algumas bandagens de linho para cumprir minha tarefa aqui. Posso contar com sua ajuda mais uma vez?", completion = "Você receberá uma menção, &lt;name&gt;. Enquanto há quem busque tirar proveito da nossa economia em pé de guerra para encher os bolsos no afã da ganância, você provou mais de uma vez que sua devoção à Aliança e, de fato, a toda a Azeroth está acima de tudo.\n\nQue a Bênção de Eluna esteja sempre consigo!", rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8519] = {title = "Um peão no tabuleiro eterno", objective = "Aprenda tudo que puder sobre o passado, depois fale com Anacronos nas Cavernas do Tempo, em Tanaris.", description = "Quando você recupera a compostura e foca o olhar, percebe que algo se agita dentro do cristal. Após observar com mais atenção, você percebe que está vendo o passado... uma lembrança... há muito esquecida.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8520] = {title = "A Aliança precisa de bandagens de seda!", objective = "Leve 20 Bandagens de Seda à Enfermeira Campedra na Ala Militar de Altaforja.", description = "Com a guerra despontando em Ahn'Qiraj, não podemos medir esforços para garantir que nossos soldados tenham tudo que precisam. É exatamente isso que estamos fazendo aqui. De minha parte, estou encarregada de garantir que juntemos bandagens de seda suficientes.\n\nVocê acha que pode ajudar, <class>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8521] = {title = "A Aliança precisa de mais bandagens de seda!", objective = "Leve 20 Bandagens de Seda à Enfermeira Campedra na base aérea de Dun Morogh.", description = "É muita bondade sua oferecer ajuda mais uma vez <class>. Vejo que acumulamos $2082w bandagens de seda, por isso, vamos precisar de mais.", progress = "É muita bondade sua oferecer ajuda mais uma vez <class>. Vejo que acumulamos um número considerável das bandagens de seda de que nos encarregaram, mas ainda não foi suficiente. <name>, há como você se dispor a reunir mais um fardo e trazê-lo aqui para mim? Eu e a Aliança seremos imensamente gratos.", completion = "Que pessoa abnegada você é. Muito obrigada pela sua contribuição com o esforço de guerra. São pessoas como você que farão a diferença entre o sucesso e o fracasso em Ahn'Qiraj. Só espero que isso tudo seja suficiente.\n\nObrigada mais uma vez, &lt;name&gt;.", rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8522] = {title = "A Aliança precisa de bandagens de runatrama!", objective = "Leve 20 Bandagens de Runatrama ao Guardião Sombraluna na Ala Militar de Altaforja.", description = "Você pode até querer discutir o mérito da guerra, <class>, mas ela já é fato. Olhe em volta para sua família e amigos, e pergunte a si <mesmo/mesma> o que você pode fazer para garantir que tenham o máximo de chance de sobreviver à conflagração, caso venham a se envolver.\n\nNesse intuito, estou acumulando bandagens de runatrama de todas as fontes disponíveis. Você gostaria de fazer uma doação?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8523] = {title = "A Aliança precisa de mais bandagens de runatrama!", objective = "Leve 20 Bandagens de Runatrama ao Guardião Sombraluna na base aérea de Dun Morogh.", description = "O acaso quis que voltássemos a discutir questões prementes <class>. Mais uma vez obrigado pelos esforços já dispensados; poucos estariam dispostos a doar de forma tão abnega. Contudo, ainda há muito por fazer.\n\n<name>, haveria interesse de sua parte em me trazer mais bandagens?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8524] = {title = "A Aliança precisa de albacora arco-íris!", objective = "Leve 20 Albacoras Arco-íris ao Fúlvio Nelidônio na Ala Militar de Altaforja.", description = "Olá olá, amiguinho! Se eu dissesse, ‘Você não acha que os soldados deviam ter o que comer como todo o mundo?', é claro que você responderia que sim. Eu concordo 110% com você! Que bom que estamos de acordo. Tudo que preciso é que você saia e pesque um montão de albacora arco-íris crua. Cozinhe tudo e traga de volta para mim. Humm, hummm, já estou com água na boca.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8525] = {title = "A Aliança precisa de mais albacora arco-íris!", objective = "Leve 20 Albacoras Arco-íris para Fúlvio Nelidônio na base aérea de Dun Morogh.", description = "O quê!? Você de novo!? Macacos me mordam... apesar de eu ser um gnomo e não uma banana. Fúlvio Nelidônio é meu nome! Voltou para me ajudar de novo, não é? Ora, eu não <o/a> culpo. Você não adora o aroma de toda esta comida? <baba>\n\nChega de enrolação! Vai lá e me traz mais albacora arco-íris!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8526] = {title = "A Aliança precisa de raptor assado!", objective = "Leve 20 Raptores Assados para Sara Apitaço na Ala Militar de Altaforja.", description = "Opa, tudo em cima, <class>? Sinto que você está <interessado/interessada> em, quem sabe, conseguir comida para a guerra iminente em Ahn'Qiraj? De algum modo, meu pai achou que seria uma boa experiência de aprendizagem se eu me oferecesse como voluntária aqui. Ledo engano. Eu nem gosto de carne.\n\nSeja como for, se quiser, me traga todo raptor assado que puder. Esse é obviamente o prato preferido dos soldados.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8527] = {title = "A Aliança precisa de mais raptor assado!", objective = "Leve 20 Raptores Assados para Sara Apitaço na base aérea de Dun Morogh.", description = "Olá de novo <class>. Sua empolgação com tudo isso é evidente. Só posso deduzir que você quer me trazer mais vinte peças de raptor assado. Maravilha. Bem, faça isso. Nos vemos na volta.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8528] = {title = "A Aliança precisa de rabo-amarelo mosqueado!", objective = "Leve 20 Rabos-amarelos Mosqueados à Caçadora Veloflumen na Ala Militar de Altaforja.", description = "Pode não parecer heroico juntar peixe cozido, mas não subestime o valor do preparo, jovem <class>. Soldados precisam comer como todo o mundo, e estamos aqui para fazer a nossa parte e garantir que tenham o que precisam quando a guerra em Ahn'Qiraj começar.\n\nAssim sendo, estou estocando rabo-amarelo mosqueado, uma versátil fonte de sustança fácil de armazenar e que pode ser usada em diversos pratos. Além de não ter o odor de alguns outros peixes marinhos. Haveria interesse de sua parte em ajudar?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8529] = {title = "A Aliança precisa de mais rabo-amarelo mosqueado!", objective = "Leve 20 Rabos-amarelos Mosqueados à Caçadora Veloflumen na base aérea de Dun Morogh.", description = "Se ainda for de seu interesse, eu gostaria que você me trouxesse mais um lanço de rabo-amarelo mosqueado, <name>. Suponho que a experiência adquirida na primeira empreitada proporcione uma pescaria mais fácil e rápida dessa vez. <Pronto/Pronta> para a empreitada?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da AliançaSuprimentos de Guerra em Ahn'Qiraj"},
[8530] = {title = "A Aliança precisa de pedra nuclear chamuscada!", objective = "Leve 50 pedras nucleares chamuscadas para o Comandante Marroforte na base aérea de Dun Morogh.", description = "<class>, preciso de sua ajuda. No Núcleo Derretido há pedras, especificamente as nucleares chamuscadas, de que eu preciso para o esforço de guerra de Ahn'Qiraj. Disseram que os nossos ferreiros e engenheiros podem fazer bom uso delas, e é aí que você entra. Preciso que você vá ao Núcleo Derretido e obtenha o máximo de pedras nucleares possível. Mas é claro que seria bom você levar uns... trinta e nove amigos com você, para garantir.", progress = nil, completion = nil, rewards = nil},
[8531] = {title = "The Alliance Needs More Singed Corestones!", objective = "Bring 50 singed corestones to Commander Stronghammer at the airfield in Dun Morogh.", description = "Thank you for returning <name>. If you and your friends still have it in you, I need you to return to the Molten Core and continue your singed corestone collection efforts. What do you say?", progress = nil, completion = nil, rewards = nil},
[8532] = {title = "A Horda precisa de barras de cobre!", objective = "Leve 20 Barras de Cobre ao Minerador Caturro no Vale dos Espíritos em Orgrimmar.", description = "Que bom você ter decidido me ajudar aqui. Nem todos fariam o mesmo, por isso preciso ter certeza de que você compreende minha necessidade. Barras, não minério de cobre. Não dispomos de tempo nem pessoal para cuidar da fundição. Portanto, se você puder se encarregar disso, seu esforço será devidamente considerado. Você acredita ser capaz, <class>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8533] = {title = "A Horda precisa de mais barras de cobre!", objective = "Leve 20 Barras de Cobre ao Minerador Caturro em Durotar.", description = "Já de volta? Logo você será <um minerador/uma mineradora> de verdade, <class>. Portanto, já sabe o que fazer; Preciso que você saia, garimpe um bocado de minério de cobre, transforme-o em barras e me traga. Sei que você é capaz, a pergunta é, haveria interesse de sua parte?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8534] = {title = "O relatório do batedor de Colme'Zora", objective = "Contate a Batedora Cenariana Azenel no interior da Colme'Zora e entregue o Relatório do Batedor de Colme'Zora à Arauto do Vento Chifre Altivo no Forte Cenariano. Você também precisa entregar as Instruções Táticas VI para concluir a missão.", description = "Mandamos nossos melhores batedores às colmeias dos silitídeos para espionar. A batedora Azenel está observando a movimentação na Colme'Zora há dias. Encontre-a e obtenha o relatório dela. Precisamos agir o mais rápido possível com base nessas informações, <name>.", progress = "A batedora Azenel já está dentro da Colme'Zora. Rápido, <name>! O tempo urge.", completion = "Seus esforços são muito bem-vindos, &lt;name&gt;. Vou ler os relatórios dos batedores imediatamente. Esteja à vontade para ir cumprir sua próxima missão.", rewards = "Você receberá:\n\nInsígnia Tática CenarianaMissão Tática Continuada"},
[8535] = {title = "Templário Gris", objective = "Evoque e mate um Templário Gris e fale novamente com Bor Juba Agreste no Forte Cenariano. Você também precisa entregar as Instruções Táticas IV para concluir a missão.", description = "O Martelo do Crepúsculo e os nobres elementais que eles veneram são uma ameaça formidável às nossas operações aqui em Silithus. Com os novos inimigos que estamos enfrentando em Ahn'Qiraj, não podemos nos dar o luxo de baixar a guarda contra nossos inimigos aqui fora.\n\nAche um modo de evocar e destruir um Templário Gris e apresente-se a Bor Juba Agreste.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Tática CenarianaMissão Tática"},
[8536] = {title = "Templário Terrano", objective = "Evoque e mate um Templário Terrano e fale novamente com Bor Juba Agreste no Forte Cenariano. Você também precisa entregar as Instruções Táticas III para concluir a missão.", description = "O Martelo do Crepúsculo e os nobres elementais que eles veneram são uma formidável ameaça às nossas operações aqui em Silithus. Com os novos inimigos que estamos enfrentando em Ahn'Qiraj, não podemos nos dar o luxo de baixar a guarda contra nossos inimigos aqui fora.\n\nAche um modo de evocar e destruir um Templário Terrano e apresente-se a Bor Juba Agreste.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Tática CenarianaMissão Tática Continuada"},
[8537] = {title = "Templário Carmesim", objective = "Evoque e mate um Templário Carmesim e fale novamente com Bor Juba Agreste no Forte Cenariano. Você também precisa entregar as Instruções Táticas II para concluir a missão.", description = "O Martelo do Crepúsculo e os nobres elementais que eles veneram são uma formidável ameaça às nossas operações aqui em Silithus. Com os novos inimigos que estamos enfrentando em Ahn'Qiraj, não podemos nos dar o luxo de baixar a guarda contra nossos inimigos aqui fora.\n\nAche um modo de evocar e destruir um Templário Carmesim e apresente-se a Bor Juba Agreste.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Tática CenarianaMissão Tática"},
[8538] = {title = "Os quatro duques", objective = "Encontre uma forma de evocar e matar o Duque das Cinzas, o Duque das Profundezas, o Duque dos Zéfiros e o Duque dos Estilhaços e apresente-se ao Comandante Mar'alith no Forte Cenariano. Você também precisa entregar as Instruções Táticas V para concluir a missão.", description = "O vínculo entre o Martelo do Crepúsculo e o Conselho Abissal está se provando mais importante do que imaginávamos.\n\nAcreditamos que os duques abissais sejam uma das forças mais importantes a organizar os sectários do Martelo do Crepúsculo. Ache uma forma de atrair os quatro duques abissais para nosso plano de existência e destrua-os!\n\nSua dedicação e perícia em missões anteriores nos levou a escolhê-l<o/a> para isso. Temos certeza de que você terá sucesso.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMarca de Cenarius"},
[8539] = {title = "Alvo: irmãs da Colme'Zora", objective = "Mate 30 Irmãs da Colme'Zora e fale novamente com o Comandante Mar'alith no Forte Cenariano, que fica em Silithus. Você também precisa entregar as Instruções de Combate V para concluir a missão.", description = "Foram solicitados reforços para se juntarem ao ataque à Colme'Zora e você foi <selecionado/selecionada> para atacar as irmãs da Colme'Zora. Fale novamente com o comandante Mar'alith quando tiver concluído a sua tarefa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia de Combate Cenariana"},
[8540] = {title = "Botas para a guarda", objective = "Leve 3 Botas Ornadas de Mithril para Vish Kozus, Capitão da Guarda, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística II para concluir a missão.", description = "Botas ornadas de mithril andam escassas para a divisão montada da guarda do Forte Cenariano. Providencie um lote e entregue imediatamente ao capitão Vish Kozus no Forte Cenariano.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística"},
[8541] = {title = "Pedras de amolar para a guarda", objective = "Leve 10 Pedras de Amolar Densas, 10 Pedras de Amolar Maciças e 10 Pedras de Amolar Pesadas para Vish Kozus, Capitão da Guarda, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística III para concluir a missão.", description = "Pedras de amolar andam escassas para a guarda do Forte Cenariano. Providencie um lote e entregue imediatamente ao capitão Vish Kozus no Forte Cenariano.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística"},
[8542] = {title = "A Horda precisa de barras de estanho!", objective = "Leve 20 Barras de Estanho ao Bruto Maug no Vale dos Espíritos em Orgrimmar.", description = "Lok'tar, <race>, você já deve saber que vamos à guerra em Ahn'Qiraj contra os insetos, mas, primeiro, o Chefe Guerreiro decidiu que vamos fazer algo chamado \"preparativos\". Eu preferia estar afiando armas e moldando armaduras a marteladas, mas me orgulho de ter sido escolhido para juntar barras de estanho. Só que sem sua ajuda não vai dar.\n\nPor isso preciso que você saia, fabrique barras de estanho e me traga. Acho que vou precisar de um número maior do que eu sei contar.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8543] = {title = "A Horda precisa de mais barras de estanho!", objective = "Leve 20 Barras de Estanho ao Bruto Maug em Durotar.", description = "Muito bem, estou vendo que você leva a guerra a sério. Já eu mal posso esperar por ela. Ou seja, vou começar a ficar nervoso se você não trouxer a mercadoria logo. Quem sabe eu não começo a treinar em você.\n\nHar! Acho que não, porque aí quem ia me trazer as barras de estanho?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8544] = {title = "Espaldares do Conquistador", objective = "Leve os Braceletes do Comando Qiraji, 2 Ídolos da Noite, 5 Escaravelhos de Pedra e 5 Escaravelhos de Barro para Andorgos em Ahn'Qiraj. Também é necessário ser <Tolerado/Tolerada> pela Prole de Nozdormu para concluir a missão.", description = "Dizem que as exuberantes ombreiras usadas pelas raças mortais foram uma tentativa de imitar asas de dragão.\n\nTraga-me os braceletes utilizados pelos líderes supremos dos qirajis, e eu os transformarei em proteções de ombros mais terríveis que as asas do próprio Nefarian!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nEspaldares do Conquistador"},
[8545] = {title = "A Horda precisa de barras de mithril!", objective = "Leve 20 Barras de Mithril à Primeiro-Sargento T'kelah no Vale dos Espíritos em Orgrimmar.", description = "<class>, a guerra tá na boca de sair e a única coisa que a gente pode fazer é se preparar pra ela. É aí que a gente pode se ajudar, cara. Saca só, me botaram pra juntar todas as barras de mithril que a gente vai precisar pra fazer tudo que a gente precisa. E ainda tô precisando de mais!\n\nPor isso, eu tô querendo que tu mande ver no garimpo pra botar mais umas barras na minha mão. Tu acha que dá conta do recado?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8546] = {title = "A Horda precisa de mais barras de mithril!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8547] = {title = "Boas-vindas!", objective = "Entregue o Vale-brinde da Praça Asa do Falcão à Marsilla Aurestela na Praça Asa do Falcão.", description = "$GBem-vindo:Bem-vinda; ao World of Warcraft!\n\nComo agradecimento especial pela compra do World of Warcraft Collector's Edition, você receberá um presente. Entregue este vale-brinde para Marsilla Aurestela, na Praça Asa do Falcão, e ganhe uma mascote que vai <acompanhá-lo/acompanhá-la> em sua busca por aventura e glória.\n\nObrigado novamente, e aproveite sua estadia no World of Warcraft!", progress = nil, completion = nil, rewards = nil},
[8548] = {title = "Equipamento de batalha do voluntário", objective = "Leve 5 Insígnias de Combate Cenarianas, 3 Insígnias Logísticas Cenarianas e 7 Insígnias Táticas Cenarianas para Vargus no Forte Cenariano, em Silithus. Também é necessário ser <Respeitado/Respeitada> pelo Círculo Cenariano para concluir a missão.", description = "As insígnias que você acumula em tarefas táticas, de combate e de logística para o Forte Cenariano não são só enfeite, <name>.\n\nÉ bom guardá-las. Conforme você for provando sua lealdade a nossa organização, em troca das insígnias eu posso lhe oferecer alguns dos equipamentos que reservamos para os nossos maiores aliados.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas do Poder TerranoAnel da Ira TerranaManto de Trama Terrena"},
[8549] = {title = "A Horda precisa de botão-da-paz!", objective = "Leve 20 Botões-da-paz à Herborista Pena Altiva no Vale dos Espíritos em Orgrimmar.", description = "Dizem que toda geração tem de enfrentar um teste definitivo. Temo que a guerra em Ahn'Qiraj seja a prova final do nosso valor. Por isso, vamos nos preparar e, com isso, descobrir de que somos feitos.\n\nEu me ofereci para ajudar a Horda juntando todo botão-da-paz que puder, pra transformar em poções para a guerra. Espero que você esteja aqui para me ajudar. Se estiver, preciso que você me traga uma boa quantidade da erva. Posso contar com você, <class>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8550] = {title = "A Horda precisa de mais botões-da-paz!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8551] = {title = "O baú do capitão", objective = "Leve o Baú do Smotts para Hacklebury Smotts na Angra do Butim.", description = "Eu costumava ir de barco daqui até Vila Catraca, em Kalimdor. Eu era um capitão bem-sucedido, de reputação impecável.\n\nAté que...\n\nAqueles malditos gigantes do mar me arruinaram! Eles destruíram o meu navio, mataram a minha tripulação e me abandonaram num bote salva-vidas a caminho da Angra do Butim. Um dos gigantes, Gorlash, roubou o meu baú. Disse que queria como porta-rapé. Que audácia!\n\nEu ouvi falar que ele vaga pela costa, a leste daqui. Encontre-o e recupere meu baú! Faça isso e você terá conquistado a gratidão de um capitão.", progress = "E então, <name>! Você achou o Gorlash? Aquele era o meu baú favorito e tinha um compartimento escondido com os meus maiores tesouros!", completion = "Você encontrou! Que dia glorioso! Obrigado, &lt;name&gt;. Recuperar meu baú abrandou um pouco o fogo dentro de mim.\n\nMas a minha vingança não terminou...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBraçadeiras Feixessangue"},
[8552] = {title = "A faixa com monograma", objective = "Devolva a Faixa com Monograma ao proprietário.", description = "A faixa refinada traz as letras \"HS\" bordadas na bainha.", progress = "Olá. Tem negócios a tratar comigo?", completion = "Você recuperou a minha faixa? Isso quer dizer... que você matou Mok'rash, gigante marinho! Uau!\n\nAinda bem o vilão sarnento está morto, Mok'rash era um dos três gigantes marinhos que esmagou meus navios e matou minha tripulação.\n\nTrês vezes.\n\nVocê é um &lt;class&gt; de valor, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[8553] = {title = "O alfanje do capitão", objective = "Fale com Zero.", description = "Depois de perder meu primeiro navio para aqueles gigantes, eu comprei outro. Batizei como Vingança do Smotts. Enchi de suprimentos, contratei tripulação e zarpei atrás dos desgraçados. Encontrei os malditos, mas... eles venceram. Destroçaram o Vingança do Smotts, mataram minha segunda tripulação e me mandaram e volta noutro bote.\n\nDessa vez outro dos gigantes, Negolash, roubou meu cutelo.\n\nEnfrente Negolash e me traga meu cutelo!\n\nFale com o Zero. Ele foi meu cozinheiro no Vingança do Smotts, e sobreviveu ao último ataque. Ele pode ajudar você a achar o gigante.", progress = nil, completion = "Olá olá, &lt;name&gt;. O capitão me disse que você vai atrás do Negolash, né?", rewards = "Você receberá:\n\n"},
[8554] = {title = "Confrontando Negolash", objective = "Leve 10 Asas de Urubu Grelhadas e 5 garrafas de Vinho Selvavinha até o bote salva-vidas do Capitão Smotts.\n\nMate Negolash e leve o Alfanje de Smotts para o Capitão Smotts na Selva do Espinhaço.", description = "O Negolash é um gigante esfomeado.\n\nQuando os gigantes destruíram o nosso navio, o Negolash foi direto na minha cozinha. Ele não queria comer a tripulação, como os outros gigantes. Ele queria vinho... e a minha asa de urubu grelhada. É a minha especialidade, e eu tive que ir pegar a receita lá nos Ermos!\n\nColoca isso no nosso bote velho, a sudeste daqui, seguindo a costa. Quando o Negolash sentir o cheiro dessa comida toda, ele vem com certeza!", progress = "Trouxe o meu alfanje, <name>?", completion = "Você tirou meu alfanje do Negolash! Eu nem acredito no meu destino, &lt;name&gt;! Encontrar você mudou totalmente a minha sorte! E para melhor, não se engane!\n\nObrigado! Se eu conseguir um novo barco e voltar a navegar pelos mares, você será minha convidada de honra.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBússola de Smotts"},
[8555] = {title = "O ataque das revoadas", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8556] = {title = "Sinete da Força Obstinada", objective = "Leve 1 Anel Magistral Qiraji, 2 Ídolos Bruxuleantes, 5 Escaravelhos de Bronze e 5 Escaravelhos de Marfim para a Arauto do Vento Yessendra em Silithus. Também é necessário ser <Honrado/Honrada> pelo Círculo Cenariano para concluir a missão.", description = "Há rumores de que os anéis usados por tenentes qirajis concedem habilidades formidáveis. As gemas conspurcadas que os adornam podem ser substituídas por material mais puro para a criação de versões não conspurcadas dos anéis.\n\nAs gemas que adornam os ídolos qirajis serviriam bem, por sua alta qualidade.\n\nTraga-me o anel e os ídolos e criarei um anel poderoso para você, <class>.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete da Força Obstinada"},
[8557] = {title = "Clâmide da Força Obstinada", objective = "Leve 1 Clâmide Marcial Qiraji, 2 Ídolos Ônix, 5 Escaravelhos de Prata e 5 Escaravelhos de Osso para Keyl Garralesta em Silithus. Também é necessário ser <Reverenciado/Reverenciada> pelo Círculo Cenariano para concluir a missão.", description = "Agrada-me saber que você nos ajudará no ataque a Ahn'Qiraj, <name>. Posso criar um item de proteção que lhe será útil na luta contra o inimigo, <class>.\n\nVou precisar de uma clâmide qiraji, que ajustarei a você, claro. Também vou precisar de ídolos e escaravelhos que usarei para criar um broche que lhe sirva.\n\nPrometo que você não se decepcionará.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nClâmide da Força Obstinada"},
[8558] = {title = "Foice da Força Obstinada", objective = "Leve 1 Punho de Pontas Qiraji, 2 Ídolos de Alabastro, 5 Escaravelhos de Cristal e 5 Escaravelhos de Pedra para o Guardião Haro em Silithus. Também é necessário ser <Exaltado/Exaltada> pelo Círculo Cenariano para concluir a missão.", description = "Os qirajis e seus servos empunham itens feitos do mais puro e raro material: ossos de criaturas ancestrais gigantescas, gemas das profundezas da terra, metais puros e resistentes.\n\nA simples visão de artefatos qirajis me leva a querer praticar meu ofício com suas peças estilhaçadas e fundidas. Prove sua lealdade ao Círculo, <name>, e criarei uma poderosa arma dos melhores materiais qirajis para você.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nFoice da Força Obstinada"},
[8559] = {title = "Grevas do Conquistador", objective = "Leve os Braceletes do Comando Qiraji, 2 Ídolos de Guerra, 5 Escaravelhos de Marfim e 5 Escaravelhos de Ouro para Kandrostrasz em Ahn'Qiraj. Também é necessário ser <Tolerado/Tolerada> pela Prole de Nozdormu para concluir a missão.", description = "A magia qiraji é antiga e poderosa. Os qirajis mais poderosos empunham itens de um poder tal que seriam capazes de paralisar até mesmo um dragão.\n\nTraga-me as braçadeiras do domínio qiraji e outros componentes secundários e criarei proteções poderosas para seus pés.\n\nIsso vai ser muito útil na luta contra o inimigo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGrevas do Conquistador"},
[8560] = {title = "Guarda-pernas do Conquistador", objective = "Leve o Pelego Intacto de Ouroboros, 2 Ídolos da Morte, 5 Escaravelhos de Bronze e 5 Escaravelhos de Marfim para Kandrostrasz em Ahn'Qiraj. Também é necessário ser <Respeitado/Respeitada> pela Prole de Nozdormu para concluir a missão.", description = "Deseja provar seu valor ao meu povo, mortal? Muito bem, talvez possamos nos ajudar mutuamente.\n\nO verme da areia ancestral, Ouroboros, é um ser de lendário poder. Há rumores de que ele foi criado pelo próprio Deus Antigo como um arremedo de vida.\n\nSua pele é extraordinária... espessa, quase impenetrável, porém flexível, permitindo que se mova com a velocidade e graça de uma criatura de porte bem menor.\n\nTraga-me uma amostra da sua pele juntamente com outros componentes secundários, e eu confeccionarei proteções de imenso poder para suas pernas.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGuarda-pernas do Conquistador"},
[8561] = {title = "Coroa do Conquistador", objective = "Leve o Diadema de Vek'Nilash, 2 Ídolos do Sol, 5 Escaravelhos de Pedra e 5 Escaravelho de Cristal para Andorgos em Ahn'Qiraj. Também é necessário ser <Respeitado/Respeitada> pela Prole de Nozdormu para concluir a missão.", description = "Os Imperadores Gêmeos... seus murmúrios incessantes estão, lentamente, embotando minha razão. Zombam de mim, sabendo que não posso penetrar em seus domínios para salvar meu irmão, Arygos.\n\nEm minhas visões, enxergo seus rostos monstruosos adornados por poderosas coroas. Mas eu terei minha vingança... Submeterei estes símbolos do poder Qiraji à nossa vontade.\n\nTraga-me os componentes de que preciso, <name>, e forjarei uma peça de poder inigualável para proteger sua cabeça.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCoroa do Conquistador"},
[8562] = {title = "Peitoral do Conquistador", objective = "Leve a Carapaça do Deus Antigo, 2 Ídolos de Guerra, 5 Escaravelhos de Prata e 5 Escaravelhos de Osso para Vethsera em Ahn'Qiraj. Também é necessário ser <Honrado/Honrada> pela Prole de Nozdormu para concluir a missão.", description = "O Deus Antigo, cujo nome não ouso proferir, desperta lentamente de seu sono nos subterrâneos de Ahn'Qiraj. O terrível destino de Azeroth pode se concretizar em questão de dias ou anos; o tempo nada significa para criatura tão poderosa.\n\nQuando as estrelas se mostrarem favoráveis, ele se lançará contra todos os seres vivos com terrível ira. Você precisa encontrá-lo antes disso e descobrir um modo de dar fim à sua existência.\n\nFaça isso e me traga um pedaço da carapaça dele. Eu lhe agraciarei com uma armadura que nenhum mortal jamais vestiu... digna de quem derrotou um deus!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPeitoral do Conquistador"},
[8563] = {title = "Treinamento de bruxo", objective = "Fale com o instrutor de bruxos Evocador Teli'Larien na Torre do Sol, que fica na Ilha Andassol.À medida que você progredir como <class>, convém aproveitar o treinamento que o respectivo instrutor oferece. Para jovens bruxos, aqui na Ilha Andassol, o instrutor é o Evocador Teli'Larien. Fale com ele e descubra que treinamento está disponível para você. Ele cobra pelo serviço, por isso leve algum dinheiro.\n\nSeu instrutor, assim como todos os instrutores da Ilha Andassol, encontra-se no nível inferior da Torre do Sol.Completando essa missão você ganhará:\n\n", description = "À medida que você progredir como <class>, convém aproveitar o treinamento que o respectivo instrutor oferece. Para jovens bruxos, aqui na Ilha Andassol, o instrutor é o Evocador Teli'Larien. Fale com ele e descubra que treinamento está disponível para você. Ele cobra pelo serviço, por isso leve algum dinheiro.\n\nSeu instrutor, assim como todos os instrutores da Ilha Andassol, encontra-se no nível inferior da Torre do Sol.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[8564] = {title = "Treinamento de sacerdote", objective = "Fale com a instrutora de sacerdotes Máter Arena na Torre do Sol, que fica na Ilha Andassol.À medida que você progredir como <class>, convém aproveitar o treinamento que o respectivo instrutor oferece. Para jovens sacerdotes, aqui na Ilha Andassol, a instrutora é a Máter Arena. Fale com ela e descubra que treinamento está disponível para você. Ela cobra pelo serviço, por isso leve algum dinheiro.\n\nMáter Arena, assim como todos os instrutores da Ilha Andassol, encontra-se no nível inferior da Torre do Sol.Completando essa missão você ganhará:\n\n", description = "À medida que você progredir como <class>, convém aproveitar o treinamento que o respectivo instrutor oferece. Para jovens sacerdotes, aqui na Ilha Andassol, a instrutora é a Máter Arena. Fale com ela e descubra que treinamento está disponível para você. Ela cobra pelo serviço, por isso leve algum dinheiro.\n\nMáter Arena, assim como todos os instrutores da Ilha Andassol, encontra-se no nível inferior da Torre do Sol.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
