[8572] = {title = "Equipamento de batalha do veterano", objective = "Leve 7 Insígnias de Combate Cenarianas, 4 Insígnias Logísticas Cenarianas e 4 Insígnias Táticas Cenarianas para Vargus no Forte Cenariano, em Silithus. Também é necessário ser <Honrado/Honrada> pelo Círculo Cenariano para concluir a missão.", description = "As insígnias que você acumula em tarefas táticas, de combate e de logística para o Forte Cenariano não são só enfeite, <name>.\n\nÉ bom guardá-las. Conforme você for provando sua lealdade a nossa organização, em troca das insígnias eu posso lhe oferecer alguns dos equipamentos que reservamos para os nossos maiores aliados.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nDádiva da TerraElo do Poder TerranoColete do Poder Terreno"},
[8573] = {title = "Equipamento de batalha do campeão", objective = "Leve 15 Insígnias de Combate Cenarianas, 20 Insígnias Logísticas Cenarianas, 20 Insígnias Táticas Cenarianas e 1 Marca de Cenarius para Vargus no Forte Cenariano, em Silithus. Também é necessário ser <Exaltado/Exaltada> pelo Círculo Cenariano para concluir a missão.", description = "As insígnias que você acumula em tarefas táticas, de combate e de logística para o Forte Cenariano não são só enfeite, <name>.\n\nÉ bom guardá-las. Conforme você for provando sua lealdade a nossa organização, em troca das insígnias eu posso lhe oferecer alguns dos equipamentos que reservamos para os nossos maiores aliados.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPunho de CenariusIra de CenariusGolpe Terreno\n\nVocê também receberá:\n\n"},
[8574] = {title = "Equipamento de batalha do impávido", objective = "Leve 15 Insígnias de Combate Cenarianas, 20 Insígnias Logísticas Cenarianas, 17 Insígnias Táticas Cenarianas e 1 Marca de Remulos para Vargus no Forte Cenariano, em Silithus. Também é necessário ser <Reverenciado/Reverenciada> pelo Círculo Cenariano para concluir a missão.", description = "As insígnias que você acumula em tarefas táticas, de combate e de logística para a Arauto do Vento Chifre Altivo não são só enfeite, <name>.\n\nÉ bom guardá-las. Conforme você for provando sua lealdade a nossa organização, em troca das insígnias eu posso lhe oferecer alguns dos equipamentos que reservamos para os nossos maiores aliados.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras RochafundaOrbe TerramansaBraçadeiras RochafúriaPoder de Cenarius"},
[8575] = {title = "O livro-caixa mágico de Azuregos", objective = "Entregue o Livro-caixa Mágico de Azuregos a Narain Suavestilo em Tanaris.", description = "Você não consegue entender uma única palavra desse livro. Você não sabe nem em que idioma ele está!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8576] = {title = "Traduzindo o livro", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8577] = {title = "Stewvul, o ex-melhor amigo no mundo todo", objective = "Narain Suavestilo quer que você localize o ex-melhor amigo dele no mundo inteiro, Stewvul, e recupere os óculos de vidência que Stewvul lhe roubou.", description = "Se for para eu traduzir qualquer coisa, eu preciso dos meus óculos de vidência. Infelizmente já faz um tempo que eles foram roubados pelo meu ex-melhor amigo no mundo todo, Stewvul. Ele arrombou meu coração e depois me deixou aqui pra apodrecer.\n\nCom a ajuda de minha fiel bola de cristal, eu consegui triangular o paradeiro dele. Ou ele está na Muralha Greymane, em Pinhaprata, ou está em um portal em Terralém que está sendo tomado pela Legião Ardente. Você precisa encontrá-lo e recuperar os meus óculos, <name>!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8578] = {title = "Óculos de vidência? Sem problema!", objective = "Encontre os Óculos da Vidência de Narain e devolva-os para Narain Suavestilo em Tanaris.", description = "<Você ouve um suspiro de alívio vindo da caixa.>\n\nVocê só quer os óculos de vidência dele? Ótimo! Não em problema... É só um minutinho... Ei! Mas onde diab... Parafusos!\n\nBom... Tem a notícia ruim e a pior. A ruim é que eu perdi os óculos. A pior é que eu deixei cair quando estava bisbilhotando no Núcleo Derretido. É isso aí. O Núcleo Derretido. E eu acho que é isso. Tchauzinho!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPoção Elevada de Rejuvenescimento"},
[8579] = {title = "Campeões mortais", objective = nil, description = nil, progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nReputação – Chefe do Templo de Ahn'Qiraj\n\nVocê receberá:\n\n"},
[8580] = {title = "A Horda precisa de ignídeas!", objective = "Leve 20 Ignídeas ao Cavalga-morcegos Pele'keiki no Vale dos Espíritos em Orgrimmar.", description = "Fala, <class>. Tudo em cima! Tem um monte de botão-de-chamas que a Horda acha que vai precisar no tal do esforço de guerra em Ahn'Qiraj. Vai ver que vão colocar nas belezinhas que explodem, ou dar pra Aliança como combustível pros tanques a vapor? Vai saber? Pele'keiki não!\n\nPele'keiki tá ligado... ele sabe que tu vai dar uma força e trazer um monte de ignídeas, né não, cara?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8581] = {title = "A Horda precisa de mais ignídeas!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8582] = {title = "A Horda precisa de lótus roxo!", objective = "Leve 20 Lótus Roxos à Boticária Jezebel no Vale dos Espíritos em Orgrimmar.", description = "O lótus roxo, <class>, é o reagente de um grande número de poções, elixires e unguentos, alguns dos quais nos serão úteis em Ahn'Qiraj. Vamos precisar criar um número infinito dessas poções para o esforço de guerra. Além disso, vou pesquisar mais usos para o lótus. Mas para essa empreitada vou precisar de uma grande quantidade da flor.\n\nÉ aí que você entra. Vá a campo e colha todo o lótus roxo que puder. Você estaria <disposto/disposta> a levar essa tarefa a cabo para nós?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8583] = {title = "A Horda precisa de mais lótus roxo!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8584] = {title = "Nunca questione o meu trabalho", objective = "Narain Suavestilo, em Tanaris, quer que você fale com Funesto Marrão em Geringontzan.", description = "Se tem uma coisa que você tem que saber sobre mim é o seguinte: NUNCA questione o meu trabalho! Eu lido com coisas que você simplesmente não conseguiria entender. Tipo... sabe o que é um focinhódorfo psicotrônico? Claro que não! E é por isso que deixamos o planejamento comigo e a logística com você.\n\nComo eu já te falei, se é pra isso aqui funcionar, vamos precisar de uma galinha de 250 quilos.\n\nVá a Geringontzan e fale com Funesto Marrão. Ele deve orientar você quanto à aquisição de um espécime desse.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8585] = {title = "A Ilha do Medo!", objective = "Obtenha a Carcaça de Lakmaeran e 20 Lombos de Quiméroco para Funesto Marrão em Tanaris.", description = "Segundo a lenda, os quimérocos da Ilha do Medo, em Feralas, dão os melhores e mais suculentos pratos que qualquer um ouviu falar! Os quimérocos também têm um papai que, se preparado do jeito certo, pode ou não parecer um frango de mais de duzentos quilos.\n\nVai lá na Ilha do Medo, mata o Lorde Lakmaeran e traz a carcaça fresca dele. Isso deve dar pro gnomo. Pra mim, você vai ter que trazer uns lombinhos de quiméroco.\n\nFaz isso que você vai ter seu frango de trocentos quilos e um extrazinho.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8586] = {title = "As costeletas sinistronas de quiméroco do Funesto", objective = "Funesto Marrão, em Geringontzan, quer que você providencie 20 unidades de Combustível de Foguete Goblínico e 20 de Sal Rochafunda.", description = "A minha receita está completa! O que eu preciso agora é de combustível de foguete e sal rochafunda. Eu cuido do tempero, e a carne que você me trouxe vai dar conta do resto.\n\nDepois que você me trouxer o que eu pedi, é só embalar o frango do Narain pra viagem.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nReceita: Costeletas Sinistronas de Quiméroco do FunestoCosteletas Sinistronas de Quiméroco do Funesto"},
[8587] = {title = "De volta a Narain", objective = "Entregue o Frango de 250 Quilos a Narain Suavestilo em Tanaris.", description = "Eu sou goblin de palavra, garot<o/a>. Taqui o frangão de 250 quilos.\n\nO que um gnomo vai querer com um desses eu nunca vou saber...\n\n<Funesto resmunga algo ininteligível.>\n\nProvavelmente é pra dormir nele, sei lá. Gnominhos sujos...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8588] = {title = "A Horda precisa de couro pesado!", objective = "Leve 10 peças de Couro Pesado à Esfoladora Jamani no Vale dos Espíritos em Orgrimmar.", description = "Já que a gente tá se preparando pra guerra, seria uma boa juntar tudo que precisa antes, tu não acha, cara? Uma das bases do equipamento de guerra que a gente tá produzindo é couro pesado. A gente usa pra quase tudo, de reforço a armaduras, além das boas e velhas lonas.\n\nMas primeiro a gente precisa botar as mãos em mais couro. É aí que tu entra, <class>. Afia a faca e bota um fardo na minha mão. Aí a gente continua a conversa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8589] = {title = "A Horda precisa de mais couro pesado!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8590] = {title = "A Horda precisa de couro grosso!", objective = "Leve 10 peças de Couro Grosso à Sargento Umala no Vale dos Espíritos em Orgrimmar.", description = "Fico feliz por você ter decidido falar comigo sobre isso, <class>. Parece que os preparativos da Horda para a batalha iminente em Ahn'Qiraj estão a todo vapor, mas você e eu ainda precisamos fazer nossa parte.\n\nMe pediram para juntar uma grande quantidade de couro grosso para usar em todo tipo de equipamento de guerra. E eu preciso que você me ajude saindo e juntando couro. Você será honrado e terá a gratidão da Horda se optar por nos ajudar. Pode ser?", progress = "<class>, já voltou com as dez peças de couro grosso de que falamos?", completion = "Bom trabalho, &lt;class&gt;. Tenho certeza de que essas peças de couro grosso serão aproveitadas para reforçar zepelins e fabricar todo tipo de armadura e armamento. Você tem minha gratidão e a da Horda. Volte aqui se encontrar mais couro grosso em suas viagens e que a sorte a acompanhe.", rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8591] = {title = "A Horda precisa de mais couro grosso!", objective = "<class>, como você pode ver, eu ainda preciso juntar mais couro grosso. Mais uma vez venho pedir sua ajuda nesta tarefa e, prometo, se concluí-la, seus esforços serão reconhecidos.\n\nO tempo urge! Volte aqui com o couro espesso para que possamos concluir nossos preparativos e ir à guerra, heroína!", description = nil, progress = "<class>, como você pode ver, eu ainda preciso juntar mais couro grosso. Mais uma vez venho pedir sua ajuda nesta tarefa e, prometo, se concluí-la, seus esforços serão reconhecidos.\n\nO tempo urge! Volte aqui com o couro espesso para que possamos concluir nossos preparativos e ir à guerra, heroína!", completion = "Você conseguiu, &lt;name&gt;. Eu sabia que você conseguiria! Mais uma vez, obrigado em nome de toda a Horda. Seus esforços são notáveis e imensamente estimados. Se tiver mais couro grosso para doar, traga-o aqui para mim.\n\nLok'tar Ogar, &lt;class&gt;!", rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8592] = {title = "Tiara do Oráculo", objective = "Leve o Diadema de Vek'Nilash, 2 Ídolos do Sábio, 5 Escaravelhos de Prata e 5 Escaravelhos de Osso para Andorgos em Ahn'Qiraj. Também é necessário ser <Respeitado/Respeitada> pela Prole de Nozdormu para concluir a missão.", description = "Os Imperadores Gêmeos... seus murmúrios incessantes estão, lentamente, embotando minha razão. Zombam de mim, sabendo que não posso penetrar em seus domínios para salvar meu irmão, Arygos.\n\nEm minhas visões, enxergo seus rostos monstruosos adornados por poderosas coroas. Mas eu terei minha vingança... Submeterei estes símbolos do poder Qiraji à nossa vontade.\n\nTraga-me os componentes de que preciso, <name>, e forjarei uma peça de poder inigualável para proteger sua cabeça.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nTiara do Oráculo"},
[8593] = {title = "Calças do Oráculo", objective = "Leve o Pelego Intacto de Ouroboros, 2 Ídolos da Vida, 5 Escaravelhos de Ouro e 5 Escaravelhos de Barro para Kandrostrasz em Ahn'Qiraj. Também é necessário ser <Respeitado/Respeitada> pela Prole de Nozdormu para concluir a missão.", description = "Deseja provar seu valor ao meu povo, mortal? Muito bem, talvez possamos nos ajudar mutuamente.\n\nO verme da areia ancestral, Ouroboros, é um ser de lendário poder. Há rumores de que ele foi criado pelo próprio Deus Antigo como um arremedo de vida.\n\nSua pele é extraordinária... espessa, quase impenetrável, porém flexível, permitindo que se mova com a velocidade e graça de uma criatura de porte bem menor.\n\nTraga-me uma amostra da sua pele juntamente com outros componentes secundários, e eu confeccionarei proteções de imenso poder para suas pernas.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCalças do Oráculo"},
[8594] = {title = "Dragonas do Oráculo", objective = "Leve os Braceletes do Comando Qiraji, 2 Ídolos do Renascimento, 5 Escaravelhos de Prata e 5 Escaravelhos de Marfim para Andorgos em Ahn'Qiraj. Também é necessário ser <Tolerado/Tolerada> pela Prole de Nozdormu para concluir a missão.", description = "Dizem que as exuberantes ombreiras usadas pelas raças mortais foram uma tentativa de imitar asas de dragão.\n\nTraga-me os braceletes utilizados pelos líderes supremos dos qirajis, e eu os transformarei em proteções de ombros mais terríveis que as asas do próprio Nefarian!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nDragonas do Oráculo"},
[8595] = {title = "Campeões mortais", objective = "Matar um soberano qiraji é um feito notável para um mortal, <name>. No entanto, eles são muitos e poderosos. Continue a derrotá-los para provar seu valor como campeão.", description = nil, progress = "Matar um soberano qiraji é um feito notável para um mortal, <name>. No entanto, eles são muitos e poderosos. Continue a derrotá-los para provar seu valor como campeão.", completion = "Você segue nos sendo útil, &lt;name&gt;. Nosso pacto ainda vigora.", rewards = "O seguinte feitiço será lançado em você:\n\nReputação – Chefe do Templo de Ahn'Qiraj"},
[8596] = {title = "Palmilhas do Oráculo", objective = "Leve os Braceletes do Comando Qiraji, 2 Ídolos da Morte, 5 Escaravelhos de Bronze e 5 Escaravelhos de Ouro para Kandrostrasz em Ahn'Qiraj. Também é necessário ser <Tolerado/Tolerada> pela Prole de Nozdormu para concluir a missão.", description = "A magia qiraji é antiga e poderosa. Os qirajis mais poderosos empunham itens de um poder tal que seriam capazes de paralisar até mesmo um dragão.\n\nTraga-me os braceletes do domínio qiraji e outros componentes secundários e criarei proteções poderosas para seus pés.\n\nIsso vai ser muito útil na luta contra o inimigo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPalmilhas do Oráculo"},
[8597] = {title = "Dracônico para Leigos", objective = "Encontre o livro de Narain Suavestilo, enterrado em uma ilha nos Mares do Sul.", description = "Eu sabia que aquele livro ia servir para alguma coisa um dia! Felizmente eu tive a sagacidade de guardar em lugar seguro.\n\nNão se preocupe, ele está onde ninguém jamais pensaria em procurar. Precisei de um girocóptero só pra chegar lá da primeira vez. Infelizmente eu caí na praia na volta. Foi como se no acidente eu tivesse ganho poderes psíquicos supergnômicos!\n\nProcure o meu girocóptero lá na Praia do Fim do Mundo. De lá, nade pro sul. Você vai saber quando vir!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8598] = {title = "r3sGAte", objective = "Leve o Pedido de Resgate a Narain Suavestilo em Tanaris.", description = "<O texto do bilhete foi composto com letras soltas coladas no pergaminho.>\n\nEstamos com o seu precioso livro, gnomo. Se quiser vê-lo de novo, faça exatamente o que dissermos.\n\n<Segue-se uma série de instruções detalhadas, que caberia em umas três páginas.>\n\nTraga o dinheiro e venha sozinho!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8599] = {title = "Canção de amor para Narain", objective = "Entregue a Carta de Amor de Meridith a Narain Suavestilo em Tanaris.", description = "Não recebo muita visita, não. Na verdade, a última vez em que eu tive visita foi quando eu resgatei aquele gnominho doce e fofo das ferragens da máquina voadora.\n\n<O olhar de Meridith se perde nas lembranças.>\n\nVocê o conhece? Narain? Narain Suavestilo? Ah, pra mim seria tão bom se alguém levasse uma mensagem minha para ele... Eu preciso dizer a ele o que eu sinto. Eu sei que é amor! Eu sei! Eu sei!\n\nVocê poderia entregar uma mensagem ao Narain? Minha dívida para com você será eterna!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8600] = {title = "A Horda precisa de couro rústico!", objective = "Leve 10 peças de Couro Rústico ao Doutor Serratus no Vale dos Espíritos em Orgrimmar.", description = "Sou de opinião que o encaixe da faca na palma da mão é mais importante que o fio da navalha. Até mesmo a lâmina mais cega pode cortar desde que a pressão correta seja aplicada, você não concorda, <class>? Mas nós não temos tais problemas, pois nossas lâminas estão sempre afiadas, sim? \n\nBem, sem mais delongas, tenho uma tarefa para você. A serviço da Horda, e para fazer sua parte no esforço de guerra de Ahn'Qiraj, quero que você volte aqui com uma boa quantidade de couro rústico. Você estaria <disposto/disposta>, não?", progress = "Não é possível que você tenha demorado tanto para esfolar dez peças de couro rústico! Talvez você devesse afiar sua faca? Ou, quem sabe eu não esteja enganado e você já os tenha na mochila?", completion = "Muito bem, tudo indica que você realizou um trabalho bastante razoável, embora algumas dessas peles pareçam um pouco rotas nas pontas. Pouco importa, a tarefa está concluída e a recompensa é sua. Caso venha a acumular outro fardo de couro rústico, traga-o a mim sem demora.", rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8601] = {title = "A Horda precisa de mais couro rústico!", objective = "Seguem os preparativos para a guerra iminente em Ahn'Qiraj, <class>. No entanto, para garantir que não sejamos os retardatários ou, pelo menos, que eu não seja responsável pelo nosso atraso, quero que você me traga outro fardo de couro rústico. Sua pronta atenção a esta questão será imensamente estimada, <name>.", description = nil, progress = "Seguem os preparativos para a guerra iminente em Ahn'Qiraj, <class>. No entanto, para garantir que não sejamos os retardatários ou, pelo menos, que eu não seja responsável pelo nosso atraso, quero que você me traga outro fardo de couro rústico. Sua pronta atenção a esta questão será imensamente estimada, <name>.", completion = "Prática, prática, prática. Uma faca afiada e a capacidade de usá-la vão levá-la longe neste mundo, &lt;name&gt;. Vejo que as bordas dessas peles estão melhores que as anteriores. Você comprou outra faca?\n\nSeja como for, somos gratos pelo seu esforço. Sua doação foi notável e, garanto, será aproveitada a contento. Caso esteja de posse de mais couro rústico, não hesite em voltar a ter comigo, pois que eu devo vou precisar.", rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8602] = {title = "Brafoneiras do Tempestário", objective = "Leve os Braceletes do Domínio Qiraji, 2 Ídolos da Vida, 5 Escaravelhos de Ouro e 5 Escaravelhos de Cristal para Andorgos em Ahn'Qiraj. Também é necessário ser <Tolerado/Tolerada> pela Prole de Nozdormu para concluir a missão.", description = "Dizem que as exuberantes ombreiras usadas pelas raças mortais foram uma tentativa de imitar asas de dragão.\n\nTraga-me os braceletes utilizados pelos líderes supremos dos qirajis, e eu os transformarei em proteções de ombros mais terríveis que as asas do próprio Nefarian!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBrafoneiras do Tempestário"},
[8603] = {title = "Vestimenta do Oráculo", objective = "Leve a Carapaça do Deus Antigo, 2 Ídolos da Morte, 5 Escaravelhos de Pedra e 5 Escaravelhos de Cristal para Vethsera em Ahn'Qiraj. Também é necessário ser <Honrado/Honrada> pela Prole de Nozdormu para concluir a missão.", description = "O Deus Antigo, cujo nome não ouso proferir, desperta lentamente de seu sono nos subterrâneos de Ahn'Qiraj. O terrível destino de Azeroth pode se concretizar em questão de dias ou anos; o tempo nada significa para criatura tão poderosa.\n\nQuando as estrelas se mostrarem favoráveis, ele se lançará contra todos os seres vivos com terrível ira. Você precisa encontrá-lo antes disso e descobrir um modo de dar fim à sua existência.\n\nFaça isso e me traga um pedaço da carapaça dele. Eu lhe agraciarei com uma armadura que nenhum mortal jamais vestiu... digna de quem derrotou um deus!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nVestimenta do Oráculo"},
[8604] = {title = "A Horda precisa de bandagens de lã!", objective = "Leve 20 Bandagens de Lã ao Curador Pisa Léguas no Vale dos Espíritos em Orgrimmar.", description = "Salve, <class>. Estou aqui nos preparativos para a guerra em Ahn'Qiraj coordenando os esforços da Horda para juntar bandagens de lã. Infelizmente, lá no campo de batalha, nossos solados não podem contar com a presença de um curador para mantê-los vivos. Confie em mim. Eu sei isso de antemão. Por isso, estamos juntando esses kits de primeiros socorros para eles.\n\nSe você tiver tempo, preciso de muitas bandagens de lã. Traz pra mim que eu garanto a recompensa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8605] = {title = "A Horda precisa de mais bandagens de lã!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8606] = {title = "Isca!", objective = "Narain Suavestilo, em Tanaris, quer que você vá a Hibérnia e coloque o saco de ouro no ponto determinado pelos sequestradores do livro.", description = "Entendi! Nós vamos fazer o seguinte: eu encho um saco de pedras e você faz a entrega. Eu sei que diz que é para eu fazer, e eu vou — bom, pelo menos mais ou menos.\n\n<Narain examina você de cima a baixo.>\n\nÉ, você serve sim. Pega esse turbante, essa veste e esse saco de pedras e vai pro local da entrega, em Hibérnia.\n\nPus tudo que você vai precisar num kit especial. Quando eles aparecerem pra fazer a coleta, pega eles no pulo! Isso vai ensinar a essa gente o que acontece com quem se mete com Narain!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8607] = {title = "A Horda precisa de bandagem de magitrama!", objective = "Leve 20 Bandagens de Magitrama à Senhora Celara no Vale dos Espíritos em Orgrimmar.", description = "Suponho que você esteja falando comigo porque vai me ajudar na coleta de que me incumbiram? Ótimo! Não sei como, e não quero saber, mas quero que você junte uma boa quantidade de bandagem de magitrama. De preferência nova, mas se for usada tudo bem. Traga de volta aqui, <class>, e cuidarei pessoalmente para que você receba uma recompensa de acordo.", progress = "Não vá falhar e me fazer passar por incompetente, <class>! Volte aqui o quanto antes com as bandagens de magitrama de que falamos.", completion = "Inesperado, mas satisfatório. Talvez eu tenha subestimado sua \"capacidade\", &lt;class&gt;. Talvez. Você concluiu uma tarefa muito simples, e a recompensa está acima do esperado, mas não deixe que isso lhe suba à cabeça. Se realmente desejar provar seu valor, saia e me traga outro fardo de bandagens de magitrama.", rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8608] = {title = "A Horda precisa de mais bandagens de magitrama!", objective = "Você de novo? Interessante. Como você pode ver, ainda estou na coleta para o \"esforço de guerra\" em Ahn'Qiraj. Ou seja, preciso que você saia mais uma vez e me traga outro fardo de bandagens de magitrama. Aguardo ansiosa o seu retorno, <class>.", description = nil, progress = "Você de novo? Interessante. Como você pode ver, ainda estou na coleta para o \"esforço de guerra\" em Ahn'Qiraj. Ou seja, preciso que você saia mais uma vez e me traga outro fardo de bandagens de magitrama. Aguardo ansiosa o seu retorno, <class>.", completion = "Espantoso, &lt;class&gt;, de fato você conseguiu de novo! Ora, não é à toa que dizem que tenho bom gosto para escolher quem trabalha comigo. Muito bem, sua contribuição é notável e você colherá os frutos sociais provenientes do seu esforço. Contudo, sugiro que você saia novamente e me traga outro fardo de bandagens de magitrama, se realmente desejar provar seu valor.", rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8609] = {title = "A Horda precisa de bandagem de runatrama!", objective = "Leve 20 Bandagens de Runatrama à Guarda Pétrea Casco Barrento no Vale dos Espíritos em Orgrimmar.", description = "Tudo indica que a Horda e a Aliança estejam em uma rota de colisão inevitável na guerra de Ahn'Qiraj. Para nossa surpresa, não é uma guerra entre elas, mas contra os silitídeos e o poder que os controla. Outra surpresa, os dois lados estão fazendo sua parte reunindo os recursos necessários para promover uma guerra bem-sucedida.\n\nEstou juntando bandagens de runatrama para distribuir aos soldados, mas não terei sucesso sem ajuda, sua espero. Você acha que pode me ajudar com isso, <class>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8610] = {title = "A Horda precisa de mais bandagens de runatrama!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8611] = {title = "A Horda precisa de filé de lobo magro!", objective = "Leve 20 Filés de Lobo Magro à Guarda-de-Sangue Rawtar no Vale dos Espíritos em Orgrimmar.", description = "Eu não estou aqui só para pegar uma cor, <class>. Há preparativos de que cuidar. Se a gente quiser esmagar aqueles insetos em Ahn'Qiraj, precisamos estar preparados, e isso inclui comida!\n\nEu estou aqui para juntar e salgar filés de lobo magro pra guerra iminente. Mas, como eu tenho ficar aqui para cuidar de tudo isso, preciso que você saia, mate alguns lobos, prepare os filés de lobo magro cozido e traga para mim. Entendeu direitinho?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8612] = {title = "A Horda precisa de mais filé de lobo magro!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8613] = {title = "A Horda precisa de rabo-amarelo mosqueado!", objective = "Leve 20 Rabos-amarelos Mosqueados ao Pescador Lin'do no Vale dos Espíritos em Orgrimmar.", description = "Tá um dia maneiro pra pescar, tu não acha? Isso é bom, porque tô vendo que vou precisar de peixe. A Horda me botou pra juntar uma tonelada de rabo-amarelo mosqueado. Eles falaram que tão precisando dele pra guerra que tá pra rolar em Ahn'Qiraj. Eu mandei pra eles que tô dando o meu jeito. Por isso tô precisando de ajuda. Tu vai me dá uma força, <class>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8614] = {title = "A Horda precisa de mais rabo-amarelo mosqueado!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8615] = {title = "A Horda precisa de salmão assado!", objective = "Leve 20 Salmões Assados ao Chefe Garra Afiada no Vale dos Espíritos em Orgrimmar.", description = "O esforço de guerra em Ahn'Qiraj é uma tarefa hercúlea, <class>. Todo o material reunido aqui, seja metal ou armas, ou mesmo comida, tem a mesma importância. Pois, se ficar dias sem comer, você não terá força para levantar o machado quando for preciso.\n\nPortanto, estamos reunindo toda a comida necessária. Estou juntando salmão assado, um prato escolhido tanto pela sustança quanto pelo bem estar que proporciona. Você pode me ajudar, <class>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8616] = {title = "A Horda precisa de mais salmão assado!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete de Comenda da HordaSuprimentos de Guerra em Ahn'Qiraj"},
[8617] = {title = "A Horda precisa de pedras nucleares chamuscadas!", objective = "Leve 50 pedras nucleares chamuscadas para o General Zog em Durotar.", description = "Chegou a hora de contribuir para o esforço de guerra em Ahn'Qiraj, <class>. Foi-se o tempo de ficar procrastinando feliz por aí. Agora e a hora de servir a mim e à Horda, e eu lhe garanto que não sou tão leniente quanto o Chefe Guerreiro.\n\nVocê mobilizará suas forças, sua guilda, se você tiver uma, e invadirá o Núcleo Derretido. Lá você notará que os habitantes usam pedras nucleares chamuscadas. Traga-as para mim em grande quantidade.", progress = nil, completion = nil, rewards = nil},
[8618] = {title = "The Horde Needs More Singed Corestones!", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[8619] = {title = "Alvafunda, o Ancião", objective = "É bom ver que os habitantes desta terra ainda prestam homenagem às raças mais antigas. Eu a saúdo, <name>! Por favor, aceite esta moeda como agradecimento...", description = nil, progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nForce Self – Bow\n\nVocê receberá:\n\nMoeda dos Ancestrais"},
[8620] = {title = "A única prescrição", objective = "Recupere os 8 Capítulos Perdidos de Dracônico para Leigos, combine-os com a Capa Mágica de Livros e entregue o exemplar restaurado de Dracônico para Leigos: Volume II para Narain Suavestilo, em Tanaris.", description = "Eu devia ter imaginado. Meu arqui-inimigo, o Doutor Ívio, aprontando de novo! E agora, graças ao seu fracasso em Hibérnia, o Doutor Ívio destruiu meu livro! Como é que você vai salvar o mundo agora, hein, her<ói/oína>?\n\nO bilhete que eu recebi dizia que o único exemplar conhecido de \"Dracônico para Leigos: Volume II\" foi rasgado em 8 partes e espalhado ao vento! Se você conseguir encontrar esses capítulos perdidos, use essa capa mágica para remontá-los e me devolva.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nTurbante Gnômico do Poder Psíquico"},
[8621] = {title = "Guarda-pés do Tempestário", objective = "Leve os Braceletes do Domínio Qiraji, 2 Ídolos do Sábio, 5 Escaravelhos de Bronze e 5 Escaravelhos de Barro para Kandrostrasz em Ahn'Qiraj. Também é necessário ser <Tolerado/Tolerada> pela Prole de Nozdormu para concluir a missão.", description = "A magia qiraji é antiga e poderosa. Os qirajis mais poderosos empunham itens de um poder tal que seriam capazes de paralisar até mesmo um dragão.\n\nTraga-me os braceletes do domínio qiraji e outros componentes secundários e criarei proteções poderosas para seus pés.\n\nIsso vai ser muito útil na luta contra o inimigo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGuarda-pés do Tempestário"},
[8622] = {title = "Cota do Tempestário", objective = "Leve a Carapaça do Deus Antigo, 2 Ídolos do Sábio, 5 Escaravelhos de Prata e 5 Escaravelhos de Osso para Vethsera em Ahn'Qiraj. Também é necessário ser <Honrado/Honrada> pela Prole de Nozdormu para concluir a missão.", description = "O Deus Antigo, cujo nome não ouso proferir, desperta lentamente de seu sono nos subterrâneos de Ahn'Qiraj. O terrível destino de Azeroth pode se concretizar em questão de dias ou anos; o tempo nada significa para criatura tão poderosa.\n\nQuando as estrelas se mostrarem favoráveis, ele se lançará contra todos os seres vivos com terrível ira. Você precisa encontrá-lo antes disso e descobrir um modo de dar fim à sua existência.\n\nFaça isso e me traga um pedaço da carapaça dele. Eu lhe agraciarei com uma armadura que nenhum mortal jamais vestiu... digna de quem derrotou um deus!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCota do Tempestário"},
[8623] = {title = "Diadema do Tempestário", objective = "Leve o Diadema de Vek'lor, 2 Ídolos do Renascimento, 5 Escaravelhos de Pedra e 5 Escaravelho de Cristal para Andorgos em Ahn'Qiraj. Também é necessário ser <Respeitado/Respeitada> pela Prole de Nozdormu para concluir a missão.", description = "Os Imperadores Gêmeos... seus murmúrios incessantes estão, lentamente, embotando minha razão. Zombam de mim, sabendo que não posso penetrar em seus domínios para salvar meu irmão, Arygos.\n\nEm minhas visões, enxergo seus rostos monstruosos adornados por poderosas coroas. Mas eu terei minha vingança... Submeterei estes símbolos do poder Qiraji à nossa vontade.\n\nTraga-me os componentes de que preciso, <name>, e forjarei uma peça de poder inigualável para proteger sua cabeça.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nDiadema do Tempestário"},
[8624] = {title = "Perneiras do Tempestário", objective = "Leve a Pele do Grande Verme da Areia, 2 Ídolos da Disputa, 5 Escaravelhos de Bronze e 5 Escaravelhos de Marfim para Kandrostrasz em Ahn'Qiraj. Também é necessário ser <Respeitado/Respeitada> pela Prole de Nozdormu para concluir a missão.", description = "Deseja provar seu valor ao meu povo, mortal? Muito bem, talvez possamos nos ajudar mutuamente.\n\nO verme da areia ancestral, Ouroboros, é um ser de lendário poder. Há rumores de que ele foi criado pelo próprio Deus Antigo como um arremedo de vida.\n\nSua pele é extraordinária... espessa, quase impenetrável, porém flexível, permitindo que se mova com a velocidade e graça de uma criatura de porte bem menor.\n\nTraga-me uma amostra da sua pele juntamente com outros componentes secundários, e eu confeccionarei proteções de imenso poder para suas pernas.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPerneiras do Tempestário"},
[8625] = {title = "Ombreiras Enigmáticas", objective = "Leve os Braceletes Qiraji de Dominância, 2 Ídolos do Renascimento, 5 Escaravelhos de Prata e 5 Escaravelhos de Marfim para Andorgos em Ahn'Qiraj. Também é necessário ser <Tolerado/Tolerada> pela Prole de Nozdormu para concluir a missão.", description = "Dizem que as exuberantes ombreiras usadas pelas raças mortais foram uma tentativa de imitar asas de dragão.\n\nTraga-me os braceletes utilizados pelos líderes supremos dos qirajis, e eu os transformarei em proteções de ombros mais terríveis que as asas do próprio Nefarian!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nOmbreiras Enigmáticas"},
[8626] = {title = "Guarda-pés do Atirador", objective = "Leve os Braceletes do Comando Qiraji, 2 Ídolos da Vida, 5 Escaravelhos de Pedra e 5 Escaravelhos de Osso para Kandrostrasz em Ahn'Qiraj. Também é necessário ser <Tolerado/Tolerada> pela Prole de Nozdormu para concluir a missão.", description = "A magia qiraji é antiga e poderosa. Os qirajis mais poderosos empunham itens de um poder tal que seriam capazes de paralisar até mesmo um dragão.\n\nTraga-me os braceletes do domínio qiraji e outros componentes secundários e criarei proteções poderosas para seus pés.\n\nIsso vai ser muito útil na luta contra o inimigo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGuarda-pés do Atirador"},
[8627] = {title = "Peitoral do Vingador", objective = "Leve a Carapaça do Deus Antigo, 2 Ídolos do Sábio, 5 Escaravelhos de Prata e 5 Escaravelhos de Osso para Vethsera em Ahn'Qiraj. Também é necessário ser <Honrado/Honrada> pela Prole de Nozdormu para concluir a missão.", description = "O Deus Antigo, cujo nome não ouso proferir, desperta lentamente de seu sono nos subterrâneos de Ahn'Qiraj. O terrível destino de Azeroth pode se concretizar em questão de dias ou anos; o tempo nada significa para criatura tão poderosa.\n\nQuando as estrelas se mostrarem favoráveis, ele se lançará contra todos os seres vivos com terrível ira. Você precisa encontrá-lo antes disso e descobrir um modo de dar fim à sua existência.\n\nFaça isso e me traga um pedaço da carapaça dele. Eu lhe agraciarei com uma armadura que nenhum mortal jamais vestiu... digna de quem derrotou um deus!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPeitoral do Vingador"},
