[8768] = {title = "Um presente alegremente embrulhado", objective = "O seu nome está escrito neste presente embrulhado com um papel bem colorido.\n\nÊpa! Tem algo se mexendo aí dentro!", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nPresente Alegremente Embrulhado"},
