[8770] = {title = "Alvo: defensores da Colme'Ashi", objective = "Mate 30 Defensores da Colme'Ashi e fale novamente com o Comandante Mar'alith no Forte Cenariano, que fica em Silithus. Você também precisa entregar as Instruções de Combate I para concluir a missão.", description = "Com o aumento do esforço de guerra, a presença dos silitídeos na área oferece uma ameaça crescente às nossas operações. Você foi <selecionado/selecionada> para participar de um ataque à Colme'Ashi e os seus alvos principais serão os defensores da Colme'Ashi.\n\nFale novamente com o comandante Mar'alith quando tiver concluído a sua tarefa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia de Combate Cenariana"},
[8771] = {title = "Alvo: espreitareias da Colme'Ashi", objective = "Mate 30 Espreitareias da Colme'Ashi e fale novamente com o Comandante Mar'alith no Forte Cenariano, que fica em Silithus. Você também precisa entregar as Instruções de Combate II para concluir a missão.", description = "Com o aumento do esforço de guerra, a presença dos silitídeos na área oferece uma ameaça crescente às nossas operações. Você foi <selecionado/selecionada> para participar de um ataque à Colme'Ashi e os seus alvos principais serão os espreitareias da Colme'Ashi.\n\nFale novamente com o comandante Mar'alith quando tiver concluído a sua tarefa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia de Combate Cenariana"},
[8772] = {title = "Alvo: atalaias da Colme'Zora", objective = "Mate 30 Atalaias da Colme'Zora e fale novamente com o Comandante Mar'alith no Forte Cenariano, que fica em Silithus. Você também precisa entregar as Instruções de Combate VI para concluir a missão.", description = "Foram solicitados reforços para se juntarem ao ataque à Colme'Zora e você foi <selecionado/selecionada> para atacar os atalaias da Colme'Zora. Fale novamente com o comandante Mar'alith quando tiver concluído a sua tarefa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia de Combate Cenariana"},
[8773] = {title = "Alvo: aniquiladores da Colme'Zora", objective = "Mate 30 Aniquiladores da Colme'Zora e fale novamente com o Comandante Mar'alith no Forte Cenariano, que fica em Silithus. Você também precisa entregar as Instruções de Combate IV para concluir a missão.", description = "Foram solicitados reforços para se juntarem ao ataque à Colme'Zora e você foi <selecionado/selecionada> para atacar os aniquiladores da Colme'Zora. Fale novamente com o comandante Mar'alith quando tiver concluído a sua tarefa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia de Combate Cenariana"},
