[8775] = {title = "Alvo: cospe-fogos da Colme'Régia", objective = "Mate 30 Cospe-fogos da Colme'Régia e fale novamente com o Comandante Mar'alith no Forte Cenariano, que fica em Silithus. Você também precisa entregar as Instruções de Combate IX para concluir a missão.", description = "Com o aumento do esforço de guerra, a presença dos silitídeos na área oferece uma ameaça crescente às nossas operações. Você foi <selecionado/selecionada> para participar de um ataque à Colme'Régia e os seus alvos principais serão os cospe-fogos aéreos da Colme'Régia.\n\nFale novamente com o comandante Mar'alith quando tiver concluído a sua tarefa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia de Combate Cenariana"},
[8776] = {title = "Alvo: escravizadores da Colme'Régia", objective = "Mate 30 Escravizadores da Colme'Régia e fale novamente com o Comandante Mar'alith no Forte Cenariano, que fica em Silithus. Você também precisa entregar as Instruções de Combate X para concluir a missão.", description = "Com o aumento do esforço de guerra, a presença dos silitídeos na área oferece uma ameaça crescente às nossas operações. Você foi <selecionado/selecionada> para participar de um ataque à Colme'Régia e os seus alvos principais serão os escravizadores da Colme'Régia.\n\nFale novamente com o comandante Mar'alith quando tiver concluído a sua tarefa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia de Combate Cenariana"},
[8777] = {title = "Alvo: entocadores da Colme'Régia", objective = "Mate 30 Entocadores da Colme'Régia e fale novamente com o Comandante Mar'alith no Forte Cenariano, que fica em Silithus. Você também precisa entregar as Instruções de Combate XI para concluir a missão.", description = "Com o aumento do esforço de guerra, a presença dos silitídeos na área oferece uma ameaça crescente às nossas operações. Você foi <selecionado/selecionada> para participar de um ataque à Colme'Régia e os seus alvos principais serão os entocadores da Colme'Régia.\n\nFale novamente com o comandante Mar'alith quando tiver concluído a sua tarefa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia de Combate Cenariana"},
[8778] = {title = "A Brigada de Altaforja vai botar pra explodir!", objective = "Leve 6 Óleos de Imolação, 5 Combustíveis de Foguete Goblínico e 10 Pós Explosivos Densos para a Arcanista Molabico, que está perto de Colme'Zora, em Silithus. Você também precisa entregar as Instruções de Logística IV para completar esta tarefa.", description = "A Brigada de Altaforja fez um pedido bastante incomum de componentes... altamente... voláteis. Colete-os e leve para o Sr. Molabico da Brigada de Altaforja.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística Continuada"},
[8779] = {title = "Matérias-primas de vidência", objective = "Leve 1 Estilhaço Brilhante Grande, 1 Estilhaço Radiante Grande e 1 Esmeralda Enorme para a Geóloga Matarriso, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística V para concluir esta missão.", description = "Os estudos da Geóloga Matarriso mostraram-se inestimáveis em nossas operações em Silithus. Recentemente, ela solicitou matérias-primas de vidência para usar em pesquisas.\n\nPor favor, reúna esses materiais e entregue-os diretamente a ela para que as pesquisas não parem.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística Continuada"},
[8780] = {title = "Kits de armadura para o campo", objective = "Leve 8 Kits de Armadura Rústica e 8 Kits de Armadura Pesada para Jenila Fortemalho no Posto Avançado da Brigada de Altaforja, perto da Colme'Zora, em Silithus. Você também precisa entregar as Instruções de Logística VII para concluir a missão.", description = "Os kits de armadura da Brigada de Altaforja no campo de batalha estão quase acabando. Consiga uma nova leva e entregue-a nas mãos de Jenila Fortemalho, lá no posto avançado da Brigada de Altaforja, nas proximidades da Colme'Zora.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística Continuada"},
[8781] = {title = "Armas para o campo", objective = "Leve 2 Espadas Largas de Lunaço para Jenila Fortemalho no Posto Avançado da Brigada de Altaforja, perto da Colme'Zora. Você também precisa entregar as Instruções de Logística VI para concluir a missão.", description = "A Brigada de Altaforja é um aliado de valor em nossa luta em Silithus. Infelizmente, muitos dos suprimentos deles foram perdidos no caminho. Consiga um lote de armas e entregue a Jenila Fortemalho, perto de Colme'Zora.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística Continuada"},
[8782] = {title = "Suprimentos de uniformização", objective = "Leve 1 Lunatrama, 2 Peças de Runatrama e 1 Seda de Aranha Ferroteia para o Arauto do Vento Chifre Altivo, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística VIII para concluir esta missão.", description = "O último influxo de voluntários causou uma escassez de uniformes. Encontre um lote de matérias-primas para uniformes e leve para o Arauto do Vento Chifre Altivo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística"},
[8783] = {title = "Materiais extraordinários e onde se encontram", objective = "Leve 2 Barras de Tório Encantado e 2 Couros Encantados para Vargus, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística IX para concluir a missão.", description = "Vargus, o ferreiro do Forte Cenariano, precisa de materiais encantados para criar armas poderosas para nossas forças. Consiga esses materiais e entregue nas mãos de Vargus para que as tropas do Forte Cenariano se mantenham bem equipadas.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística"},
[8784] = {title = "Segredos dos Qiraji", objective = "Leve o Artefato Qiraji Antigo aos dragões escondidos perto da entrada do templo.", description = "Você descobriu um artefato qiraji antigo! Talvez um dos dragões nos arredores da entrada do templo encontre uma utilidade para a relíquia.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nFacção Prole de Nozdormu +1000"},
[8785] = {title = "A Legião de Orgrimmar precisa de mojo!", objective = "Leve 6 Mojos Poderosos, 6 Frascos de Mojo Grande e 8 Óleos de Imolação para a Sacerdotisa Sombria Shai, que fica perto de Colme'Régia, em Silithus. Você também precisa entregar as Instruções de Logística IV para completar esta tarefa.", description = "A Legião de Orgrimmar fez um pedido de diversos ingredientes peculiares. Considerando o atual desempenho deles em Silithus, queremos atender esse pedido assim que possível. Reúna o material e leve tudo para a trolesa chamada Shai.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística Continuada"},
[8786] = {title = "Armas para o campo", objective = "Leve 3 Machados Sólidos de Ferro para Merok Passo Longo no acampamento da Legião de Orgrimmar, do lado de fora da Colme'Régia. Você também precisa entregar as Instruções de Logística VI para concluir a missão.", description = "A Legião de Orgrimmar fez mais um pedido de armas, que não podemos atender neste momento. Obtenha as armas que eles solicitaram e entregue a Merok Passo Longo, perto de Colme'Régia.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística Continuada"},
[8787] = {title = "Kits de armadura para o campo", objective = "Leve 8 Kits de Armadura Rústica e 8 Kits de Armadura Pesada para Merok Passo Longo perto de Colme'Régia. Você também precisa entregar as Instruções de Logística VII para concluir a missão.", description = "Os bravos voluntários da Legião de Orgrimmar chegaram a Silithus para contribuir com nossa causa. De alguma maneira, as armaduras deles se perderam na viagem. Obtenha um lote de kits de armadura e entregue para Merok Passo Longo no acampamento da Legião de Orgrimmar.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística Continuada"},
[8788] = {title = "Um presente chacoalhado gentilmente", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nPresente Chacoalhado Gentilmente"},
[8789] = {title = "Armamentos Imperiais Qiraji", objective = "Arygos, no Templo de Ahn'Qiraj, criará Armamentos Infundidos em Elemêntio para você. Para tanto, entregue-lhe Armamentos Imperiais Qirajis e 3 Minérios de Elemêntio.", description = "Não ficamos passivos por um milênio, <name>.\n\nAssistimos essas feras devorarem os irmãos que já não atendiam aos seus propósitos. Escutamos as conspirações para destruir nosso mundo. Soubemos da traição entre eles.\n\nSim, eles têm fraquezas, e nós sabemos como usá-las a nosso favor.\n\nEncontre e traga-me o armamento cerimonial do inimigo. Combinadas ao elemento criado pela volatilidade do próprio deus deles, essas armas serão o motor de nosso ajuste de contas.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nMachado de Guerra Qiraji AbençoadoMosquete Qiraji AbençoadoPugio Qiraji AbençoadoBaluarte Qiraji Abençoado"},
[8790] = {title = "Paramentos Imperiais Qiraji", objective = "Merithra do Sonho, no Templo de Ahn'Qiraj, criará Armamentos Infundidos em Elemêntio para você. Para tanto, entregue-lhe Paramentos Imperiais Qiraji e 3 Minérios de Elemêntio.", description = "A teoria de Arygos é de que o elemento originário dos conflitos entre os Deuses Antigos e seus lacaios também é extremamente letal aos deuses sob condições específicas. Refiro-me ao elemêntio.\n\nEncontre e me traga os paramentos imperiais dos qiraji e o elemento tóxico para que eu possa infundir um poder supremo nas armas.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCajado Qiraji Abençoado do AcólitoCajado Qiraji Abençoado do ÁugureMartelo de Guerra Qiraji Abençoado"},
[8791] = {title = "A queda de Ossirian", objective = "Entregue a Cabeça de Ossirian, o Imaculado, ao Comandante Mar'alith no Forte Cenariano, que fica em Silithus.", description = "Uma terrível ameaça foi silenciada. O cruel Ossirian foi subjugado.\n\nOs habitantes de Kalimdor ficarão aliviados em saber dessa vitória. Leve a cabeça de Ossirian e apresente-a ao Comandante Mar'alith do Forte Cenariano.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPatuá das Areias CambiantesAmuleto das Areias CambiantesGargantilha das Areias CambiantesPingente das Areias Cambiantes\n\nVocê também receberá:\n\n"},
[8792] = {title = "A Horda precisa de você!", objective = "Fale com o Senhor da Guerra Gorchuk no Vale dos Espíritos em Orgrimmar.", description = "Olá, que bom que você resolveu me dar ouvidos. A Horda precisa de toda a ajuda que conseguir para os preparativos de guerra em Ahn'Qiraj. Ou seja, precisamos de você! Enquanto tagarelamos, os coletores oficiais reúnem o material necessário à guerra iminente, mas não seremos capazes de cumprir as metas sem sua ajuda, <name>!\n\nFale com o encarregado, o Senhor da Guerra Gorchuk. Então, <class>? Podemos contar com sua ajuda em preparativos tão vitais?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8793] = {title = "A Horda precisa de você!", objective = "Fale com o Senhor da Guerra Gorchuk no Vale dos Espíritos em Orgrimmar.", description = "Olá, que bom que você resolveu me dar ouvidos. A Horda precisa de toda a ajuda que conseguir para os preparativos de guerra em Ahn'Qiraj. Ou seja, precisamos de você! Enquanto tagarelamos, os coletores oficiais reúnem o material necessário à guerra iminente, mas não seremos capazes de cumprir as metas sem sua ajuda, <name>!\n\nFale com o encarregado, o Senhor da Guerra Gorchuk. Então, <class>? Podemos contar com sua ajuda em preparativos tão vitais?", progress = nil, completion = "Throm'ka, &lt;class&gt;! Que bom ver tantos da Horda, como você, &lt;name&gt;, oferecendo ajuda para estabelecer a base da guerra em Ahn'Qiraj que está por vir. O esforço daqui garantirá nossa vitória contra os silitídeos e seus cruéis soberanos escondidos em Ahn'Qiraj.\n\nEnquanto você estiver aqui, não deixe de falar com os coletores e oferecer ajuda para reunir o material necessário.", rewards = "Você receberá:\n\n"},
[8794] = {title = "A Horda precisa de você!", objective = "Fale com o Senhor da Guerra Gorchuk no Vale dos Espíritos em Orgrimmar.", description = "Olá, que bom que você resolveu me dar ouvidos. A Horda precisa de toda a ajuda que conseguir para os preparativos de guerra em Ahn'Qiraj. Ou seja, precisamos de você! Enquanto tagarelamos, os coletores oficiais reúnem o material necessário à guerra iminente, mas não seremos capazes de cumprir as metas sem sua ajuda, <name>!\n\nFale com o encarregado, o Senhor da Guerra Gorchuk. Então, <class>? Podemos contar com sua ajuda em preparativos tão vitais?", progress = nil, completion = "Throm'ka, &lt;class&gt;! Que bom ver tantos da Horda, como você, &lt;name&gt;, oferecendo ajuda para estabelecer a base da guerra em Ahn'Qiraj que está por vir. O esforço daqui garantirá nossa vitória contra os silitídeos e seus cruéis soberanos escondidos em Ahn'Qiraj.\n\nEnquanto você estiver aqui, não deixe de falar com os coletores e oferecer ajuda para reunir o material necessário.", rewards = "Você receberá:\n\n"},
[8795] = {title = "A Aliança precisa de você!", objective = "Fale com o Marechal-de-campo Nevado na Ala Militar de Altaforja.", description = "Olá, que bom que tu resolveste me escutar. A Aliança precisa de toda a ajuda que conseguir para os preparativos de guerra em Ahn'Qiraj. Ou seja, precisamos de ti! Enquanto mantemos este colóquio, os coletores oficiais reúnem o material necessário à guerra iminente, mas não seremos capazes de cumprir as metas sem tua ajuda, <name>!\n\nFala com o encarregado, o Marechal-de-campo Nevado. Como é, <class>? Podemos contar com tua ajuda em preparativos tão vitais?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8796] = {title = "A Aliança precisa de você!", objective = "Fale com o Marechal-de-campo Nevado na Ala Militar de Altaforja.", description = "Olá, que bom que tu resolveste me escutar. A Aliança precisa de toda a ajuda que conseguir para os preparativos de guerra em Ahn'Qiraj. Ou seja, precisamos de ti! Enquanto mantemos este colóquio, os coletores oficiais reúnem o material necessário à guerra iminente, mas não seremos capazes de cumprir as metas sem tua ajuda, <name>!\n\nFala com o encarregado, o Marechal-de-campo Nevado. Como é, <class>? Podemos contar com tua ajuda em preparativos tão vitais?", progress = nil, completion = "Salve e buenas guri! Que bom ver tantos da Aliança, como você, &lt;name&gt;, oferecendo ajuda para estabelecer a base da guerra iminente em Ahn'Qiraj. O esforço daqui garantirá nossa vitória contra os silitídeos e seus cruéis soberanos escondidos em Ahn'Qiraj.\n\nEnquanto você estiver aqui, não deixe de falar com os coletores e oferecer ajuda para reunir o material necessário.", rewards = "Você receberá:\n\n"},
[8797] = {title = "A Aliança precisa de você!", objective = "Fale com o Marechal-de-campo Nevado na Ala Militar de Altaforja.", description = "Olá, que bom que tu resolveste me escutar. A Aliança precisa de toda a ajuda que conseguir para os preparativos de guerra em Ahn'Qiraj. Ou seja, precisamos de ti! Enquanto mantemos este colóquio, os coletores oficiais reúnem o material necessário à guerra iminente, mas não seremos capazes de cumprir as metas sem tua ajuda, <name>!\n\nFala com o encarregado, o Marechal-de-campo Nevado. Como é, <class>? Podemos contar com tua ajuda em preparativos tão vitais?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8798] = {title = "Um yeti para chamar de seu", objective = nil, description = nil, progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nYeti Mecânico Pacato"},
[8799] = {title = "O herói da ocasião", objective = "Deposite 1 punhado de Sal Rochafunda e 5 moedas de ouro em qualquer Secadora de Azevinho para receber 5 Azevinhos Secos.", description = "Veja bem... Eu não deveria estar lhe contando isso, mas já que você é <o herói/a heroína> da ocasião e coisa e tal...\n\nNesta época do ano, a gente encontra azevinho em qualquer lugar, mas essa frutinha não dura nada. Nós usamos uma máquina secadora de azevinho pras bagas não estragarem. Usamos isso para apimentar as festas... e os lucros durante o verão!\n\nEu deixo você usar a secadora, mas só se você for <um/uma> mestre <cozinheiro/cozinheira>. Você vai precisar de um pouco de sal rochafunda e duas, três... hum, digamos cinco moedas de ouro. Isso vai garantir uma boa partida de azevinho para você guardar.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAzevinho Seco"},
[8800] = {title = "Equipamento de batalha do Forte Cenariano", objective = "Apresente-se a Vargus no Forte Cenariano, em Silithus.", description = "Conforme você completar as tarefas que recebeu na guerra, converse com Vargus, o ferreiro. Mostre-lhe todos os distintivos que você tiver ganhado ao longo do caminho. Ele irá lhe fornecer o equipamento compatível ao seu nível de comprometimento com a nossa causa.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[8801] = {title = "O legado de C'Thun", objective = "Leve o Olho de C'Thun para Celestrasz no Templo de Ahn'Qiraj.", description = "Os muros de Ahn'Qiraj estremecem. Um poder maligno, mais antigo que o próprio mundo, foi destruído.\n\nA visão dos vestígios da aberração colossal quase lhe congela o coração. Mesmo morto, o legado maligno de C'Thun continua presente no ar.\n\nVocê fez o que julgavam impossível.\n\nVocê recolhe um pedúnculo do demônio, alojando-o na mochila.\n\nLeve o Olho de C'Thun aos dragões no aposento seguinte.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8802] = {title = "O salvador de Kalimdor", objective = "Leve o Olho de C'Thun para Anacronos nas Cavernas do Tempo.", description = "Siga rumo às Cavernas do Tempo, <meu senhor/minha senhora>. Anacronos aguarda seu retorno. Entregue-lhe o olho de C'Thun, pois que, de certo, ele desejará agregá-lo à coleção do seu amo.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nAmuleto do Deus CaídoManto do Deus CaídoAnel do Deus Caído\n\nO seguinte feitiço será lançado em você:\n\nFacção Prole de Nozdormu +1000\n\nVocê também receberá:\n\n"},
[8803] = {title = "Um presente festivo", objective = "Uma pequena etiqueta no presente diz:\n\nPara: <name>\n\nQue este presente ajude-o a espalhar alegria e esperança por toda Azeroth.\n\nAssinado: Papai Inverno", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nPresente Festivo"},
[8804] = {title = "Kits de sobrevivência no deserto", objective = "Leve 4 Globos de Água, 4 Antipeçonhas Poderosos e 4 Bolinhos Defumados do Deserto para Calandrath na estalagem do Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística I para completar esta tarefa.", description = "Com a chegada dos novos recrutas ao Forte Cenariano, vem o problema de como mantê-los vivos no deserto. A desidratação e as diversas criaturas venenosas representam uma séria ameaça a voluntários inexperientes. Reúna os suprimentos necessários para um kit de sobrevivência no deserto e leve-os para Calandrath no Forte Cenariano.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística"},
[8805] = {title = "Botas para a guarda", objective = "Leve 3 Botas Ornadas de Mithril para Vish Kozus, Capitão da Guarda, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística II para concluir a missão.", description = "Botas ornadas de mithril andam escassas para a divisão montada da guarda do Forte Cenariano. Providencie um lote e entregue imediatamente ao capitão Vish Kozus no Forte Cenariano.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística"},
[8806] = {title = "Pedras de amolar para a guarda", objective = "Leve 10 Pedras de Amolar Densas, 10 Pedras de Amolar Maciças e 10 Pedras de Amolar Pesadas para Vish Kozus, Capitão da Guarda, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística III para concluir a missão.", description = "Pedras de amolar andam escassas para a guarda do Forte Cenariano. Providencie um lote e entregue imediatamente ao capitão Vish Kozus no Forte Cenariano.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística"},
[8807] = {title = "Matérias-primas de vidência", objective = "Leve 1 Estilhaço Brilhante Grande, 1 Estilhaço Radiante Grande e 1 Esmeralda Enorme para a Geóloga Matarriso, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística V para concluir esta missão.", description = "Os estudos da Geóloga Matarriso mostraram-se inestimáveis em nossas operações em Silithus. Recentemente, ela solicitou matérias-primas de vidência para usar em pesquisas.\n\nPor favor, reúna esses materiais e entregue-os diretamente a ela para que as pesquisas não parem.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística Continuada"},
[8808] = {title = "Suprimentos de uniformização", objective = "Leve 1 Lunatrama, 2 Peças de Runatrama e 1 Seda de Aranha Ferroteia para o Arauto do Vento Chifre Altivo no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística VIII para cumprir esta missão.", description = "O último influxo de voluntários causou uma escassez de uniformes. Encontre um lote de matérias-primas para uniformes e leve para o Arauto do Vento Chifre Altivo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística"},
[8809] = {title = "Materiais extraordinários e onde se encontram", objective = "Leve 2 Barras de Tório Encantado e 2 Couros Encantados para Vargus, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística IX para concluir a missão.", description = "Vargus, o ferreiro do Forte Cenariano, precisa de materiais encantados para criar armas poderosas para nossas forças. Consiga esses materiais e entregue nas mãos de Vargus para que as tropas do Forte Cenariano se mantenham bem equipadas.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística"},
[8810] = {title = "Bandagens para o campo", objective = "Leve 30 Bandagens Grossas de Runatrama, 30 Bandagens Grossas de Seda e 30 Bandagens Grossas de Magitrama para o Arauto do Vento Chifre Altivo, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística X para concluir esta missão.", description = "Nosso suprimento de bandagens para o campo de batalha está arriscadamente escasso depois das últimas baixas que sofremos. Consiga um lote de suprimentos de primeiros socorros e entregue-os ao Arauto do Vento Chifre Altivo no Forte Cenariano.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia Logística CenarianaMissão de Logística Continuada"},
