[8821] = {title = "Dez sinetes de comenda", objective = "Aceito sinetes de comenda de todos os aventureiros que os receberam por trabalhos realizados. Para cada lote de dez que entregar, me certificarei de que teus esforços sejam devidamente recompensados e reconhecidos por Altaforja. Aceito também um sinete por vez, mas a taxa de recompensa será menor. Estamos interessados em proezas grandiosas, mas nenhum feito passará em branco.\n\nTendo dito isso, ficarei feliz em recolher teus sinetes, se desejar entregá-los em lotes.", description = nil, progress = "Aceito sinetes de comenda de todos os aventureiros que os receberam por trabalhos realizados. Para cada lote de dez que entregar, me certificarei de que teus esforços sejam devidamente recompensados e reconhecidos por Altaforja. Aceito também um sinete por vez, mas a taxa de recompensa será menor. Estamos interessados em proezas grandiosas, mas nenhum feito passará em branco.\n\nTendo dito isso, ficarei feliz em recolher teus sinetes, se desejar entregá-los em lotes.", completion = "Excelente! Não tenho dúvidas de que foi com muito sacrifício que tu conseguiste estes sinetes. Não te preocupa, pois todo o teu esforço será devidamente reconhecido e recompensado. São aventureiros como você que fazem a diferença, &lt;name&gt;.\n\nProcure-me caso tenha mais sinetes para entregar. Ficarei feliz em trocá-los.", rewards = nil},
[8822] = {title = "Dez sinetes de comenda", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[8823] = {title = "Dez sinetes de comenda", objective = "Aceito sinetes de comenda de todos os aventureiros que os receberam por trabalhos realizados. Para cada lote de dez que me entregares, me certificarei de que seus esforços sejam devidamente registrados e recompensados por parte de Orgrimmar. Também aceito um sinete por vez, mas a taxa de recompensa será menor. Estamos interessados em grandes feitos, mas nenhum feito passará em branco.\n\nTendo dito isso, vou recolher com orgulho os sinetes, caso queira entregá-los em lotes.", description = nil, progress = "Aceito sinetes de comenda de todos os aventureiros que os receberam por trabalhos realizados. Para cada lote de dez que me entregares, me certificarei de que seus esforços sejam devidamente registrados e recompensados por parte de Orgrimmar. Também aceito um sinete por vez, mas a taxa de recompensa será menor. Estamos interessados em grandes feitos, mas nenhum feito passará em branco.\n\nTendo dito isso, vou recolher com orgulho os sinetes, caso queira entregá-los em lotes.", completion = "Excelente! Não tenho dúvidas de que foi com muito sacrifício que você conseguiu estes sinetes. Não se preocupe, pois todo o seu esforço será devidamente reconhecido e recompensado. São aventureiros como você que fazem a diferença, &lt;name&gt;.\n\nVolte a mim caso tenha mais sinetes para entregar. Ficarei feliz em trocá-los.", rewards = nil},
