[8921] = {title = "O destilador ectoplásmico", objective = "Leve para o Mux Zoamana, em Geringontzan, 1 Conversor Delicado de Arcanita, 4 Essências Eternas Maiores, 10 unidades de Óleo de Petrescama, 25 Cinzas Vulcânicas e 40 peças de ouro.", description = "Ah, você precisa da minha última invenção... o revelador extradimensional de fantasmas! Infelizmente fiquei sem dinheiro e sem ajudantes.\n\nVocê quer ajudar? Excelente! A primeira tarefa é criar uma ferramenta para colher a matéria da morte! Montar um destilador ectoplasmático não vai ser fácil, <name>.\n\nPreciso que você me traga um conversor de arcanita, umas essências eternas maiores e óleo de petrescama. Também vou precisar de um punhado de cinzas vulcânicas das poças de lava nas Estepes Ardentes para fazer um filtro.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMercadorias de Qualidade de Mux"},
[8922] = {title = "Um dispositivo sobrenatural", objective = "Leve o Recipiente Lacrado de Sangue para Mux Zoamana em Geringontzan, em Tanaris.", description = "Preciso encontrar uma maneira de falar com Anthion Harmon... ou, mais especificamente, com o fantasma dele!\n\nConheço um engenheiro que tem um estranho interesse pelo sobrenatural. Ele diz que pode construir uma máquina para falar com os mortos!\n\nO sangue que você coletou era para ele. Vá a Geringontzan e leve para ele como pagamento por seus serviços!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8923] = {title = "Um dispositivo sobrenatural", objective = "Leve o Recipiente de Peçonha Lacrado para Mux Zoamana em Geringontzan.", description = "Você deve ser corajos<o/a> mesmo para ter continuado por aqui depois daquilo. Ou isso, ou eu despertei o seu interesse com a peça de armadura que eu lhe dei. Tem mais de onde ela veio se você ainda estiver interessad<o/a> em me ajudar.\n\nAs amostras de peçonha que você colheu são para um conhecido meu, Mux Zoamana, um engenheiro que se interessa pelo sobrenatural. O último projeto dele é um dispositivo que permite a comunicação com os mortos! Minha última esperança é falar com Anthion e descobrir mais sobre a minha situação.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8924] = {title = "Em busca de ectoplasma", objective = "Use o Destilador Ectoplásmico perto de mortos-vivos incorpóreos para coletar 12 Ectoplasmas Chamuscados em Silithus, 12 Ectoplasmas Congelados em Hibérnia e 12 Ectoplasmas Estáveis nas Terras Pestilentas Orientais. Leve-os juntamente com o Destilador Ectoplásmico para Mux Zoamana em Geringontzan.", description = "Uma bela obra de engenharia, se me permite dizer! Mas você vai precisar bater perna pra achar os mortos-vivos incorpóreos de que a gente precisa.\n\nSabe, climas diferentes dão consistências diferentes pro tecido espectral dos mortos. Os elfos noturnos atormentados de Silithus, os espíritos dos altaneiros de Hibérnia e os fantasmas e banshees das Terras Pestilentas Orientais devem nos dar os tipos de ectoplasma necessários. Só não esqueça de colocar o destilador por perto antes de colher as amostras!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8925] = {title = "Fonte de energia portátil", objective = "Encontre Bokk, Senhor do Magma, nas Estepes Ardentes, obtenha o Núcleo de Magma dele e leve-o para Mux Zoamana em Geringontzan.", description = "Agora a gente tem que achar uma fonte de energia estável e portátil pra manter o ectoplasma funcionando. Eu ouvi umas histórias de um elemental poderoso, um tal de Bokk, Senhor do Magma, com um coração que é um núcleo de magma poderoso. Seria uma fonte de calor infinita!\n\nDizem que ele fica lá pras Estepes Ardentes, ao sul da Montanha Rocha Negra. Traz lá o núcleo de magma que vai dar pra fazer o revelador de fantasmas extradimensional funcionar!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8926] = {title = "Recompensa justa", objective = "Leve um Cinto do Coração Selvagem e um par de Luvas do Coração Selvagem para Deliana em Altaforja.", description = "Você cumpriu sua palavra e trouxe o dispositivo construído por Mux. Creio que, se for para estimular seu interesse em salvar a minha pele, isso peça uma recompensa. Traga o cinto e um par de luvas que você queira aprimorar. Prometo que as que eu lhe der depois serão muito superiores.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nCinto do Coração FeralLuvas do Coração Feral"},
[8927] = {title = "Recompensa justa", objective = "Leve um Cinto do Coração Selvagem e um par de Luvas do Coração Selvagem para Mokvar em Orgrimmar.", description = "Você se provou deveras confiável, <name>. Por mais que eu confie que você me auxiliará nesse dilema, seria sábio recompensá-l<o/a> conforme seu desempenho.\n\nPor enquanto eu fornecerei seu cinto e suas luvas. Se sobrevivermos até lá, farei o restante das peças que estiverem disponíveis para você.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nCinto do Coração FeralLuvas do Coração Feral"},
[8928] = {title = "Comerciante ardiloso", objective = "Procure um diabrete em uma caverna na entrada da Garganta do Sussurro Sombrio, no sul de Hibérnia, compre um Bastão de Elemental Vil e fale novamente com Mux Zoamana em Geringontzan.", description = "O núcleo vai render o suficiente pra fazer o dispositivo aqui rodar. Agora falta construir um bastão que aguente essa energia sem fundir.\n\nEm Hibérnia eu conheci um diabrete que vendia tudo de exótico. Ele tinha um bastão de elemental que serviria perfeitamente. Mas eu não comprei, e agora tô aqui arrancando os cabelos que não tenho.\n\nEle mora numa caverna na entrada da Garganta do Sussurro Sombrio. Chega lá e tenta achar o cara.\n\nE não deixa ele te cobrar mais de 40 peças de ouro! Foi a cotação que ele me deu na época.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8929] = {title = "À procura de Anthion", objective = "Use o Revelador Extradimensional de Fantasmas na entrada de Stratholme e fale com o fantasma de Anthion Harmon.\n\nItem fornecido:\n", description = "Bem, agora só temos pôr à prova a invenção do goblin. Dizem que Anthion foi abatido pelas legiões de mortos-vivos de Stratholme ao tentar passar pelos portões da cidade.\n\nTente usar o dispositivo lá e veja se consegue descobrir o que Anthion sabia.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8930] = {title = "À procura de Anthion", objective = "Use o Revelador Extradimensional de Fantasmas na entrada de Stratholme e fale com o fantasma de Anthion Harmon.\n\nItem fornecido:\n", description = "Muito bem, <name>. Agora só temos pôr à prova a invenção do goblin. Dizem que Anthion foi abatido pelas legiões de mortos-vivos de Stratholme ao tentar passar pelos portões da cidade.\n\nTente usar o dispositivo lá e veja se consegue mesmo falar com os mortos!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
