[8956] = {title = "As últimas palavras de Anthion", objective = "Fale novamente com Deliana em Altaforja com um conjunto de Botas da Arte Sombria, Calças da Arte Sombria e Espaldares da Arte Sombria.", description = "Esse amuleto contém a alma de Valthalak. Nossa ganância cega fez com que tomássemos a péssima decisão de dividir o amuleto em três, sem saber que seríamos vitimados por uma maldição.\n\nO único jeito de conter o feitiço de Valthalak é unir as peças do medalhão. Fale novamente com Deliana e diga a ela que Bodley é a única esperança para encontrar os pedaços que faltam. Fale também sobre qualquer recompensa que você tenha negociado.\n\nEu, por fim, hei de descansar em paz. Desejo toda a sorte do mundo a você, pois você vai precisar!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBotas do Manto NegroEspaldares do Manto NegroCalças do Manto Negro"},
[8957] = {title = "As últimas palavras de Anthion", objective = "Fale novamente com Mokvar, em Orgrimmar, com um conjunto de Botas dos Elementos, Kilt dos Elementos e Brafoneiras dos Elementos.", description = "Esse amuleto contém a alma de Valthalak. Nossa ganância cega fez com que tomássemos a péssima decisão de dividir o amuleto em três, sem saber que seríamos vitimados por uma maldição.\n\nO único jeito de conter o feitiço de Valthalak é unir as peças do amuleto. Fale novamente com Mokvar e diga a ele que Bodley é a única esperança para encontrar os pedaços que faltam. Fale também sobre qualquer recompensa que você tenha negociado.\n\nEu, por fim, hei de descansar em paz. Desejo toda a sorte do mundo a você, pois você vai precisar!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBotas dos Cinco TrovõesKilt dos Cinco Trovões"},
[8958] = {title = "As últimas palavras de Anthion", objective = "Fale novamente com Deliana em Altaforja, com um conjunto de Sandálias de Brumedo, Perneiras de Brumedo e Dragonas de Brumedo.", description = "Esse amuleto contém a alma de Valthalak. Nossa ganância cega fez com que tomássemos a péssima decisão de dividir o amuleto em três, sem saber que seríamos vitimados por uma maldição.\n\nO único jeito de conter o feitiço de Valthalak é unir as peças do medalhão. Fale novamente com Deliana e diga a ela que Bodley é a única esperança para encontrar os pedaços que faltam. Fale também sobre qualquer recompensa que você tenha negociado.\n\nEu, por fim, hei de descansar em paz. Desejo toda a sorte do mundo a você, pois você vai precisar!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nSandálias BrumortePerneiras Brumorte"},
[8959] = {title = "As últimas palavras de Anthion", objective = "Fale novamente com Deliana em Altaforja, com um conjunto de Botas do Valor, Coxotes do Valor e Espaldares do Valor.", description = "Esse amuleto contém a alma de Valthalak. Nossa ganância cega fez com que tomássemos a péssima decisão de dividir o amuleto em três, sem saber que seríamos vitimados por uma maldição.\n\nO único jeito de conter o feitiço de Valthalak é unir as peças do medalhão. Fale novamente com Deliana e diga a ela que Bodley é a única esperança para encontrar os pedaços que faltam. Fale também sobre qualquer recompensa que você tenha negociado.\n\nEu, por fim, hei de descansar em paz. Desejo toda a sorte do mundo a você, pois você vai precisar!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBotas do HeroísmoEspaldares do HeroísmoCoxotes do Heroísmo"},
[8960] = {title = "O infeliz destino de Bodley", objective = "Vá até a Montanha Rocha Negra e use o Revelador Extradimensional de Fantasmas para encontrar Bodley próximo ao Pico da Rocha Negra.", description = "Então precisamos recuperar o resto do medalhão? Encontrar Bodley vai ser uma dor de cabeça. Ele voltou para a Montanha Rocha Negra para tentar entrar de novo no Pico da Rocha Negra e nunca mais voltou.\n\nEu sugiro que você leve o dispositivo revelador de fantasmas do goblin. Receio que tenha acontecido o pior.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8961] = {title = "Três reis das chamas", objective = "Colete a Incendicita de Incendius, a Brasa do Mirabrasa e a Cinza das Cinzas, além de um Braseiro Santificado, e fale novamente com Bodley na Montanha Rocha Negra.", description = "Para evocar Lorde Valthalak, primeiro vamos ter que juntar os outros dois terços do amuleto. Para fazer isso, vamos precisar de um braseiro e de um tipo especial de carvão.\n\nO que precisamos está em posse de três nobres do fogo: de Lorde Incendius no Abismo Rocha Negra, do Piroguarda Mirabrasa no Pico da Rocha Negra e do Duque das Cinzas em Silithus. Traga-me os restos mortais deles, depois um Braseiro Santificado, que é possível comprar se você for considerado honrad<o/a> pela Aurora Argêntea.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8962] = {title = "Componentes importantes", objective = "Obtenha Restos Mortais Druídicos e os leve para Bodley na Montanha Rocha Negra.", description = "Atenção, <class>. Agora que a gente tem o braseiro e um jeito de acender ele, falta um componente pra aspergir, pra fornecer um foco forte o suficiente pra instigar o espírito do meu antigo companheiro, que se foi.\n\nMor Casco Gris, nosso druida mais proeminente, era amigo próximo de um monte de gente no Círculo Cenariano, e alguns desses amigos dele foram mortos pelos silitídeos. Preciso que você recupere os Restos Mortais Druídicos que estão com os silitídeos que habitam a região de Colme'Régia, a sudeste de Silithus.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8963] = {title = "Componentes importantes", objective = "Obtenha uma Relíquia do Vilarejo Brisastral e leve-a para Bodley na Montanha Rocha Negra.", description = "<class>, escute bem. Agora que já temos o braseiro e como acendê-lo, só falta um componente para aspergir o braseiro, algo que vá proporcionar um fogo forte o suficiente para atiçar o espírito da minha falecida companheira.\n\nIsalien, nossa sacerdotisa, cresceu no Vilarejo Brisastral, em Hibérnia. Você vai ter que pegar uma relíquia do Vilarejo Brisastral dos gigantes Friomalho, na Garganta dos Sussurros Gelados, na parte sul de Hibérnia. De tempos em tempos eles vão atacar o norte.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8964] = {title = "Componentes importantes", objective = "Obtenha uma Espada Brilhante do Fanatismo e leve-a para Bodley na Montanha Rocha Negra.", description = "<class>, escute bem. Agora que já temos o braseiro e como acendê-lo, só falta um componente para alimentá-lo. Algo que confira um fogo forte o suficiente para evocar o espírito dos meus falecidos companheiros.\n\nOs irmãos Jil e Soeiro apoiavam os princípios da Cruzada Escarlate. Eles queriam se tornar cavaleiros naquela organização pútrida. Vá à Manopla de Tyr, nas Terras Pestilentas Orientais, e pegue uma Espada Brilhante do Fanatismo dos pretorianos que ficam por lá.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8965] = {title = "Componentes importantes", objective = "Obtenha Cinzas de Alma Banida e leve-as para Bodley, dentro da Montanha Rocha Negra.", description = "<class>, escute bem. Agora que já temos o braseiro e como acendê-lo, só falta um componente para alimentá-lo. Algo que confira um fogo forte o suficiente para atrair o espírito do meu falecido camarada de outrora.\n\nKormok, um ogro necromante que viajava com a gente, gostava de evocar as almas dos mortos banidos da Ilha da Purgação, na costa ao sul do Contraforte de Eira dos Montes. Vá até lá, procure cinzas de alma banida entre os restos mortais e traga para mim.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8966] = {title = "A parte esquerda do amuleto do Lorde Valthalak", objective = "Use o Braseiro de Sinalização para evocar o espírito de Mor Casco Gris e então mate-o. Fale novamente com Bodley na Montanha Rocha Negra, com a Parte Esquerda do Amuleto do Lorde Valthalak e o Braseiro de Sinalização.", description = "Meu amigo Mor Casco Gris foi a primeira vítima a ser possuída pelo espírito corrupto que vive no amuleto de Lorde Valthalak. Nós roubarmos o amuleto e o grimório de Valthalak e fugimos do Pico da Rocha Negra para não acabarmos mortos. No meio do caminho, Mor caiu no sopé do pico.\n\nDepois descobrimos que ele tinha sobrevivido à queda, porém foi capturado e torturado pelo Senhor da Guerra Voone. Uns espectros assassinos invadiram o lugar e o mataram bem na frente de Voone.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8967] = {title = "A parte esquerda do amuleto do Lorde Valthalak", objective = "Use o Braseiro de Sinalização para evocar o espírito de Isalien e então mate-o. Fale novamente com Bodley na Montanha Rocha Negra, com a Parte Esquerda do Amuleto do Lorde Valthalak restaurado e o Braseiro de Sinalização.", description = "Posteriormente chegou ao nosso conhecimento, enquanto fazíamos o possível para sobreviver, que Isalien, uma sacerdotisa elfa noturna do nosso grupo, tinha partido para a antiga cidade noctiélfica onde hoje fica o Gládio Cruel. Até onde sabemos, ela tinha ido lá por motivos pessoais, mas também para revelar os segredos da parte do amuleto que estava em posse dela.\n\nMas não chegou às vias de fato. Ficamos sabendo que ela foi emboscada no Altar de Eldretharr por Azzin, o Selvamorfo, e suas tropas.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8968] = {title = "A parte esquerda do amuleto do Lorde Valthalak", objective = "Use o Braseiro de Sinalização para evocar os espíritos de Jil e Soeiro e então mate os dois. Fale novamente com Bodley na Montanha Rocha Negra, com a Parte Esquerda do Amuleto do Lorde Valthalak e o Braseiro de Sinalização.", description = "Os irmãos Jil e Soeiro decidiram se alistar na Cruzada Escarlate assim que desertaram do nosso bando mercenário. Levaram consigo a parte esquerda do amuleto do Lorde Valthalak. Normalmente eu daria \"tchau e bênção\", mas precisamos da peça de volta.\n\nAté onde sabemos, eles morreram pelas mãos do próprio Grão-Cruzado Dathrohan por terem fracassado no rito de iniciação, nos aposentos dele, dentro do Bastião Escarlate.\n\nEm todo caso, nós... e por \"nós\" entenda-se \"você\", vamos ter que recuperar aquela parte do amuleto de qualquer forma.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8969] = {title = "A parte esquerda do amuleto do Lorde Valthalak", objective = "Use o Braseiro de Sinalização para evocar o espírito de Kormok e então mate-o. Fale novamente com Bodley, na Montanha Rocha Negra, com a Parte Esquerda do Amuleto do Lorde Valthalak e o Braseiro de Sinalização.", description = "Agora que já temos o que queríamos, é hora de explicar qual será a entidade evocada.\n\nKormok até que era aceitável para os padrões de um ogro, isso até se interessar por necromancia. Depois disso, a coisa ficou feia para todo mundo. Não foi surpresa para ninguém quando ele pegou o amuleto e se enfurnou na sua nova casa: Scolomântia.\n\nMas nem lá... ou muito menos lá, pensando bem, ele estava a salvo dos assassino espectrais. Mataram-no bem na frente do mestre, Ras Friomúrmuro.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[8970] = {title = "Eu vejo a Ilha Alcaz no seu futuro...", objective = "Colete 20 Algassangues das nagas Strashaz e fale novamente com Bodley na Montanha Rocha Negra.", description = "Como eu dizia, <class>, na época que ainda era vivo eu flertei com a adivinhação. Mas não é um ramo muito lucrativo, como se pode imaginar, principalmente por ser perigoso, além dos reagentes serem raros e caros. Mas entendo do assunto bem o bastante para localizar a última peça do amuleto e o espírito do meu velho companheiro.\n\nÉ aí que você entra, <name>. Vou precisar que você vá ao mar que banha o Pântano Vadeoso, na Ilha Alcaz, para colher algassangue das nagas Strashaz que moram na região.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nTranse Divinatório\n\nVocê receberá:\n\n"},
