[9206] = {title = "Escritura do Artífice – Enguia Elétrica", objective = "Leve 30 Enguias Elétricas e a Escritura do Artífice – Enguia Elétrica – para o Caravaneiro Esmagapedra na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais.", description = "O portador deste édito tem direito a pagamento na forma de uma insígnia à sua escolha após o aviamento da ordem de serviço contratada.\n\nOrdem de serviço FS-9:\n\n30 Enguias Elétricas.\n\nTodas as ordens aviadas devem ser entregues ao Caravaneiro Esmagapedra na Capela Esperança da Luz, nas Terras Pestilentas Orientais.\n\nO contrato é nulo em caso de adulteração ou danos.\n\nAss: Comandante de Expedição Metz, da Aurora ArgênteaVocê vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nInsígnia da CruzadaInsígnia da Aurora"},
[9211] = {title = "A Guardafria", objective = "Mataus, o Arauto da Ira, na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais, quer 10 Insígnias da Cruzada e 30 peças de ouro.", description = "Ah, a Guardafria! É uma camada fina de proteção mágica que cobre certas peças de armadura. Quem lutar contra criaturas do frio e do gelo estará protegido dos seus ataques pela Guardafria.\n\nComo tudo que é feito pelo Arauto da Ira, isso tem um preço... um preço que não vou baixar por mais que você chore. Interessad<o/a>?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGuardafria"},
[9213] = {title = "O Guardassombra", objective = "Mataus, o Arauto da Ira, na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais, quer 10 Insígnias da Cruzada e 30 peças de ouro.", description = "Uma obra de gênio! Como o Guardafria, o Guardassombra emprega uma fina camada de magia protetora que cobre determinadas partes do equipamento. E como o Guardafria, o Guardassombra vai lhe custar uma boa soma em dinheiro.\n\n<Mataus junta as mãos e esfrega os dedos uns nos outros.>\n\nInteressa?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGuardassombra"},
[9221] = {title = "Armamentos de Batalha Superiores: Aliados da Aurora", objective = "Por 30 Insígnias da Aurora e 30 Insígnias da Cruzada, você pode escolher um item no tesouro da Aurora Argêntea.", description = "Saudações, <amigo/amiga>. Trouxe insígnias da Aurora ou da Cruzada para trocar por itens de poder?\n\nEsteja ciente: a reputação de respeitad<o/a> dentro da Aurora vem com taxas altíssimas nos produtos. Eu aconselho melhorar sua reputação dentro da Aurora antes de levar o plano adiante.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nElo da DevoçãoElo da ResoluçãoÚltimo Recurso de VerimondeSacola de SuprimentosPerneiras do Caçador de PestesElmo Santificado de Couro"},
[9222] = {title = "Armamentos Épicos de Batalha: Aliados da Aurora", objective = "Por 110 Insígnias da Aurora e 110 Insígnias da Cruzada, você pode escolher um item no tesouro da Aurora Argêntea.", description = "Saudações, <amigo/amiga>. Trouxe insígnias da Aurora ou da Cruzada para trocar por itens de grande poder?\n\nEsteja ciente: compras feitas na reputação de respeitad<o/a> dentro da Aurora vêm com taxas altíssimas nos produtos. Aconselho melhorar sua reputação dentro da Aurora antes de levar o plano adiante.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nAmuleto da AuroraBraçadeiras da EsperançaBraceleiras do SubterfúgioMedalhão da AuroraTalismã da AscendênciaO Purificador"},
[9233] = {title = "Manual de Omarion", objective = "Leve o Manual de Omarion ao Artesão Wilhelm na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais.", description = "Wilhelm,\n\nSe estás lendo isto é porque meu caneco secou — aleluia. Quero que tu saibas que parti desta terra atucanado com a tua bocabertice na hora de fazer comércio. Mas pior que o portador deste manual é mais bocaberta ainda que tu pra criação.\n\nA respeito do livro: estas aqui são o arremate das minhas receitas. Que o portador deste livro crie o que lhe pedirem. Estou certo de que tu cobrarás até os pelos da barba pelos teus serviços, pois que tu és um baita dum guampudo.\n\nCom desdém,\n\nOmarionO seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nSegredos de Omarion"},
[9247] = {title = "O chamado do guardião", objective = "Devolva a Carta para o Guardião dos Pergaminhos na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais.", description = "<name>,\n\nNo passado você serviu à Aurora Argêntea, sempre doando o melhor de si. Agora, mais uma vez, pedimos ajuda em um tempo de grandes ameaças.\n\n<name>, se você estiver <disposto/disposta> a nos ajudar novamente, apresente-se ao Guardião dos Pergaminhos na Capela Esperança da Luz, que fica na Terras Pestilentas Orientais.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nÓleo Abençoado de TeurgoPedra de Afiar Consagrada\n\nVocê também receberá:\n\n"},
[9249] = {title = "40 Cupons – Diagrama: Controlador de Tonque a Vapor", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nDiagrama: Controlador de Tonque a Vapor"},
[9250] = {title = "Cajado de Atiesh", objective = "Encontre alguém que ajude você a recuperar as duas últimas peças e a livrar o cajado do mal.", description = "Terminada a armação, Atiesh está quase completo. Faltam apenas a base e o cabeçote do cajado. E há também o pequeno detalhe do cajado ser um condutor do mal de Sargeras.\n\nEncontre alguém que possa <ajudá-lo/ajudá-la> a encontrar as partes restantes e a exorcizar o mal do cajado.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[9259] = {title = "Um duas-caras na Vela Sangrenta", objective = nil, description = nil, progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nReputação – Angra do Butim +500"},
[9260] = {title = "Investigação do Flagelo em Ventobravo", objective = "Colete três Pedras Necróticas Opacas dos membros do Flagelo no entorno de Ventobravo e investigue os círculos rúnicos perto da base do Flagelo.", description = "O Flagelo está bem na nossa porta, não podemos ficar aqui parados! Você vai ajudar a defender Ventobravo desses malditos cadáveres invasores, não vai? \n\nLá fora, há vários círculos rúnicos estranhos no chão, brilhando com a mesma energia que envolve os mortos-vivos e a Necrópole. Esses sinais têm algum significado, tenho certeza, e é isso que quero que você descubra. Enquanto investiga os círculos, aproveite e mate todos os membros do Flagelo que puder. Se você trouxer provas das mortes, eu <o/a> recompensarei. Vá!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
