[9319] = {title = "Uma luz na escuridão", objective = "Encontre e toque as fogueiras no Pico da Rocha Negra, no Gládio Cruel, em Scolomântia e em Stratholme. Em seguida, fale novamente com o Guardachamas do Festival.", description = "Nós não somos os únicos a celebrar o solstício, <name>. As chamas do festival chegam a todas as criaturas e culturas de Azeroth! Elas estão sendo acesas mesmo nos lugares mais sombrios!\n\nSe quiser ver por si <mesmo/mesma>, procure as fogueiras em Stratholme, em Scolomântia, no Gládio Cruel e no Pico da Rocha Negra. Aguardarei seu retorno aqui.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nChama CapturadaFlor de Fogo"},
[9320] = {title = "Poção Elevada de Mana", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nPoção Elevada de Mana"},
[9322] = {title = "As chamas selvagens de Kalimdor", objective = "Visite o Festival de Verão em Azshara, em Silithus, na Cratera Un'Goro e em Hibérnia. Em seguida, fale novamente com o Guardachamas do Festival.", description = "O Festival do Fogo do Solstício é celebrado por todo o mundo de Azeroth, e Kalimdor não é exceção. Você parece <pronto/pronta> para a jornada.\n\nSó é possível vivenciar completamente o Festival do Fogo quando se vê as fortíssimas chamas azuis. Elas estão acesas nas regiões gélidas a oeste de Hibérnia; no nordeste de Azshara; perto do Repouso dos Valorosos na desértica Silithus; e na entrada da luxuriante Un'Goro. Volte aqui quando terminar a jornada.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBraceletes do Elemental de BrasasFlor de Fogo"},
[9323] = {title = "As chamas selvagens dos Reinos do Leste", objective = "Visite o Festival do Fogo na Barreira do Inferno, nas Terras Pestilentas Orientais, nas Terras Agrestes e na Garganta Abrasadora. Em seguida, fale com o Guardachamas do Festival.", description = "A celebração das chamas abrange todo o continente, <class>. Tudo está coberto pelo calor do festival! Entre todas as fogueiras, as mais fortes são as de chamas azuis. Vá atrás delas!\n\nHá uma a norte do Pico da Rocha Negra. As outras estão perto de uma cidade decrépita no coração das Terras Pestilentas Orientais, na estrada para os penhascos das montanhosas Terras Agrestes, e no caminho para o Portal Negro, na Barreira do Inferno.\n\nVolte aqui quando completar a jornada.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nDragonas do Festival do FogoFlor de Fogo"},
[9324] = {title = "Roubando a chama de Orgrimmar", objective = "Devolva a Chama de Orgrimmar para o Historiador do Festival.", description = "A chama, roubada do coração de Orgrimmar, enche você de calor e entusiasmo. Este poder em estado bruto lhe é estranho, mas talvez o historiador saiba mais a respeito...Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nFlor de FogoFlor de Fogo"},
[9325] = {title = "Roubando a chama do Penhasco do Trovão", objective = "Entregue a chama do Penhasco do Trovão para o Historiador do Festival.", description = "A chama, roubada do xamã do Penhasco do Trovão, enche você de calor e entusiasmo. Este poder em estado bruto lhe é estranho, mas talvez o historiador saiba mais a respeito...Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBebida Fogosa do FestivalFlor de Fogo"},
[9326] = {title = "Roubando as chamas da Cidade Baixa", objective = "Entregue a chama da Cidade Baixa para o Historiador do Festival.", description = "A chama, roubada das ruínas sobre a Cidade Baixa, enche você de calor e entusiasmo. Este poder em estado bruto lhe é estranho, mas talvez o historiador saiba mais a respeito...Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nTorta de SabugoPão Doce Tostado no FogoLinguiça do SolstícioOrcmellow Tostado\n\nVocê também receberá:\n\nFlor de Fogo"},
[9330] = {title = "Roubando a chama de Ventobravo", objective = "Entregue a Chama de Ventobravo para o Contador de Histórias do Festival.", description = "A chama, roubada do coração de Ventobravo, enche você de calor e entusiasmo. Este poder em estado bruto lhe é estranho, mas talvez o contador de histórias saiba mais a respeito...Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBebida Fogosa do FestivalFlor de Fogo"},
