[9339] = {title = "A recompensa de um ladrão", objective = "Entregue a Chama de Ventobravo para (NAME).A chama, roubada do coração de Ventobravo, enche você de calor e entusiasmo. Este poder em estado bruto lhe é estranho, mas talvez o (NAME) saiba mais a respeito...Você receberá:\n\n", description = "A chama, roubada do coração de Ventobravo, enche você de calor e entusiasmo. Este poder em estado bruto lhe é estranho, mas talvez o (NAME) saiba mais a respeito...Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCoroa do Festival do Fogo"},
