[9362] = {title = "Senhor da Guerra Krellian", objective = "Recupere a Concha Prismática para o Arquimago Tauriel. O Arquimago reside numa torre no topo dos penhascos de Azshara.", description = "Não temos muito tempo, <name>! Meus feitiços divinatórios revelaram uma fonte poderosa de magia. Não posso abandonar a torre. Por isso peço que você mate a criatura vil que possui o artefato.\n\nO cretino patético se chama Senhor da Guerra Krellian. Ele se esconde atrás dos guardas no Templo de Zin-Malor. Transforme-o em cinzas e pegue a Concha Prismática que ele tanto preza.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[9364] = {title = "Magia fragmentada", objective = "Polimorfe as Iraliscas de Azshara e mate os clones que aparecerem. Quando matar 50 Clones Polimorfados, volte e fale com o Arquimago Tauriel, em Azshara.", description = "Minha pesquisa revelou que a concha prismática tem um único propósito.\n\nLeve a concha e lance seu feitiço de polimorfia nas Iraliscas em Azshara. Basta esperar um pouco e a aura caótica da concha fará as Iraliscas se dividirem em vários clones menores por alguns instantes. Mate esses clones o mais rápido possível e a concha irá coletar-lhes as essências.\n\nQuando a concha estiver cheia, volte e fale comigo.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nPolimorfia\n\nO seguinte feitiço será lançado em você:\n\nPolimorfia: Porco\n\nVocê também receberá:\n\n"},
[9367] = {title = "O Festival do Fogo", objective = "Verifique as fogueiras de Ventobravo, Altaforja e Darnassus. Quando terminar, fale com um Historiador do Festival em uma capital.", description = "Mais um solstício!\n\nTodo ano, como manda a tradição, alguns guardachamas são escolhidos para cuidar das fogueiras nas capitais. Embora haja fogueiras por todo Azeroth, é fundamental que as nossas sejam as mais quentes e brilhantes para homenagearmos devidamente a estação.\n\nNa verdade, se não for incômodo, tenho uma tarefa para você. Ainda não fui informado se as fogueiras estão fortes como deveriam. Vá às capitais e verifique se as fogueiras estão fortes. Certifique-se de que eles estejam quentes e brilhantes!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nFlor de FogoBebida Fogosa do Festival"},
[9368] = {title = "O Festival do Fogo", objective = "Toque as fogueiras de Orgrimmar, do Penhasco do Trovão e da Cidade Baixa. Em seguida, fale com um Contador de Histórias do Festival em qualquer capital.", description = "Mais um solstício!\n\nTodo ano, como manda a tradição, alguns guardachamas são escolhidos para cuidar das fogueiras nas capitais. Embora haja fogueiras por todo Azeroth, é fundamental que as nossas sejam as mais quentes e brilhantes para homenagearmos devidamente a estação.\n\nNa verdade, se não for incômodo, tenho uma tarefa para você. Ainda não fui informado se as fogueiras estão fortes como deveriam. Vá às capitais e verifique se as fogueiras estão fortes. Certifique-se de que eles estejam quentes e brilhantes!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nFlor de FogoBebida Fogosa do Festival"},
[9386] = {title = "Uma luz na escuridão", objective = "Encontre e toque as fogueiras no Pico da Rocha Negra, no Gládio Cruel, em Scolomântia e em Stratholme. Em seguida, fale novamente com o Guardachamas do Festival.", description = "Hum, você é bastante <impetuoso/impetuosa>, não é mesmo? Se estiver <disposto/disposta> a voltar às profundezas, posso lhe recompensar muito bem.\n\nProcure as fogueiras em Stratholme, em Scolomântia, no Gládio Cruel e no Pico da Rocha Negra. Ficarei aqui aguardando você retornar, <name>.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nFlor de Fogo"},
[9388] = {title = "As chamas tremeluzentes de Kalimdor", objective = "Visite as fogueiras no Vale Gris, nos Sertões, na Costa Negra e na Cordilheira das Torres de Pedra. Em seguida, fale com o Guardachamas do Festival.", description = "Há muitas chamas espalhadas por todo o mundo, <name>. As mais complicadas são as verdes: sem o devido cuidado, elas não ficam acesas por muito tempo. Você poderia verificar se as chamas esmeralda de Kalimdor ainda estão acesas?\n\nHá chamas esmeralda nas cercanias da Vila Catraca, nos Sertões; ao lado do Glaive do Mestre, na Costa Negra; na floresta do Vale Gris, perto da ponte do Posto Avançado Asa de Prata; e próximo à estrada para o Rochedo Cortavento, que fica no caminho principal para a Cordilheira das Torres de Pedra.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nTorta de SabugoPão Doce Tostado no FogoLinguiça do SolstícioOrcmellow Tostado\n\nVocê também receberá:\n\n"},
[9389] = {title = "As chamas tremeluzentes dos Reinos do Leste", objective = "Visite as fogueiras no Contraforte de Eira dos Montes, na Floresta de Pinhaprata, em Cerro Oeste e no Pantanal. Em seguida, fale com o Guardachamas do Festival.", description = "Tradicionalmente, o Festival do Fogo do Solstício ilumina todo Azeroth com sua infinidade de fogueiras. Dizem que as fogueiras de chamas verdes são as melhores, por serem as mais difíceis de manter acesas e terem uma tonalidade tão diferente.\n\nCertifique-se de que elas ainda continuam acesas nos Reinos do Leste. Elas estão nas cercanias de Dun Modr, no Pantanal; ao lado do Vilarejo Lenhardente, na Floresta de Pinhaprata; nos penhascos acima do farol, em Cerro Oeste; e na torre em ruínas no Contraforte de Eira dos Montes.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nTorta de SabugoPão Doce Tostado no FogoLinguiça do SolstícioOrcmellow Tostado\n\nVocê também receberá:\n\n"},
[9415] = {title = "Apresente-se ao Marechal Murazul", objective = "Fale com Marechal Murazul no acampamento da Aliança, perto da Colme'Zora.", description = "Você é <um dos novos/uma das novas> recrutas do Marechal Murazul? Deve ser sim.\n\nO que está esperando? Bons soldados estão morrendo no deserto. Não está <ansioso/ansiosa> para se juntar a eles?\n\nVai, mexa-se e vê se não se esturrica no caminho.", progress = nil, completion = nil, rewards = nil},
[9416] = {title = "Apresente-se à General Kyríala", objective = "Fale com a General Kyríala no acampamento da Horda, perto da Colme'Régia.", description = "Você deve ser <um dos novos/uma das novas> recrutas da General Kyríala. Já estava na hora. Todos se cansam muito rápido aqui no deserto.\n\nOs anões recebem reforços todo dia. Não podemos perder territórios na nossa luta por recursos em Silithus.\n\nFale com a General para receber suas ordens. Não é meu trabalho treinar você. Se vira.", progress = nil, completion = nil, rewards = nil},
[9419] = {title = "Limpeza do deserto", objective = "Encontre e leve a Silitista até o Extrator de Silitista, então fale com o Marechal Murazul no acampamento da Aliança perto da Colme'Zora.", description = "Há pouco tempo atrás, ficamos sabendo que a Horda vem varrendo o deserto de Silithus à procura de recursos, mas nunca nos importamos com isso. Achávamos uma perda de tempo estocar tanta coisa nesse lugar sufocante.\n\nEntretanto, descobrimos a importância dos recursos e agora estamos brigando para não ficar para trás.\n\nSe puder me ajudar, gostaria que fosse ao deserto e trouxesse a silitista de volta para cá.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPoção Elevada de CuraPoção Elevada de Mana\n\nVocê também receberá:\n\n"},
[9422] = {title = "Limpeza do deserto", objective = "Encontre e leve a Silitista até o Extrator de Silitista, então fale com a General Kyríala, no acampamento da Horda perto da Colme'Régia.", description = "Já faz algum tempo que estamos coletando recursos cristalinos do deserto de Silithus. Até então, os únicos perigos que combatíamos eram as criaturas que vivem por aqui, além da areia, da poeira e do calor...\n\nMas agora parece que a Aliança descobriu o que estamos fazendo. Estamos em uma corrida contra eles, coletando os recursos naturais e impedindo-os de fazer o mesmo.\n\nPreciso de toda ajuda disponível. Espero poder contar com você.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPoção Elevada de CuraPoção Elevada de Mana\n\nVocê também receberá:\n\n"},
[9556] = {title = "To The Victor...", objective = "Collect a bottle of Stratholme Holy Water from within the city of Stratholme and deliver it to the Lordaeron Mage within the Mage Quarter of Stormwind or the Undercity when your side is victorious in the Eastern Plaguelands.", description = "You've no doubt been fighting long and hard among the towers, <race>. We would like to give something back to you in return for your defense of these lands, but first, you must prove to us you understand the weight of the tragedies that took place here.\n\nWalk the streets of Stratholme and find a vial of the holy water. Once you have it, travel to the mage quarter of Stormwind or the Undercity... There, if you are victorious within these lands, you will find my colleague.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[9664] = {title = "Novos postos avançados", objective = "Capture a Torre da Coroa, a Torre da Muralha Leste, a Torre do Passo Norte e a Torre do Bosque Pestilento, nas Terras Pestilentas Orientais, depois fale novamente com o Emissário Barbabranca, na Capela Esperança da Luz.", description = "Bem-vind<o/a> às Terras Pestilentas, <name>. Como você sabe, apesar de todos os nossos esforços, os Renegados e seus aliados ainda controlam as terras e oeste daqui. Os comandantes da Aliança decidiram estabelecer novas fortificações aqui, a leste, para aumentar a pressão sobre nossos inimigos.\n\nTorres abandonadas desde os tempos de Lordaeron serão ocupadas e reconstruídas com este propósito. Estas terras são perigosas, e precisamos de ajuda.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[9665] = {title = "Reforçando nossas defesas", objective = "Capture a Torre da Coroa, a Torre da Muralha Leste, a Torre do Passo Norte e a Torre do Bosque Pestilento, nas Terras Pestilentas Orientais, depois fale novamente com o Emissário Gormok, na Capela Esperança da Luz.", description = "Agentes dos Renegados descobriram um plano da Aliança para montar novas bases aqui, nas Terras Pestilentas Orientais, em diversas torres abandonadas.\n\nÉ imperativo impedir que estabeleçam uma presença significativa em nossas fronteiras orientais. Precisamos impedir esta incursão da Aliança agora. O plano é expulsá-los das torres que já tiverem ocupado e proteger as que ainda estão sob nosso controle de novos ataques.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[77568] = {title = "A força de um caçador", objective = "Derrote Githyiss, a Torpe, na Caverna Fionumbra e recupere a runa perdida. Aprenda a runa e fale novamente com Ayanna Perenanda, em Umbravale.", description = "Um caçador tira força da conexão e respeito que tem com todas as criaturas, não apenas com seus ajudantes. Você vai caçar e matar vários animais a serviço da Aliança, mas, mesmo em batalha, é preciso respeitar o inimigo.\n\nMinha aprendiz anterior usava uma runa mística para aumentar seus poderes, mas, tolamente, tentou praticar na maior das aranhas da caverna do norte, uma criatura a quem demos o nome de Githyiss, a Torpe. Esse desrespeito levou à sua morte.\n\nMate Githyiss, mas não subestime a força dela.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLuvas de Couro Rachado"},
[77588] = {title = "Prova de resistência", objective = "Encontre o Baú Escondido nos penhascos atrás de Frang. Aprenda a runa e apresente-se a Frang no Vale das Provações.", description = "Acima de tudo, guerreiros da Horda precisam ter um corpo forte. Na sua jornada, você se submeterá a provas de vigor e resistência. \n\nPara tanto, eu escondi um baú nos penhascos atrás de mim. Você pode vê-lo do centro do vilarejo perto da fogueira. Chegar ao baú será um feito e tanto. Dentro dele você encontrará uma runa de grande poder.\n\nA prova consiste em encontrar a runa e voltar quando aprender seus segredos.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLuvas Encadeadas Manchadas"},
[77617] = {title = "Relíquias da Luz", objective = "Recupere o incunábulo e siga a orientação para aprender uma nova habilidade. Em seguida fale novamente com o Irmão Samuel na Vila Norte.", description = "É possível encontrar relíquias da Luz em toda Azeroth. Essas relíquias, em especial os incunábulos que guiam nossa ordem desde a sua fundação, podem ajudar a fortalecer sua conexão com a Luz.\n\nUm desses incunábulos ficava nos arquivos da Abadia, mas os bandidos dos Défias a leste o roubaram. Recupere o incunábulo e siga sua orientação para aprender uma nova técnica de combate. Volte aqui quando terminar.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLuvas Encadeadas Manchadas"},
[77620] = {title = "Pesquisa de feitiços", objective = "Recupere as anotações de pesquisa dos bandidos dos Défias e use-as para aprender um novo feitiço. Depois, fale novamente com Gaspar Melchior, na Vila Norte.", description = "Ensinarei a você o que eu puder, <name>, mas suas próprias pesquisas levarão a um poder impressionante. Estudar as anotações dos maiores arquimagos de Azeroth é um excelente começo. Os arquivos da Abadia abrigavam uma infinidade de anotações de pesquisa, mas os agentes dos Défias as roubaram.\n\nRecupere as anotações de feitiços dos bandidos a leste e use esta cartilha para decifrá-las. Volte quando concluir a pesquisa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPatuá da CompreensãoLuvas de Tecido Esfarrapado"},
[77652] = {title = "Ícones de poder", objective = "Recupere o ícone perdido dos Xamãs Costagulha, na Ravina da Lâmina Espinhenta. Aprenda seu poder, então, volte a falar com Harutt Chifre Troante, na Aldeia Narache.", description = "O xamanismo é uma prática ancestral, <name>. É possível encontrar muitos artefatos repletos de energia espiritual em Azeroth. Eu mesma encontrei há pouco um ícone de incrível poder, mas os javatuscos o roubaram.\n\nVá ao território deles, a leste, e encontre o ícone roubado. Deve estar com um dos xamãs deles, tentando compreender seus segredos. \n\nQuando encontrar o ícone, aprenda seu poder e volte aqui.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nVestimenta de Couro Rachado"},
[77659] = {title = "Três vezes roubado", objective = "Recupere a runa roubada na caverna dos troll Jubafria, no Vale Cristálgida. Use-a para aprender uma nova habilidade e fale novamente com Solm Hargrin, em Sidermar.", description = "Me envergonha admitir, <name>, mas aqueles Jubafria idiotas me passaram para trás. Roubaram uma runa mágica que eu... bem... \"peguei emprestada\" da tesouraria. Achei que me traria vantagem em relação aos outros, mas não consegui descobrir como ela funciona.\n\nTu podes ir até a caverna deles ao sul e tentar pegá-la de volta? E tenta usar a runa quando encontrá-la. Tu pareces ser do tipo estudioso. Sem ofensa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLuvas de Couro Rachado"},
[77667] = {title = "Pesquisa de feitiços", objective = "Encontre as anotações de feitiços perdidas na aldeia dos troggs a sudoeste de Sidermar, então, use-as para aprender uma nova habilidade. Depois, fale novamente com Marryk Nurribit, em Sidermar.", description = "Como <um mago/uma maga>, pesquisar o arcano leva a poderes inimagináveis. Estudar as anotações dos maiores arquimagos de Azeroth é um excelente começo. Encontrei alguns recentemente, mas foram roubados por aqueles malditos troggs a sudoeste daqui.\n\n. Recupere as anotações de feitiços da aldeia dos troggs e use a cartilha para decifrá-las. Volte quando concluir a pesquisa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPatuá da CompreensãoLuvas de Tecido Esfarrapado"},
[77672] = {title = "A runa perdida", objective = "Recupera a runa no Vale Teia da Noite e use-a para aprender uma nova habilidade, então, fale novamente com Maximillion, em Plangemortis.", description = "Tenho testado um novo tipo de magia: uma runa capaz de canalizar energias vis para abastecer feitiços poderosos. Eu mandei um aprendiz testar a runa contra as aranhas da mina ao norte, mas ele não voltou. Suspeito que não o veremos novamente.\n\nTalvez você se mostre mais resiliente. Vá à mina e recupere a runa nas teias das aranhas. Aprenda a magia e volte aqui.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLuvas de Tecido Esfarrapado"},
[78092] = {title = "É preciso destruí-lo", objective = "Encontre um modo de destruir o orbe.", description = "Este orbe está, de fato, ligado a uma poderosa entidade, em algum lugar além da Espiral Etérea. Não consigo descobrir sua natureza, mas sinto sua presença no orbe... me estudando de volta.\n\nTemo admitir isto, <name>, mas é preciso destruir o orbe.\n\nParece imune a danos, mas talvez seja vulnerável à arma de um poderoso lorde-demônio. Não sei onde encontrar uma arma dessas, mas é a sua melhor opção.\n\nBoa sorte, <name>.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstilhaçar Orbe"},
[78114] = {title = "Laçando mantícoras selvagens", objective = "Leve um Cristal Hipnótico e uma Varinha Nodosa de Magia Selvagem para Jixo Bombaloca, na Cordilheira das Torres de Pedra.", description = "Tu viu aquelas mantícoras asaltiva voando por aí? Aposto que não vai faltar quem pague uma nota pra me ver voar invertido em uma delas!\n\nMas elas são teimosas, e eu não consigo domar uma para montar, sem falar nos voos loucos nas alturas.\n\nOuvi dizer que tu faz uma varinha que acalma elas. Eu mesmo posso fazer uma. Só preciso de um cristal hipnótico e uma varinha nodosa da magia selvagem. \n\nAgora, eu não sei direito onde encontrar, mas isso são detalhes.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
