[78611] = {title = "Carregamento emboscado", objective = "Faltam alguns itens no carregamento, mas vai ser útil mesmo assim. Eu agradeço, <name>.", description = nil, progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nConsumir Suprimentos Parciais\n\nVocê receberá:\n\n"},
