[79098] = {title = "Fora da floresta!", objective = "Traga o Patuá de Sentinela da Asa de Prata para Kazragore, o Clarividente, na base da Fogueira, no Vale Gris.", description = "Matamos um deles, mas essa Aliança irritante continua a lutar... que patuá é esse?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nTambor de Batalha do Brado Guerreiro"},
[79099] = {title = "Barão Aquanis", objective = "Traga o Globo d'Água Estranho para Davidus Astrocaos em Auberdine, na Costa Negra.", description = "Saudações, <class>. Eu ouvi histórias muito curiosas sobre um elemental da água corrompido chamado Barão Aquanis, que vigia as proximidades da entrada das ruínas noctiélficas conhecidas como Profundezas Negras. Estou aqui para contratar aventureiros com coragem para recuperar o núcleo do Barão Aquanis, um globo de água corrompida. Em troca, disponho do acesso a um portal diretamente para a Praia de Zoram, que tenho usado para estudar os arredores das ruínas... de uma distância segura, é claro.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
