[79007] = {title = "... e aquele bilhete, hein?", objective = "Os restos calcinados do bilhete são seu único guia.", description = "Você encontra resquícios de um incêndio recente, com o brilho tênue de brasas esfriando.\nEm meio às provisões espalhadas, você encontra uma nota queimada.\n\"Nem tudo que queima está perdido. Encontre a terra gêmea, encontre o lugar.\"Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPoção de RapidezPoção Forte de Sangue de Troll"},
[79008] = {title = "... e aquele bilhete, hein?", objective = "Os restos calcinados do bilhete são seu único guia.", description = "Você encontra resquícios de um incêndio recente, com o brilho tênue de brasas esfriando.\nEm meio às provisões espalhadas, você encontra uma nota queimada.\n\"Nem tudo que queima está perdido. Encontre a terra gêmea, encontre o lugar.\"", progress = nil, completion = "Quando você encontra os restos de mais um corpo calcinado, seus olhos se voltam para uma mensagem presa a uma tábua.", rewards = "Você receberá:\n\nPoção de RapidezPoção Forte de Sangue de Troll"},
[79192] = {title = "Degraus", objective = "Você arranca o bilhete da tábua e lê o que está rabiscado.", description = "O bilhete diz: \"A delegação disse que estavam fazendo entregas para a Nova Peste. Parecem-me simples acidentes de boticário. Encontre meu cantinho aconchegante nas montanhas entre os Sertões e Desolação, se quiser conversar\".", progress = nil, completion = "Aventureiros e mercenários de regiões remotas sem dúvida acampam aqui. A ponta de um bilhete esvoaçando com a brisa chama sua atenção. Você se aproxima para pegá-lo.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPicareta de MineraçãoPá do Fazendeiro\n\nVocê também receberá:\n\nLancheira RobustaMadeira SimplesPederneira e Lenha"},
